float map(vec3 p) {
   return scene(p);
}
vec3 grad(vec3 p) {
   vec2 q = vec2 (0.0, EPS);
   return vec3(map(p + q.yxx) - map(p - q.yxx),
               map(p + q.xyx) - map(p - q.xyx),
               map(p + q.xxy) - map(p - q.xxy));
}
vec3 shade(vec3 origin, vec3 ray, float traced) {
   return normalize(grad(origin+traced*ray));
}

float trace(vec3 origin, vec3 ray, 
      inout vec3 normal, inout float shadow){ 

   float intersect = 0.1;
   //if( distance(origin, ray) < ){//scene(origin+ray) > 0.5 ){
   for(int i = 0; i < STEPS; i++){

      vec3 pos = origin + ray * intersect;
      if (fSphere(pos-v3(0.0,0.0,1.01),1.5)>0.0){return 0.0;}

      float dist = scene(pos);
      if(dist < 0.01){
         normal = shade(origin, ray, intersect);
         return intersect;
         return float(i) / float(STEPS);
         //return intersect;
      }
      //if(dot(pos, v3(0.10))>0.1) {return 0.0;}
      intersect += dist * 0.50;
   }

   normal = vec3(0.0);
   return 0.0;
}
