#define PI 3.14159265358979323846

float random (in vec2 st) {
   return fract(sin(dot(st.xy,vec2(12.9898,78.233))) * 43758.5453123);
}

float noise (in float x) {

   // split x int its integer and fraction.
   float i = floor(x);
   float f = fract(x);

   float r = random(vec2(i, 1.0));
   float r_next = random(vec2(i + 1.0, 1.0));
   // return r;
   // return mix(r, r_next, f);
   return mix(r, r_next, smoothstep(0.0, 1.0, f));
}

vec2 rotate2D(vec2 _st, float _angle){
   _st -= 0.5;
   _st =  mat2(cos(_angle),-sin(_angle),
   sin(_angle),cos(_angle)) * _st;
   _st += 0.5;
   return _st;
}


float posSin(in float val) {
   return (sin(val) + 1.0) / 2.0;
}

vec2 tile(vec2 _st, float _zoom){
    // make it perfectly square
   float aspect = iResolution.y / iResolution.x; 
   _st.y *= _zoom * aspect;
   _st.x *= _zoom;
   return fract(_st);
}

float box(vec2 _st, vec2 _size, float _smoothEdges){
   _size = vec2(0.5)-_size*0.5;
   vec2 aa = vec2(_smoothEdges*0.5);
   vec2 uv = smoothstep(_size,_size+aa,_st);
   uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
   return uv.y * uv.x;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

   // noisify ALL the values!!!1

   vec2 st = fragCoord.xy / iResolution.xy;
   vec3 color = vec3(0.0);
   float tileNum = 3. + noise(iTime * 0.1 + 59349.0) * 5.;

   // Save original coordinates for later
   vec2 st_orig = st;

   // Divide the space in tileNum (eg. 5)
   st = tile(st, tileNum);

   // Calculate distance of vec(sinus time) and the actual position
   float dist = distance(st_orig, vec2(noise(iTime), noise(iTime + 3000.)));

   // maxDist defines the radius of effect
   // lower it, to see the diagonal movement (eg. try 0.1 * ...)
   float maxDist = 1.0 * noise(iTime + 22355.);

   float rotation = smoothstep(0.0, maxDist, dist);

   // Use a matrix to rotate the space
   st = rotate2D(st, PI * rotation);

   // Draw a square
   color = vec3(box(st,vec2(0.7),0.01));
   // fade gray along the x axis of each square
   float fade = st.x;

   color *= mix(color, vec3(fade), (cos(iTime) + 1.0) * 0.5);
   color *= vec3(noise(iTime * 0.5 + 1000.), noise(iTime * 0.5), noise(iTime + 800. * 0.5));

   fragColor = vec4(color, color.r * dist);
}

