    float h=0.,d,i;
	for(i=1.;i<100.;i++)
    {
        d=map(ro+rd*h);
        if(d<.001)break;
        h+=d;
	}
    fragColor.xyz+=20.*(cos(vec3(9,5,12)+orbit*3.)*.5+.5)/i;
}

