const int max_iteration = 200;
const float r2 = 100.;

vec2 get_c() {
  return vec2(
    -0.512511498387847167 + sin(iTime / 20.) * 0.005,
     0.521295573094847167 + cos(iTime / 20.) * 0.005
  );
}

vec2 get_scale() {
 vec2 s = vec2(2.0, 2.0);
 s *= cos(iTime / 10.) * 0.4 + 0.6;
 return s;
}

const int AA = 2;

// Complex number
struct Complex {
  vec2 data;
};

Complex complex_mult(Complex lhs, Complex rhs) {
  return Complex(mat2(lhs.data, -lhs.data.y, lhs.data.x) * rhs.data);
}

Complex complex_plus(Complex lhs, Complex rhs) {
  return Complex(lhs.data + rhs.data);
}


float complex_norm(Complex c) {
  return dot(c.data, c.data);
}

// Main
vec3 color_at(in vec2 coord) {
    vec3 col;

    Complex z = Complex(coord);
    Complex c = Complex(get_c());

    int i = 0;
    while (i < max_iteration && complex_norm(z) < r2) {
      z = complex_plus(complex_mult(z, z), c);
      ++i;
    }

    if (i == max_iteration) {
      col = vec3(0.8, 1.0, 0.8);
    } else {
      float x = float(i) / float(max_iteration);
      x = pow(x, 0.2);
      col = vec3(x / 2.0, x, x / 2.0);
   }

   return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;
    for (int i = 0; i < AA; ++i) {
        for (int j = 0; j < AA; ++j) {
            vec2 offset = -0.5 + vec2(float(i), float(j)) / float(AA);
            vec2 coord = (fragCoord + offset - 0.5 * iResolution.xy) / - iResolution.y;
            coord *= get_scale();
            col += color_at(coord);
        }
    }
    col /= float(AA * AA);




    fragColor = vec4(col,1.0);
}

float calculateSlope(vec2 fromXY, vec2 toXY) {
    return (toXY.y - fromXY.y) / (toXY.x - fromXY.x);
}

float calculateTheB(vec2 tipPnt, float slope) {
    return tipPnt.y - slope * tipPnt.x;
}

float calculateYforX(float slope, float x, float b) {
    return slope * x + b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 lineSegmentFromXY = vec2(128.0, 256.0);
    vec2 lineSegmentToXY = iMouse.xy;
    float tolerance = 5.0;

    float slope = calculateSlope(lineSegmentFromXY, lineSegmentToXY);
    float b = calculateTheB(lineSegmentFromXY, slope);
    float targetY = calculateYforX(slope, fragCoord.x, b);

    bool withinLine = abs(targetY - fragCoord.y) <= tolerance;
    bool withinHorizontalBound = fragCoord.x >= min(lineSegmentFromXY.x, lineSegmentToXY.x) && 
                                 fragCoord.x <= max(lineSegmentFromXY.x, lineSegmentToXY.x);
    bool withinVerticalBound = fragCoord.y >= min(lineSegmentFromXY.y, lineSegmentToXY.y) && 
                               fragCoord.y <= max(lineSegmentFromXY.y, lineSegmentToXY.y);
    bool shouldDraw = withinLine && withinHorizontalBound && withinVerticalBound;
    vec3 col = vec3(1., 1., 1.);
    if(shouldDraw) {
        // Normalized pixel coordinates (from 0 to 1)
        vec2 uv = fragCoord/iResolution.xy;

        // Time varying pixel color
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));   
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

