// 3d mandelbulb
float mandel4(in vec3 v, in vec4 c, out vec3 map){
    //float t = sin(iTime)*.5+.5;
    vec4 mv = vec4(v, 0.);

    vec4 r = mv;
    vec4 rNext = r;
    float m = dot(mv, mv);
    vec4 trap = vec4(abs(r));

    int i = 0;
    float d = 1.;

    float n =  2.;

    for(i=0; i<ITER; i++){
        float cr = length(r);
        float nr = pow(cr, n);

        d = pow(cr, n-1.) *n * d + 1.0;

        rNext.x = pow(r.x, n) - pow(r.y, n) - pow(r.z, n) - pow(r.w, n);
        rNext.y = n*r.x*r.y;
        rNext.z = n*r.x*r.z;
        rNext.w = n*r.x*r.w;
        r = c + rNext;

        trap = min(trap, vec4(abs(r)));
        map = vec3(trap.xyz);

        m = dot(r,r);

        if(m > 3.5){
            break;
        }
    }
    //return pow(length(r), 2.)+sqrt(m);
    return  .35*log(m) * sqrt(length(m))/d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d; 
}

float RayMarch(in vec3 ro, in vec3 rd, in vec4 c, out vec3 col){
    float dO = 0.;

    for(int i=0; i<MAX_STEPS; i++){
        vec3 p = ro+rd*dO;
        //p.xz *= Rot(iTime/SPEED);
        //p.yz *= Rot(iTime/SPEED-2.);
        float mbd = mandel4( p, c, col);
        float dS = mbd;

        if(dO > MAX_DIST || dS<SURF_DIST) break;
        dO += dS;
    }

    return dO;
}
vec3 GetNormal(in vec3 p, in vec4 c){
     vec3 col;
    // my e value made black artifacts
    // so I took a look at iq's normal epsilone. 
    // don't exactly know how this is calculated.
    // looks like it is relevant to some screen pixel size calculation..?
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*2.0/(iResolution.y*1.5);

    // getting vector with very small vector
    vec3 n = vec3(
        e.xyy*mandel4(p+e.xyy, c, col)+
        e.yxy*mandel4(p+e.yxy, c, col)+
        e.yyx*mandel4(p+e.yyx, c, col)+
        e.xxx*mandel4(p+e.xxx, c, col ) 
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/(iResolution.y);
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    vec3 ro = vec3(0, 1.5, 1.5);
    ro.xz *= Rot(iTime/5.);
    ro.y = 2.*sin(iTime/3.);
    vec3 rd = R(uv, ro, vec3(0,.0,0), .7);

    vec3 backgrounduv;
    //2.*(sin(iTime)*.5 +.5)
    //1.5*(sin(iTime)*.5+.5)
    float time = iTime*.45;
    vec4 c = 0.45*cos( vec4(0.5,3.9,1.4,1.1) + time*vec4(1.2,1.7,1.3,2.5) ) - vec4(0.3,0.0,0.0,0.0);
    col = vec3(.05, .13, .1);
    float d = RayMarch(ro, rd, c, backgrounduv);
    col += vec3(.6-d*.3);
    //col += .5;
    col += pow(clamp(3. - backgrounduv.y, 0.0, .99), 9.)*vec3(.5, .3, .4);
    //col *= 10.;
    col *= (col+.9)*(col+.6);

    //light
    vec3 lp = vec3(0, 3.5, 4.5);
    lp.xz *= Rot(iTime/3.);
    //lp.yz *= Rot(iTime/3.);
    vec3 lr = ro+rd*d;
    //lr.xz *= Rot(iTime/SPEED);
    //lr.yz *= Rot(iTime/SPEED-2.);
    lr = lr;
    vec3 l = normalize(lp - lr);
    vec3 n = GetNormal(lr, c);
    vec3 shadow = clamp(dot(n, l), 0.1, 1.)*vec3(0., .8, 1.)*.7;
    shadow *= 1.;
    //col *= vec3(pow(shadow, 1.))*vec3(.3, .6, 1.);


    vec3 lp2 = vec3(3., 0., 4.5);
    lp2.xz *= Rot(iTime);
    lp2.yz *= Rot(iTime/3.);
    vec3 lr2 = ro+rd*d;
    //lr2.xz *= Rot(iTime/SPEED);
    //lr2.yz *= Rot(iTime/SPEED-2.);
    lr2 = lr2;
    vec3 l2 = normalize(lp2 - lr2);
    vec3 n2 = GetNormal(lr2, c);
    vec3 shadow2 = clamp(dot(n2, l2), 0.1, 1.)*vec3(1., .2, 0.4)*.7;
    shadow2 *= 1.;
    col *= (shadow+shadow2);
    //col += vec3(.09, .04, .09);

    fragColor = vec4(col, 0);
}

// This shader shows a wet look stones ground.
// The ground geometry and ao was taken from a BigWIngs shader:
// https://www.shadertoy.com/view/XlcSzM
//
// You can play with the mouse to move the light. In Constants section
// you will find "vec4 stones" to control the shape of the stones.

#define MAX_STEPS 100
#define MAX_DISTANCE 100.0
#define SURFACE_DIST 0.01

#define PI 3.141592653589793238
#define GAMMA 2.2
#define saturate(val) clamp(val, 0.0, 1.0) 

// Structs

struct Material {
    vec3  albedo;
    vec3  normal;
    float NdotL;
    float NdotH;
    float NdotV;
    float HdotL;
    float metalness;
    float roughness;
};


// Constants

vec4 stones = vec4(.2, .3, .5, 2.);
float lightIntensity = 3.;
float lightOffset = 7.;

// Global

vec3 lightPos = vec3(0, 2, 7);

// Aux Functions

float N2(vec2 p) {
	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 N21(float p) {
	vec2 p2 = fract(vec2(p) * vec2(443.897, 441.423));
	p2 += dot(p2, p2.yx + 19.19);
    return fract((p2.xx+p2.yx)*p2.yy);

}

float N2(float x, float y) { return N2(vec2(x, y)); }

vec3 N31(float p) {	
   vec3 p3 = fract(vec3(p) * vec3(443.897, 441.423, 437.195));
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

float Noise(vec2 uv) {
    vec2 id = floor(uv);
    vec2 m = fract(uv);
    m = 3.* m * m - 2.* m * m * m;

    float top = mix(N2(id.x, id.y), N2(id.x+1., id.y), m.x);
    float bot = mix(N2(id.x, id.y+1.), N2(id.x+1., id.y+1.), m.x);

    return mix(top, bot, m.y);
}

float noiseTex(vec2 uv, float seed, float octaves) {
    float v = 0.;
    uv += N21(seed);

    for (float i = 1.; i <= 11.; i++) {
    	v += Noise(uv) / i;
        uv *= 2.;

        if (i > octaves) break;
    }

    return v * .5;
}

// Gamma Correction

vec3 gammaToLinear(vec3 col) {
    return pow(col, vec3(GAMMA));
}

vec3 linearToGamma(vec3 col) {
    return pow(col, vec3(1.0 / GAMMA));
}

// PBR

float SchlickFresnel(float dotProduct) {
    return pow(clamp(1.0 - dotProduct, 0.0, 1.0), 5.0);
}

float Geometry(float NdotV, float alphaG) {
    float a = alphaG * alphaG;
    float b = NdotV * NdotV;
    return 1.0 / (NdotV + sqrt(a + b - a * b));
}

float TrowbridgeReitzNDF(float NdotH, float roughness) {
    if (roughness >= 1.0)
        return 1.0 / PI;

    float roughnessSqr = roughness * roughness;

    float d = (NdotH * roughnessSqr - NdotH) * NdotH + 1.0;
    return roughnessSqr / (PI * d * d);
}

// -------

vec2 GetStoneTiles(vec3 p) {
    return vec2(p.x + sin(p.z * stones.x), p.z + sin(p.x * stones.y) * stones.z) * stones.w;
}

float getDistance(vec3 p) {
    float d = p.y;
    vec2 tiles = GetStoneTiles(p);;
    vec2 id = floor(tiles);
    float bump = N2(id.x, id.y);

    vec2 tUv = tiles * PI;
    float tileMask = abs(sin(tUv.x)*sin(tUv.y));
    tileMask = 1.0 - pow(1.0 - tileMask, 8.0);
    d -= tileMask * .09 * bump;

    vec3 n = N31(id.x + id.y * 165.32);	
    float detail = sin(n.x * p.x * 15.0) * sin(n.y * p.z * 15.0) * 0.05;
    detail += sin(n.z * p.x * p.z * 5.0) * 0.005;
    d += detail;					

    return d;
}

float GroundAO(vec3 normal, vec3 pos, float dist) {
	float occ = 0.0;
    float sca = 1.0;
    for (int i = 0; i < 5; i++) {

        float hr = 0.01 + dist * float(i) / 5.0;
        vec3 aopos =  normal * hr + pos;
        float dd = getDistance(aopos);
        occ += -(dd - hr)*sca;
        sca *= 0.35;
    }

    return saturate(1.0 - 3.0 * occ);    
}

float rayMarch(vec3 ro, vec3 rd) {
    // Distance to the object
    float dO = 0.0; 
    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + (dO * rd);
        float dS = getDistance(p);
        dO += dS;
        if (dS <= SURFACE_DIST || dS > MAX_DISTANCE) break;
    }

    return dO;
}

vec3 getNormal(vec3 p) {
    vec3 eps = vec3(0.001, 0.0, 0.0);
	vec3 nor = vec3(
	    getDistance(p+eps.xyy) - getDistance(p-eps.xyy),
	    getDistance(p+eps.yxy) - getDistance(p-eps.yxy),
	    getDistance(p+eps.yyx) - getDistance(p-eps.yyx));
	return normalize(nor);
}  

vec3 diffuseTerm(Material mat, float lightIntensity) {
    return (mat.albedo / PI * (1.0 - mat.metalness)) * mat.NdotL * lightIntensity;
}

vec3 specularTerm(Material mat) {
    vec3 irradiatedColor = vec3(.3);
    float specularRoughness = mat.roughness * (1.0 - mat.metalness) + mat.metalness;
    float D = TrowbridgeReitzNDF(mat.NdotH, specularRoughness);

    float Cspec0 = 0.02;
    vec3 F = vec3(mix(Cspec0, 1.0, SchlickFresnel(mat.HdotL)));
    float alphaG = pow(specularRoughness * 0.5 + 0.5, 2.0);
    float G = Geometry(mat.NdotL, alphaG) * Geometry(mat.NdotV, alphaG);

    return (D * G * F * irradiatedColor) * (1.0 + mat.metalness * mat.albedo) +
                                           irradiatedColor * mat.metalness * mat.albedo;
}

vec3 renderGround(vec3 p, vec3 v) {
    vec3 col = vec3(0);
    Material mat;

    vec2 uv = p.xz * 5.;
    mat.albedo = gammaToLinear(vec3(0.5));
    vec2 noise = vec2(noiseTex(uv, 0., 5.), noiseTex(uv, 12., 5.));

    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);
    n.xz += (noise - 0.5) * 2.0;

    n = normalize(n);

    mat.normal = n;

    vec3 h = normalize(v + l);

    vec2 id = floor(GetStoneTiles(p));
    mat.albedo *= 1.0 + (N2(id) - 0.5) * 0.3;		

    float ao = GroundAO(n, p, 1.0);

    mat.metalness = 1.0 - noise.y;
    mat.roughness = noise.y;
    mat.NdotL = dot(n, l);
    mat.NdotH = dot(h, n);
    mat.NdotV = dot(n, v);
    mat.HdotL = dot(h, l);


    vec3 diff = diffuseTerm(mat, lightIntensity);
    vec3 specular = saturate(specularTerm(mat));

    // return specular;
    return (diff + specular) * ao * dot(vec3(0, 1, 0), l);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Set 0 of uv in the midle of the screen
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    if (iMouse.z > 0.0) {
        vec2 mouseOffset = (iMouse.xy - 0.5 * iResolution.xy) / iResolution.y;
        lightPos.xz += mouseOffset * lightOffset;
    } else {
        lightPos.xz += vec2(sin(iTime), cos(iTime)) * 3.0;
    }

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 4, 0);
    vec3 rd = normalize(vec3(uv.x, uv.y - .5, 1.0));

    if (rd.y <= .0) {
        float dist = rayMarch(ro, rd);

        vec3 p = ro + (rd * dist);

        col = renderGround(p, rd);
    }

    col = linearToGamma(col);

    fragColor = vec4(col, 1.0);
}

#define TAU 6.28318530718 // 2 * pi
#define PHI 1.57079632679 // pi / 2

// r: vec2(horizontal repeats, vertical repeats)
void pModSpherical(inout vec3 p, in vec2 r) {
    vec2 a = TAU / r, ha = 0.5 * a;
    p.xz = sin(mod(atan(p.z, p.x) + ha.x, a.x) - ha.x + vec2(PHI, 0.0)) * length(p.xz);
    p.xy = sin(mod(atan(p.y, p.x) + ha.y, a.y) - ha.y + vec2(PHI, 0.0)) * length(p.xy);
}

float mapScene(in vec3 p) {
    float c = cos(iTime), s = sin(iTime);
    p.xz *= mat2(c, -s, s, c);
    p.yz *= mat2(c, -s, s, c);

    pModSpherical(p, vec2(27.0 + 23.0 * sin(iTime), 27.0 + 23.0 * cos(iTime)));
    vec3 q = abs(p - vec3(2.0, 0.0, 0.0)) - 0.1;
    float boxes = max(q.x, max(q.y, q.z));

    return boxes;
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(0.001, 0.0, 0.0);
    return normalize(vec3(mapScene(p + e.xyy) - mapScene(p - e.xyy),
                          mapScene(p + e.yxy) - mapScene(p - e.yxy),
                          mapScene(p + e.yyx) - mapScene(p - e.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    vec3 ro = vec3(0.0, 0.0, 5.0);
    vec3 rd = normalize(vec3(uv, -1.0));

    float t = 0.0;
    for (int i=0; i < 150; i++) {
        vec3 p = ro + rd * t;
        float d = mapScene(p);
        if (d < 0.001) {
            vec3 n = getNormal(p);
            vec3 l = vec3(-0.58, 0.58, 0.58);
            fragColor.rgb += max(0.1, dot(n, l));
            break;
        }

        if (t > 100.0) {
            break;
        }

        t += d;
    }
}

#define MAX_STEPS 200
#define MAX_DIST 200.
#define SURFACE_DIST 0.01
mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r, float offX, float offY, float offZ) {
	a.x += offX;
    b.x += offX;

    a.y += offY;
    b.y += offY;

    a.z += offZ;
    b.z += offZ;

    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    //line from a to b
    vec3 ab = b-a;

    // line from p to a
    vec3 ap = p-a;

    // as p approaches a, it will be 1
    // as p approaches b it will b 0
    float t = dot(ab,ap) / dot(ab,ab);

    // clamp values between 0 and 1
    t= clamp(t,0.,1.);

    // center point of the capsule
    // start at a (top of capsule)
    // go towards b, t steps
    vec3 c = a + t*ab;

    float d = length(p-c)-r;

    return d;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.));
}
// http://iquilezles.org/www/articles/smin/smin.htm
float smin( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return min(a, b) - h*h*0.25/k;
}

// http://iquilezles.org/www/articles/smin/smin.htm
vec2 smin( vec2 a, vec2 b, float k )
{
    float h = clamp( 0.5+0.5*(b.x-a.x)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

// http://iquilezles.org/www/articles/smin/smin.htm
float smax( float a, float b, float k )
{
    float h = max(k-abs(a-b),0.0);
    return max(a, b) + h*h*0.25/k;
}


float cloudGen (vec3 p, int xOff,float yOff, int zOff, float timing){
    float cloud = dBox(p-vec3(sin(iTime*.1 * timing)*20.+float(xOff),5. + float(yOff),0.+float(zOff)), vec3(2.,.2,1));
    vec2 uv = vec2(atan(p.x,p.z)/(3.14159*30.),p.y/70.)+iTime*.001;
    float cloudWallDisp = texture(iChannel1,uv).r;

    cloudWallDisp *= smoothstep(3.8,1.8,abs(p.y));
    vec2 uv2 = vec2(p.x*.005 +iTime*.001,p.z*.005 - iTime*.001);
    float cloudDisp = texture(iChannel1,uv2).r;
    cloud -= clamp(cloudDisp, 0.,1.);
    cloud -= clamp(cloudWallDisp, 0.,1.);

    return cloud;
}
float GetDist(vec3 p){

    //--------------------------------
    // distance to center of sphere: 
    // length(sphere position - camera position)
    // distance to outside of the sphere:
    // length(sphere position - camera position) - sphere radius
   // float ds = length(p-sphere.xyz)-sphere.w;

    //distance to floor (plane)
    //this is just height of the origin point passed

    //ground plane
    float dp =p.y;
    //cloud

    float d = dp;
    for(int x = 0; x < 5; x++){
        for(int z = 0; z < 5; z++){
            vec3 pos = abs(p);
            int xOff = x*5*(x%5);
            float yOff =float(sin(float(x)/5.)*10.);
            float cloud = cloudGen(pos,xOff,yOff,z*5*(z%5), 1.+float((x%5)));
            d = smin(d, cloud,0.4);
        }
    }


    return d;
}
float RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    float dO=0.;
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO*rd;

        // get distance to seam
        float ds = GetDist(p);
        //move origin to new point
        dO+=ds*.4;
        if(ds < SURFACE_DIST || dO > MAX_DIST){
            break;
        }
    }
    return min(dO, MAX_DIST);;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p);

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy),
         GetDist(p-e.yxy),
         GetDist(p-e.yyx));

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,50,6);

    lightPos.xz *= Rot(sin(iTime*.05)*50.);
    //light vector
    vec3 l = normalize(lightPos-p+vec3(0,0,0));

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l);
    if(d < length(lightPos-p)){
        diff *= 0.1;
    }
    return diff;
}
vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ro = vec3(4,30,1);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    //ray dir
    //controls rotation
    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    //------------------
    float d = RayMarch(ro,rd);
    // position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d;

    float y = p.y+10.;
    col = vec3(0,y*.01,y);

    //color objects
    if(d < MAX_DIST){

        //get normals
        vec3 n = abs(GetNormal(p));
        col = vec3(0,1,0);
        //colore each normal with appropriate texture



        //colour cloud
        float clouds;
        for(int x = 0; x < 5; x++){
            for(int z = 0; z < 5; z++){
                float cloud = cloudGen(abs(p),x*5*(x%5),0.,z*5*(z%5), -1.*smoothstep(.9,.7,float((x%5))));
                clouds = smin(cloud, cloud,0.4);
            }
        }
        col = p.y *vec3(.6);


        col += clouds * vec3(0,.01,0);

        //get diffuse lighting
        float diff = GetLight(p);
        col *= vec3(diff);



    }
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}
vec2 rotate2D (vec2 _st, float _angle) {
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f) *f;

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
