struct Surface {
    float dist;
    vec3 ambient;
    vec3 diffuse;
    vec3 specular;
    float gloss;
};

float sdOctahedron(in vec3 p, in float s) {
    const float c = 1.73205080757; // sqrt(3)
    return (dot(abs(p), vec3(1.0)) - s) / c;
}

Surface mapScene(in vec3 p) {
    vec3 planeColor = vec3(mod(dot(floor(p.xz), vec2(1.0)), 2.0));
    Surface plane = Surface(p.y + 1.0, 1.6 * planeColor, planeColor, vec3(0.0), 0.0);
    Surface shape = Surface(sdOctahedron(p, 0.5 + 0.5 * sin(iTime)), vec3(0.2, 0.0, 0.0), vec3(1.0, 0.0, 0.0), vec3(1.0), 8.0);

    if (plane.dist < shape.dist) {
        return plane;
    }

    return shape;
}

vec3 getNormal(in vec3 p) {
    vec3 e = vec3(0.001, 0.0, 0.0);
    return normalize(vec3(mapScene(p + e.xyy).dist - mapScene(p - e.xyy).dist,
                          mapScene(p + e.yxy).dist - mapScene(p - e.yxy).dist,
                          mapScene(p + e.yyx).dist - mapScene(p - e.yyx).dist));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    float time = 0.5 * iTime;
    float c = cos(time), s = sin(time);
    vec3 ro = vec3(-5.0 * c, 1.0, -5.0 * s);

    vec3 f = -normalize(ro);
    vec3 r = normalize(vec3(-f.z, 0.0, f.x));
    vec3 u = normalize(cross(r, f));
    vec3 rd = normalize(f + uv.x * r + uv.y * u);

    vec3 l = -f;
    l.xz *= mat2(c, s, -s, c);

    float t = 0.0;
    for (int i=0; i < 100; i++) {
        vec3 p = ro + rd * t;
        Surface scene = mapScene(p);
        if (scene.dist < 0.001) {
            vec3 n = getNormal(p);
            fragColor.rgb += scene.ambient;

            float lambertian = max(0.0, dot(n, l));
            fragColor.rgb += scene.diffuse * lambertian;

            if (lambertian > 0.0) {
                vec3 r = reflect(l, n);
                float specAngle = max(0.0, dot(r, rd));
                fragColor.rgb += scene.specular * max(0.0, pow(specAngle, scene.gloss));
            }

            break;
        }

        if (t > 100.0) {
            break;
        }

        t += scene.dist;
    }
}

const float pi = acos(-1.0);

float t=0.0, g=0.0;
int mat=0;

#define sat(a) clamp(a,0.,1.)
#define rep(p,r) (mod(p,(r))-(r)*.5)
mat2 rot(float a) { float c=cos(a),s=sin(a); return mat2(c,s,-s,c); }

float box(vec3 p, vec3 s) {
    vec3 b=abs(p)-s*.5;
    return length(max(b,0.0)) + min(0.0, max(max(b.x, b.y), b.z));
}

vec2 moda(vec2 p, float r) {
    r = 2.0*pi/r;
    float a= mod(atan(p.y, p.x), r) - r*0.5;
    return vec2(cos(a), sin(a)) * length(p);
}

float hash(float a) { return fract(sin(a*2958.9281)*5493.913); }

vec3 tex(vec3 p) {
    const float s = 0.2;
    const float s2 = 0.005;
    const float it = 10.;
    float c = 0.0;
    for(float i=1.0; i<it+1.0; i++) {
        float h = 5.0*hash(i);
        vec3 v = vec3(fract(     h*i+h*p.x),
                      fract(0.50*h*i+h*p.y),
                      fract(0.75*h*i+h*p.z));
        float l=s-s2*h;
        float r=s+s2*h;

        float a = 1.0;
        a *= smoothstep(l,r,v.x)*smoothstep(l,r,1.0-v.x);
        a *= smoothstep(l,r,v.y)*smoothstep(l,r,1.0-v.y);
        a *= smoothstep(l,r,v.z)*smoothstep(l,r,1.0-v.z);
        c += a/it;
    }
    return vec3(sat(c));
}

float city(vec3 p) {
    p.y+=25.0;
    float d=10000.0;
    float s=1.5;
    float ss=s-0.15;
    vec2 id=floor(p.xz/s);
    float den = texture(iChannel0, id*0.001).r;
    den=pow(den, 2.0)*0.4+.1;
    float h=pow(10.0*den, 3.0)+sin(t*2.0+id.x*id.y*0.01);
    p.xz=rep(p.xz,s);
    d = min(d,box(p, vec3(ss,h,ss)));
    return d;
}

float hplus(vec3 p) {
    vec3 pp=p;
    const float s = .3;
    p.xz *= rot(.25*pi);
    p.xz = moda(p.xz, 4.);
    p -= .1*(tex(pp*.4)-.5);
    float o = box(p, vec3(2.,.8,2.));
    o = min(o, box(p-vec3(1.5,0,0), vec3(3,s,s)));
    o = min(o, box(p-vec3(2.8,0,0), vec3(1.4,.8,1.)));
    return o;
}

float pawtickles(vec3 p) {
    vec3 pp = p;
    pp.xz *= rot(.25*pi);
    pp.xz = moda(pp.xz, 4.);
    float o = length(vec3(mod(pp.x+3.0*fract(t*2.0),3.0)-1.5, pp.y, pp.z))-0.05;
    float a = sin(2.0*sin(t*2.0))*sin(t+pp.x*pp.z*(10.0*fract(t*2.0)));
    g+=1.0/(0.01+pow(abs(o),2.0)*(10.0+9.7*a));
    return o;
}

float scene(vec3 p) {
    float d = 10000.;
    p.xz *= rot(t*.25);
    vec3 pp=p;
    float a = pow(sin(2.0*sin(t*2.0)), 2.0);
    pp.xz *= rot(a*0.05*sin(3.0*(p.y+t*10.0)));
    pp.yz *= rot(a*0.05*sin(2.0*(p.x+t*6.0)));

    float o = hplus(pp);
    if(o<d) { d=o;mat=1; }

    float ptk=pawtickles(p);
    d = min(d,max(ptk, .1));

    float c = city(p);
    if(c<d) { d=c;mat=2; }

    return d;
}

vec3 march(vec3 o, vec3 rd, float tr, float md) {
    float d=0.0;
    float ii=0.0;
    for(float i=0.0; i<100.0; i++) {
        float h=scene(o+rd*d)*0.8;
        if(abs(h)<tr) return vec3(d,i,1.0);
        if(d>md) return vec3(d,i,0.0);
        d+=h;
        ii=i;
    }
    return vec3(d,ii,0.0);
}

vec3 normal(vec3 p) {
    vec2 e=vec2(0.01,0.0);
    return normalize(scene(p)-vec3(scene(p-e.xyy), scene(p-e.yxy), scene(p-e.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1.0);

    t=mod(iTime, 20.0*pi);
    uv *= 1. - length(uv)*.25;
    vec3 eye = 4.5*vec3(1.,.6+.4*sin(t),-1.);
    eye.xz+= 2.*vec2(sin(t), cos(t));
    vec3 tg = vec3(0);
    vec3 f=normalize(tg-eye);
    vec3 s=normalize(cross(vec3(-.25+.5*sin(t),1,0), f));
    vec3 u=normalize(cross(f, s));
    vec3 dir=normalize(f*.6+uv.x*s+uv.y*u);

    vec3 lp = vec3(3);
    lp.xyz-= 3.*vec3(sin(t), cos(t), cos(t));

    vec3 col = vec3(0);
    float ddd = 0.0;

    vec3 m = march(eye, dir, .001, 500.);
    ddd = m.x;
    if(m.z == 1.0) {
        vec3 p = eye+dir*m.x;
        vec3 n=normal(p);
        vec3 ld=normalize(lp-p);
        float diff = (abs(dot(n, ld)));
        float spec = (pow(abs(dot(dir, reflect(ld, n))), 30.0));
        float fres = sat(pow(max(0.0, 1.0-dot(n, -dir)), 1.0));
        if(mat == 1) {
            col += sat(vec3(diff+spec)*fres);
        }
        if(mat == 2) {
          col += 0.005*vec3(spec)*acos(-dir);
        }
        col *= pow(m.y/50., 2.);
    } else {
        col += smoothstep(0.,.3,dir.y+.07)*acos(-dir);
    }
    col += acos(-dir)*g*.01;
    fragColor.rgb = pow(col, vec3(1./2.2));
}

// SIMD Implementation of xoroshiro64**
//
// next2 generates two uniformly distributed unsigned ints (32 bits),
// with a 4*32 bits state. The state must be seeded so it is not
// everywhere zero.
//
// Note that using correlated values as seeds (such as pixel coordinates)
// results in correlated sequences in the generator output. This is why
// seeds should be hashed before being used for a PRNG.
//
// Reference implementation by David Blackman and Sebastiano Vigna:
//   http://xoshiro.di.unimi.it/
//   http://xoshiro.di.unimi.it/xoroshiro64starstar.c

uvec2 rotl2(uvec2 x, int k) {
    return (x << k) | (x >> (32 - k));
}

// Generates 2 uniform integers, updates the state given as input
uvec2 next2(inout uvec4 s) {
    uvec2 s0 = s.xz;
    uvec2 s1 = s.yw;
    uvec2 rs = rotl2(s0 * 0x9E3779BBu, 5) * 5u;

    s1 ^= s0;
    s.xz = rotl2(s0, 26) ^ s1 ^ (s1 << 9);
    s.yw = rotl2(s1, 13);

    return rs;
}

// Converts a vector of unsigned ints to floats in [0,1]
vec4 tofloat(uvec4 u) {
    //Slower, but generates all dyadic rationals of the form k / 2^-24 equally
    //return vec4(u >> 8) * (1. / float(1u << 24));

    //Faster, but only generates all dyadic rationals of the form k / 2^-23 equally
    return uintBitsToFloat(0x7Fu << 23 | u >> 9) - 1.;
}

// Simple coordinate hash
void hash(inout uvec4 x) {
    x = ((x >> 16) ^ x) * 0x45d9f3bu;
    x = ((x >> 16) ^ x) * 0x45d9f3bu;
    x = (x >> 16) ^ x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Seed the generator: this requires setting 4 uints
    //  int pixel coords
    uvec4 s = uvec4(fragCoord, 0, 0);
    //  Enumerate all pixels in row-major order
    s.x = 4u * (s.y * uint(iResolution.x) + s.x);
    s.yzw = s.x + uvec3(1, 2, 3);
    //  Hash coordinates used in the state
    //  This is what decorrelates the different parts of the state
    hash(s);

    s += uvec4(iFrame, iFrame + 1, iFrame + 2, iFrame + 3);

    hash(s);

    // Generate random numbers, output as float
    fragColor = tofloat(uvec4(next2(s),next2(s)));
}

const float PI = 3.14159265;

