const uint K = 0x456789abU;
const float uint_range = 1. / float(0xFFFFFFFFU);

void xorshift(inout uint seed) {
  seed ^= seed << 13;
  seed ^= seed >> 17;
  seed ^= seed << 5;
}

float next_float(inout uint seed) {
  xorshift(seed);
  return float(seed - 1U) * uint_range;
}

float atan2(float y, float x){
  return x == 0.0 ? sign(y) * PI/2.0 : atan(y, x);
}

vec3 texture_check(vec2 uv, vec3 color) {
  if (sin(uv.x * 8.0 * PI) * sin(uv.y * 8.0 * PI) > 0.0) {
    return vec3(1.0);
  } else {
    return color;
  }
}

vec3 texture_check(vec3 uv) {
  if (sin(uv.x * 4.0 * PI) * sin(uv.y * 2.0 * PI) * sin(uv.z * 4.0 * PI) > 0.0) {
    return vec3(1.0);
  } else {
    return vec3(0.1);
  }
}

vec2 random_in_disk(inout uint seed) {
  float r1 = next_float(seed);
  float r2 = next_float(seed);

  float r = sqrt(r1);
  float theta = 2.0 * PI * r2;
  float x = r * cos(theta);
  float y = r * sin(theta);
  return vec2(x, y);
}

vec3 random_in_sphere(inout uint seed) {
  vec3 v;
  for (int i = 0; i < 1024; ++i) {
    v.x = next_float(seed) - 0.5;
    v.y = next_float(seed) - 0.5;
    v.z = next_float(seed) - 0.5;
    if (length(v) < 0.5) return v * 2.0;
  }
  return normalize(v);
}

vec3 random_unit_vector(inout uint seed) {
  return normalize(random_in_sphere(seed));
}

vec3 background(vec3 rd) {
  return vec3(0.2);
}

float hit_sphere(vec3 ro, vec3 rd, vec3 center, float radius) {
  vec3 oc = ro - center;
  float a = dot(rd, rd);
  float b = 2.0 * dot(oc, rd);
  float c = dot(oc, oc) - radius * radius;
  float discriminant = b * b - 4.0 * a * c;
  if (discriminant < 0.0) {
    return -1.0;
  }
  return (-b - sqrt(discriminant)) / (2.0 * a);
}

bool hit_xy_rect(vec3 ro, vec3 rd, vec3 center, vec2 size, float tMin, float tMax, out float t, out vec3 rp, out vec2 uv) {
  float k = center.z;
  t = (k - ro.z) / rd.z;
  if (t < tMin || t > tMax) {
    return false;
  }

  vec2 min_p = center.xy - size;
  vec2 max_p = center.xy + size;

  vec3 p = ro + t * rd;
  if (p.x < min_p.x || p.x > max_p.x
    || p.y < min_p.y || p.y > max_p.y) {
    return false;
  }

  rp = p;
  uv = (p.xy - min_p) / (max_p - min_p);
  return true;
}

bool hit_yz_rect(vec3 ro, vec3 rd, vec3 center, vec2 size, float tMin, float tMax, out float t, out vec3 rp, out vec2 uv) {
  float k = center.x;
  t = (k - ro.x) / rd.x;
  if (t < tMin || t > tMax) {
    return false;
  }

  vec2 min_p = center.zy - size;
  vec2 max_p = center.zy + size;

  vec3 p = ro + t * rd;
  if (p.z < min_p.x || p.z > max_p.x
    || p.y < min_p.y || p.y > max_p.y) {
    return false;
  }

  rp = p;
  uv = (p.zy - min_p) / (max_p - min_p);
  return true;
}

bool hit_xz_rect(vec3 ro, vec3 rd, vec3 center, vec2 size, float tMin, float tMax, out float t, out vec3 rp, out vec2 uv) {
  float k = center.y;
  t = (k - ro.y) / rd.y;
  if (t < tMin || t > tMax) {
    return false;
  }

  vec2 min_p = center.xz - size;
  vec2 max_p = center.xz + size;

  vec3 p = ro + t * rd;
  if (p.x < min_p.x || p.x > max_p.x
    || p.z < min_p.y || p.z > max_p.y) {
    return false;
  }

  rp = p;
  uv = (p.xz - min_p) / (max_p - min_p);
  return true;
}

vec2 get_sphere_uv(vec3 p) {
  float phi = atan2(p.z, p.x);
  float theta = asin(p.y);
  float u = 1.0 - (phi + PI) / (2.0 * PI);
  float v = theta / PI + 0.5;
  return vec2(u,v);
}

bool raytracing(vec3 ro, vec3 rd, in Sphere spheres[LEN_SPHERES], in Rect rects[LEN_RECTS], out Hit hit) {
  float t_min = EPS;
  float t_max = INF;
  Sphere sph;
  bool hit_flag = false;
  vec3 nv;
  vec3 rp;
  vec2 uv;

  int geo_idx = 0;

  float t;

  for (int i = 0; i < LEN_SPHERES; i++) {
    Sphere sphere = spheres[i];
    float t = hit_sphere(ro, rd, sphere.center, sphere.radius);
    if (t >= t_min && t < t_max) {
      rp = ro + t * rd;
      nv = (rp - sphere.center) / sphere.radius;
      t_max = t;
      uv = get_sphere_uv((rp - sphere.center) / sphere.radius);
      geo_idx = i;
      hit_flag = true;
    }
  }
  for (int i = 0; i < LEN_RECTS; ++i) {
    Rect rect = rects[i];
    if (rect.axis == XY) {
      if (hit_xy_rect(ro, rd, rect.center, rect.size, t_min, t_max, t, rp, uv)) {
        nv = vec3(0., 0., 1.0);
        geo_idx = LEN_SPHERES + i;
        hit_flag = true;
        t_max = t;
      }
    } else if (rect.axis == YZ) {
      if (hit_yz_rect(ro, rd, rect.center, rect.size, t_min, t_max, t, rp, uv)) {
        nv = vec3(1.0, 0., 0.);
        geo_idx = LEN_SPHERES + i;
        hit_flag = true;
        t_max = t;
      }
    } else if (rect.axis == XZ) {
      if (hit_xz_rect(ro, rd, rect.center, rect.size, t_min, t_max, t, rp, uv)) {
        nv = vec3(0.0, -1.0, 0.);
        geo_idx = LEN_SPHERES + i;
        hit_flag = true;
        t_max = t;
      }
    }
  }
  if (!hit_flag) return false;

  hit = Hit(rp, nv, uv, geo_idx);
  return true;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  vec2 r = iResolution.xy;
  vec2 uv = gl_FragCoord.xy/r;

