  vec3 white = vec3(1.);
  Sphere spheres[LEN_SPHERES];
  Rect rects[LEN_RECTS];
  Material materials[LEN_SPHERES + LEN_RECTS];

  {
    float rotation_speed = 0.5;
    int i = 0;
    uint j = 11U;
    float r, l, phi;
    float phiStep = 2.0 * PI / float(LEN_SPHERES - 1);

    phi = iTime * rotation_speed;
    l = 0.0;

    r = 1000.;
    spheres[i] = Sphere(vec3(0., -r, 0.), r);
    materials[i] = Material(METAL, TEX_CHECK3, vec3(0.7, 0.7, 0.4), black);
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.2;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(METAL, TEX_CHECK2, vec3(0., .0, .9), black);
    phi += phiStep;
    i++;

    l = next_float(j) + 1.0;
    r = 0.1 + next_float(j) * 0.2;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(LAMBERTIAN, TEX_COLOR, vec3(0.9, 0.9, 0.9), black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.2;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(LAMBERTIAN, TEX_COLOR, vec3(0.8, 0.3, 0.3), black);
    phi += phiStep;
    i++;

    l = next_float(j) + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(METAL, TEX_COLOR, vec3(0.9, .9, .9), black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(LAMBERTIAN, TEX_COLOR, vec3(.9, .1, .0), black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(METAL, TEX_COLOR, white * 0.9, black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(METAL, TEX_COLOR, vec3(0.8, 0.6, 0.2), black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(LAMBERTIAN, TEX_COLOR, vec3(0., .9, .1), black);
    phi += phiStep;
    i++;

    l = next_float(j) * 0.5 + 1.0;
    r = 0.1 + next_float(j) * 0.1;
    spheres[i] = Sphere(vec3(l*cos(phi), r, l*sin(phi)), r);
    materials[i] = Material(LAMBERTIAN, TEX_COLOR, vec3(0.9, 0.0, 0.9), black);
    phi += phiStep;
    i++;

    float w = 1.6;
    float h = 0.4;
    rects[i - LEN_SPHERES] = Rect(vec3(0., h, 1.6), vec2(w, h), XY);
    materials[i] = Material(METAL, TEX_COLOR, vec3(1.0), black);
    i++;

    rects[i - LEN_SPHERES] = Rect(vec3(0., h, -1.6), vec2(w, h), XY);
    materials[i] = Material(METAL, TEX_COLOR, vec3(1.0), black);
    i++;

    rects[i - LEN_SPHERES] = Rect(vec3(1.6, h, 0.), vec2(w, h), YZ);
    materials[i] = Material(METAL, TEX_COLOR, vec3(1.0), black);
    i++;

    rects[i - LEN_SPHERES] = Rect(vec3(-1.6, h, 0.), vec2(w, h), YZ);
    materials[i] = Material(METAL, TEX_COLOR, vec3(1.0), black);
    i++;

    rects[i - LEN_SPHERES] = Rect(vec3(-0., h * 2.0, 0.), vec2(w, w), XZ);
    materials[i] = Material(METAL, TEX_COLOR, vec3(1.0), black);
    i++;
  }

  float t = iTime * 0.25;

  float rate = 2.0;
  for (int i = 1; i < LEN_SPHERES; i += 3) {
    float jumpY = spheres[i].radius * (pow(abs(sin((rate * t + 1.) * PI)), 16.0) + 1.0);
    spheres[i].center.y = jumpY;
    rate *= 2.0;
  }

  uint sseed = uint(t + 1.5);
  for (int i = 0; i < 3; ++i) {
    int li = 2 + i * 3 + int(next_float(sseed) * 2.0);
    materials[li].emit = vec3(3.0 * pow(abs(sin(t * PI + PI * 0.5)), 8.0));
  }

  float r_rate = 1./min(r.x,r.y);
  vec2 p = (gl_FragCoord.xy*2.-r)*r_rate;
  vec3 ro0 = vec3(0.0, 0.5, 0.0);

  vec3 ta = vec3(0., 0.5, 2.);

  float focus_dist = length(ta - ro0);
  vec3 cdir = normalize(ta - ro0);
  vec3 side = cross(cdir,vec3(0.,1.,0.));
  vec3 up = cross(side, cdir);

  vec3 rd0 = normalize(p.x*side+p.y*up+cdir*fov);

  vec3 col = vec3(0.);
  int n = 0;

  int total_depth = 0;

  uint seed = uint(gl_FragCoord.y * r.x + gl_FragCoord.x);

  vec3 ro;
  vec3 rd;
  for (int s = 0; s < NS; ++s) {
    ro = ro0;
    rd = rd0;

    vec3 attenuation = vec3(1.);
    int depth = 0;

    for (int i = 0; i < ITER; ++i) {

      Hit ret;
      if (!raytracing(ro, rd, spheres, rects, ret)) {
        col += attenuation * background(rd);
        n++;
        break;
      }

      vec3 rp = ret.rp;
      Material material = materials[ret.index];

      if (length(material.emit) > 0.1) {
        col += attenuation * material.emit;
        n++;
        break;
      }

      vec3 albedo;
      if (material.texture == TEX_CHECK2) {
        albedo = texture_check(ret.uv, material.albedo);
      } else if (material.texture == TEX_CHECK3) {
        albedo = texture_check(rp);
      } else {
        albedo = material.albedo;
      }

      int type = material.type;
      if (type == LAMBERTIAN) {
        rd = ret.nv + random_unit_vector(seed);
      } else {
        vec3 reflected = reflect(rd, ret.nv);
        rd = reflected;
      }

      rd = normalize(rd);
      ro = rp;
      attenuation *= albedo;
      depth++;
    }
    if (depth == 0) break;
    total_depth += depth;
  }
  col = n > 0 ? sqrt(col / float(n)) : vec3(0.05);
  fragColor = vec4(col, 1.);
}

#define PI 3.14159265358979323846

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

float box(vec2 _st, vec2 _size, float i, float _smoothEdges){
    _size = vec2(i)-_size*i;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
    return uv.x*uv.y;
}

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.913);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(abs(cos(iTime)),sin(iTime),abs(sin(iTime)));

    // Divide the space in 4
    st = tile(st,4.);

    // Use a matrix to rotate the space 45 degrees
    st = rotate2D(st,PI*fract(iTime));

    // Draw a square
    color += vec3(box(st,vec2(0.8),sin(iTime),1.0-abs(cos(iTime))));
    color *= vec3(st,abs(cos(iTime)));
    color /= vec3(st,abs(sin(iTime)));

    fragColor = vec4(color,1.0);
}

