uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec3 hsv2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return c.z * mix(vec3(1.0), rgb, c.y);
}
uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k.x;
}
uvec2 hash22u(uvec2 n) {
    n ^= (n.yx << 24);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    return n * k.xy;
}
vec2 hash22(vec2 p) {
    uvec2 n = uvec2(p);
    vec2 v = vec2(hash22u(n)) / vec2(max32);
    return normalize(2.0 * v - vec2(1.0));
}
float hash21(vec2 p) {
    uint n = hash11u(uint(p.x)) + uint(p.y);
    return float(hash11u(n)) / float(max32);
}
float vnoise21(vec2 p) {
    vec2 i = floor(p);
    float v00 = hash21(i);
    float v01 = hash21(i + vec2(0.0, 1.0));
    float v10 = hash21(i + vec2(1.0, 0.0));
    float v11 = hash21(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
}
float hash31(vec3 p) {
    uint n = hash11u(hash11u(uint(p.x)) + uint(p.y)) + uint(p.z);
    return float(hash11u(n)) / float(max32);
}
float vnoise31(vec3 p) {
    vec3 i = floor(p);
    float v000 = hash31(i);
    float v001 = hash31(i + vec3(0.0, 0.0, 1.0));
    float v010 = hash31(i + vec3(0.0, 1.0, 0.0));
    float v011 = hash31(i + vec3(0.0, 1.0, 1.0));
    float v100 = hash31(i + vec3(1.0, 0.0, 0.0));
    float v101 = hash31(i + vec3(1.0, 0.0, 1.0));
    float v110 = hash31(i + vec3(1.0, 1.0, 0.0));
    float v111 = hash31(i + vec3(1.0, 1.0, 1.0));
    vec3 f = fract(p);
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float xy0 = mix(mix(v000, v100, f.x), mix(v010, v110, f.x), f.y);
    float xy1 = mix(mix(v001, v101, f.x), mix(v011, v111, f.x), f.y);
    return mix(xy0, xy1, f.z);
}
uvec3 hash33u(uvec3 n) {
    n ^= (n.yzx << 24);
    n ^= (n.yzx >> 1);
    n *= k;
    n ^= (n.yzx << 1);
    return n * k;
}
vec3 hash33(vec3 p) {
        uvec3 n = uvec3(p);
        vec3 v = vec3(hash33u(n)) / vec3(max32);
        return normalize(2.0 * v - vec3(1.0));
}
float gnoise21(vec2 p) {
    vec2 i = floor(p);
    vec2 g00 = hash22(i);
    vec2 g01 = hash22(i + vec2(0.0, 1.0));
    vec2 g10 = hash22(i + vec2(1.0, 0.0));
    vec2 g11 = hash22(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return 0.5 * v + 0.5;
}
float gnoise31(vec3 p) {
    vec3 i= floor(p);
    vec3 g000 = hash33(i);
    vec3 g001 = hash33(i + vec3(0.0, 0.0, 1.0));
    vec3 g010 = hash33(i + vec3(0.0, 1.0, 0.0));
    vec3 g011 = hash33(i + vec3(0.0, 1.0, 1.0));
    vec3 g100 = hash33(i + vec3(1.0, 0.0, 0.0));
    vec3 g101 = hash33(i + vec3(1.0, 0.0, 1.0));
    vec3 g110 = hash33(i + vec3(1.0, 1.0, 0.0));
    vec3 g111 = hash33(i + vec3(1.0, 1.0, 1.0));
    vec3 f = fract(p);
    float v000 = dot(g000, f);
    float v001 = dot(g001, f - vec3(0.0, 0.0, 1.0));
    float v010 = dot(g010, f - vec3(0.0, 1.0, 0.0));
    float v011 = dot(g011, f - vec3(0.0, 1.0, 1.0));
    float v100 = dot(g100, f - vec3(1.0, 0.0, 0.0));
    float v101 = dot(g101, f - vec3(1.0, 0.0, 1.0));
    float v110 = dot(g110, f - vec3(1.0, 1.0, 0.0));
    float v111 = dot(g111, f - vec3(1.0, 1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    float xy0 = mix(mix(v000, v100, f.x), mix(v010, v110, f.x), f.y);
    float xy1 = mix(mix(v001, v101, f.x), mix(v011, v111, f.x), f.y);
    return 0.5 * mix(xy0, xy1, f.z) + 0.5;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = fragCoord.xy/min(iResolution.x, iResolution.y);
    pos *= 20.;
    pos += iTime;
    float v;
    if (fragCoord.x < 0.5 * iResolution.x){
        if (fragCoord.y < 0.5 * iResolution.y){
            v = vnoise21(pos);
        } else {
            v = vnoise31(vec3(pos, iTime));
        }
    } else{
        if (fragCoord.y < 0.5 * iResolution.y){
            v = gnoise21(pos);
        } else {
            v = gnoise31(vec3(pos, iTime));
        }
    }
    vec3 col = hsv2rgb(vec3(v, 1.0, 1.0));
    fragColor = vec4(col, 1.0);
}

