    // Assign a color using the closest point position
   //  color += dot(f_st,vec2(-0.060,-0.460)*PI/2.*sin(iTime));

    st-=st/2.;
    st*=rotate2d(noise+PI/cos(iTime*0.005));
    st*=scale(vec2(rand(st*0.000075)*(atan(fract(iTime/100.)))));
    st+=st*2.;

    st*=1.-noise;

   // Add distance field to closest point center
    color.r =rand(noise*st*0.5)*iTime/100.;
