float worley(vec3 n,float s)
{
    float dis = 2.0;
    for(int x = -1;x<=1;x++)
    {
        for(int y = -1;y<=1;y++)
        {
            for(int z = -1; z <= 1; z++) {
                vec3 p = floor(n/s)+vec3(x,y,z);
                float d = length(random(p)+vec3(x,y,z)-fract(n/s));
                if (dis>d)
                {
                    dis = d;   
                }
            }

        }
    }
    return 1.0 - dis;

}

// copy from https://www.shadertoy.com/view/4sc3z2
#define MOD3 vec3(.1031,.11369,.13787)

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float perlin_noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}

// not so worley anymore
float perlinworley3d(vec3 p) {
    float noise = perlin_noise(20.*p + iTime) * .8 + .05
                + perlin_noise(40.*p + iTime) * .5
                + perlin_noise(70.*p + iTime) * .25
      //          * worley(12.*p, 1.0);
      ;
    //noise += perlin_noise(12.*p) * .5;
    return noise;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

# define BBOX 1
float sdBoundingBox( vec3 p, vec3 b, float e )
{
       p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

int id = 0;

float map(vec3 p) {
    id = 0;
    float volBox = sdBox(p, vec3(0.27));
    float bBox = sdBoundingBox(p, vec3(0.27), 0.005);
    if(bBox < volBox) {
        id = BBOX;
    }

    return min(volBox, bBox);
}

vec3 normal(in vec3 p) {
    float eps = MIN_MARCH_DIST;
    vec2 h = vec2(eps, 0);
    return normalize(vec3(map(p+h.xyy) - map(p-h.xyy),
                          map(p+h.yxy) - map(p-h.yxy),
                          map(p+h.yyx) - map(p-h.yyx)));
}

mat2 rotate(float a) {
    float si = sin(a), co = cos(a);
    return mat2(co, si, -si, co);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(0,0,-1.);
    ro.xz *= rotate(iTime/8.);
    vec3 ta = vec3(0,0,0);
    vec3 ww = normalize(ta-ro);
    vec3 uu = normalize(cross(ww, vec3(0,1,0)));
    vec3 vv = normalize(cross(uu,ww));
    vec3 rd = normalize(uv.x*uu + uv.y*vv + 1.0*ww);

    vec3 col = vec3(0,0,0);
    vec3 p = vec3(0);
    float t = 0.;
    float i = 0.;
    float sampleDensity = 0.;
    bool inside = false;
    float StepSize = 1. / MAX_VOLUMETRIC_STEPS;    
    vec3 C = vec3(0,0,0);
    vec3 C2 = vec3(0);

    for(i=0.; i < MAX_MARCH_STEPS; i++) {
        p = ro + t*rd;
        float d = map(p);
        if(inside && d > 0.) {
            // there is nothing behind the volume atm
            // so just break when we exit the volume.
            break;
        }
        if(d < MIN_MARCH_DIST) {
            if(id == BBOX) {
                break;
            }
            if(!inside) {
                // fix weird plane visuals
                t += random(dot(p, p) + iTime) * StepSize;
                p = ro + t*rd;
            }
            inside = true;
            float density = perlinworley3d(p);


            // method1
            sampleDensity += clamp(density, 0., 1.) * StepSize;
            // Cout(v) = Cin(v) * (1 - Opacity(x)) + Color(x) * Opacity(x)
            C = C * (1. - density * StepSize) + vec3(1,0,0) * density * StepSize;


            // method2
            float absorbance = exp(-density * StepSize);
            C2 += vec3(1,0,0) * (1.-absorbance);


            t += StepSize;
        }
        else {
            t += d;
        }
        if(t > MAX_MARCH_DIST)
            break;
    }
    if(i >= MAX_MARCH_STEPS) {
        t = MAX_MARCH_DIST;
    }

    float strength = 14.;
    sampleDensity *= strength;
    C *= strength;

    if(t < MAX_MARCH_DIST) {
        if(id == BBOX) {
            vec3 L = normalize(ro-rd - cross(vec3(0,1,0),rd)*.2);
            vec3 N = normal(p);            
            vec3 c = vec3(.7) * clamp(dot(L, N), 0., 1.) + vec3(0.1);
            col = mix(c, C, sampleDensity);
        }
        else col = C2*14.;// C2*14.;
    }

    fragColor = vec4(pow(col, 1./vec3(2.2)), 1);
}

uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
const uint max32 = 0xffffffffu;
vec2 fragCoord_;
vec2[8] gtable2 = vec2[](   //0.92387953 = cos(pi/8), 0.38268343 = cos(pi/8)
    vec2(0.92387953, 0.38268343),
    vec2(0.38268343, 0.92387953),
    vec2(-0.92387953, 0.38268343),
    vec2(-0.38268343, 0.92387953),
    vec2(0.92387953, -0.38268343),
    vec2(0.38268343, -0.92387953),
    vec2(-0.92387953, -0.38268343),
    vec2(-0.38268343, -0.92387953)
);
uint hash11u(uint n) {
    n ^= (n << 24);
    n ^= (n >> 1);
    n ^= (n << 1);
    return n * k.x;
}
uvec2 hash22u(uvec2 n) {
    n ^= (n.yx << 24);
    n ^= (n.yx >> 1);
    n *= k.xy;
    n ^= (n.yx << 1);
    return n * k.xy;
}
vec2 hash22(vec2 p) {
        uint n = hash11u(hash11u(uint(p.x)) + uint(p.y));
        n = n >> 29;
        n = n % 8u;
        return gtable2[n];
}
float pnoise21(vec2 p) {
    vec2 f = fract(p);
    vec2 g00 = hash22(p);
    vec2 g01 = hash22(p + vec2(0.0, 1.0));
    vec2 g10 = hash22(p + vec2(1.0, 0.0));
    vec2 g11 = hash22(p + vec2(1.0, 1.0));
    float v00 = dot(g00, f);
    float v01 = dot(g01, f - vec2(0.0, 1.0));
    float v10 = dot(g10, f - vec2(1.0, 0.0));
    float v11 = dot(g11, f - vec2(1.0, 1.0));
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f); 
    float v = mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y);
    return v;
}
float hash21(vec2 p) {
    uint n = hash11u(uint(p.x)) + uint(p.y);
    return float(hash11u(n)) / float(max32);
}
float vnoise21(vec2 p) {
    vec2 i = floor(p);
    float v00 = hash21(i);
    float v01 = hash21(i + vec2(0.0, 1.0));
    float v10 = hash21(i + vec2(1.0, 0.0));
    float v11 = hash21(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * (3.0 -2.0 * f); 
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y)-0.5;
}
float fbm21(vec2 p, float G){
    float val = 0.0;
    float amp = 1.0;
    float freq = 1.0;
    for (int i = 0; i < 4; i++){
        val += amp * vnoise21(freq * p);
        amp *= G;
        freq *= 2.01;
    }
    return val;
}
float warp21(vec2 p, float G){
    int n = 3;
    float val = 0.0;
    for (int i = 0; i < n; i++){
        vec2 dir = vec2(sin(G * val), cos(G * val));
        if (fragCoord_.y < 0.5 * iResolution.y){
            val = fbm21(p + dir, 0.5);
        } else{
            val = pnoise21(p + dir);
        }
    }
    return val;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    fragCoord_ = fragCoord;
    vec2 pos = fragCoord.xy/min(iResolution.x, iResolution.y);
    pos *= 10.;
    pos += iTime;
    float v = warp21(pos, 3.0);
    if(fragCoord.x < 0.33 * iResolution.x){
        v = step(0.0, v);  //left
    } else if (fragCoord.x < 0.66 * iResolution.x){
        v = smoothstep(-0.2, 0.2, v);   //center
    } else {
        v = max(0.0, v) * 2.0;  //right
    }
    fragColor = vec4(vec3(v), 1.0);
}

