// from:
// https://gist.github.com/volkansalma/2972237
// source:
// http://dspguru.com/dsp/tricks/fixed-point-atan2-with-self-normalization/

#define PI_FLOAT     3.14159265f
#define PIBY2_FLOAT  1.5707963f

float atan2_approximation1(float y, float x)
{
    //http://pubs.opengroup.org/onlinepubs/009695399/functions/atan2.html
    //Volkan SALMA

    float ONEQTR_PI = PI_FLOAT / 4.0;
	float THRQTR_PI = 3.0 * PI_FLOAT / 4.0;
	float r, angle;
	float abs_y = abs(y) + 1e-10f;      // kludge to prevent 0/0 condition
	if ( x < 0.0f )
	{
		r = (x + abs_y) / (abs_y - x);
		angle = THRQTR_PI;
	}
	else
	{
		r = (x - abs_y) / (x + abs_y);
		angle = ONEQTR_PI;
	}
	angle += (0.1963f * r * r - 0.9817f) * r;
	if ( y < 0.0f )
		return( -angle );     // negate if in quad III or IV
	else
		return( angle );


}

// |error| < 0.005
float atan2_approximation2( float y, float x )
{
	if ( x == 0.0f )
	{
		if ( y > 0.0f ) return PIBY2_FLOAT;
		if ( y == 0.0f ) return 0.0f;
		return -PIBY2_FLOAT;
	}
	float atan;
	float z = y/x;
	if ( abs( z ) < 1.0f )
	{
		atan = z/(1.0f + 0.28f*z*z);
		if ( x < 0.0f )
		{
			if ( y < 0.0f ) return atan - PI_FLOAT;
			return atan + PI_FLOAT;
		}
	}
	else
	{
		atan = PIBY2_FLOAT - z/(z*z + 0.28f);
		if ( y < 0.0f ) return atan - PI_FLOAT;
	}
	return atan;
}

float remap(float x, float in_min, float in_max, float out_min, float out_max)
{
    return out_min + (x - in_min) * (out_max - out_min) / (in_max - in_min);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float tiling = 13.0;
    float speed = 0.0 * iTime / (1.0 / tiling);

    float u = uv.x - 0.5;
    float v = uv.y - 0.5;

    // atan2_a
    float atan2_a = atan2_approximation1(u, v);
    atan2_a = mod(remap(atan2_a, -PI_FLOAT, PI_FLOAT, 0.0, 1.0) * tiling + speed, 1.0);

    vec3 atan2_a_clr = vec3(atan2_a, atan2_a, atan2_a);

    // atan2_b
    float atan2_b = atan2_approximation2(u, v);
    atan2_b = mod(remap(atan2_b, -PI_FLOAT, PI_FLOAT, 0.0, 1.0) * tiling + speed, 1.0);

    vec3 atan2_b_clr = vec3(atan2_b, atan2_b, atan2_b);

    // ground truth atan2
    float atan2 = atan(u, v);
    atan2 = mod(remap(atan2, -PI_FLOAT, PI_FLOAT, 0.0, 1.0) * tiling + speed, 1.0);

    vec3 atan2_clr = vec3(atan2, atan2, atan2);

    float line = (sin(iTime * 2.) + 1.0) / 2.0;
    // compare approximation a and b
    if(uv.x < line - 0.001) fragColor = vec4(atan2_a_clr, 1.0);
    if(uv.x > line + 0.001) fragColor = vec4(atan2_b_clr, 1.0);

    // compare approximation a and ground truth
    //if(uv.x < line - 0.001) fragColor = vec4(atan2_a_clr, 1.0);
    //if(uv.x > line + 0.001) fragColor = vec4(atan2_clr, 1.0);

    // compare approximation b and ground truth
    //if(uv.x < line - 0.001) fragColor = vec4(atan2_b_clr, 1.0);
    //if(uv.x > line + 0.001) fragColor = vec4(atan2_clr, 1.0);
}

