    int i = 0;
    float d = 1.;

    float n =  9.;

    for(i=0; i<ITER; i++){
        float cr = length(r);
        float nr = pow(cr, n);
        //*abs(sin(iTime/5.) )
        float accos = acos(r.z/cr);
        float actan = atan(r.y/r.x);

        d = pow(cr, n-1.) *n * d + 1.0;

        rNext.x = nr*sin(accos*n)*cos(n*actan);
        rNext.y = nr*sin(accos*n)*sin(n*actan);
        rNext.z = nr*cos(n*accos);
        r = c + rNext;

        trap = min(trap, vec4(abs(r), m));
        map = vec3(trap.yzw);

        m = dot(r,r);

        if(m > 256.){
            break;
        }
    }

    return  .25*log(m) * sqrt(m) / d;
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d; 
}

float RayMarch(in vec3 ro, in vec3 rd, out vec3 col, in vec3 c){
    float dO = 0.;

    for(int i=0; i<MAX_STEPS; i++){
        vec3 p = ro+rd*dO;
        p.xz *= Rot(iTime/SPEED);
        p.yz *= Rot(iTime/SPEED-2.);

        float mbd = mandel3(abs(p), c, col);
        float dS = mbd;

        if(dO > MAX_DIST || dS<SURF_DIST) break;
        dO += dS;
    }

    return dO;
}
vec3 GetNormal(in vec3 p, in vec3 c){
    vec3 col;
    // my e value made black artifacts
    // so I took a look at iq's normal epsilone. 
    // don't exactly know how this is calculated.
    // looks like it is relevant to some screen pixel size calculation..?
    vec2 e = vec2(1.0,-1.0)*0.5773*0.25*2.0/(iResolution.y*1.5);

    // getting vector with very small vector
    vec3 n = vec3(
        e.xyy*mandel3(p+e.xyy, c, col)+
        e.yxy*mandel3(p+e.yxy, c, col)+
        e.yyx*mandel3(p+e.yyx, c, col)+
        e.xxx*mandel3(p + e.xxx, c, col ) 
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/(iResolution.y);
    vec2 m = iMouse.xy/iResolution.xy;
    vec3 col = vec3(0);

    vec3 ro = vec3(0,1.3, 1.3);
    vec3 rd = R(uv, ro, vec3(0,0,0), .7);

    vec3 c = vec3( 0.9*(cos(iTime/2.)*.5-.5)-.4, 0.8*(cos(iTime/2.)*.5-.5), 0.8*(cos(iTime/2.)*.5 -.5)-.1 );

    vec3 backgrounduv;

    col = vec3(.05, .12, .1);
    float d = RayMarch(ro, rd, backgrounduv, c);
    col *= 3.-d;
    col += pow(clamp(backgrounduv.z, 0., .97), 10.)*vec3(.8, .1, .1);

    //light
    vec3 lp = vec3(1000, 1, 0);
    lp.xz *= Rot(iTime/3.);
    lp.yz *= Rot(iTime/3.);
    vec3 lr = ro+rd*d;
    lr.xz *= Rot(iTime/SPEED);
    lr.yz *= Rot(iTime/SPEED-2.);
    lr = abs(lr);
    vec3 l = normalize(lp - lr);
    vec3 n = GetNormal(lr, c);
    float shadow = clamp(dot(n, l), 0.2, 1.);
    shadow *= 1.;
    col += vec3(0.1); 
    col *= vec3(pow(shadow, 1.));

    fragColor = vec4(col, 0);
}

//2D noise generator & voronoi code -> https://www.youtube.com/watch?v=l-07BXzNdPw
vec2 n22(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a += dot(a, a+34.45);
    return fract(vec2(a.x*a.y,a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    //color
    float m = 0.;

     //noise size 1/size
    float mul = 16.;

    //local grids
    vec2 gv = fract(uv*mul)-0.5;
    //grid ids
    vec2 id = floor(uv*mul);

    float MinDist = 100.;
    vec2 point;

    vec3 col = vec3(0);
    //Voronoi-ing
    for(float y = -2.; y<2.; y++){
        for(float x = -2.; x<2.; x++){
            vec2 of = vec2(x,y);
            //random point generation (blue noise like)
            vec2 p = of+sin(n22(id+of)*iTime)*0.4;
            float d = length(gv-p);
            if(d < MinDist){
               //Cell Distance
               MinDist = d;
               //cell_index;
               point = n22(id+of);
            }
        }
    }

    //out color
     if(smoothstep(0.1,0.08,MinDist)>0.0)
         col = vec3(0);
     else
         col = vec3(1,.15,0)+vec3(point.x,point.y,0)*.2;

    // Output to screen
    fragColor = vec4(col,1.0);
}
