vec2 Coordinate_Normalize(out vec2 tex_coord, vec2 fragment_coordinate, vec2 resolution)
{
    tex_coord = fragment_coordinate / resolution;
    vec2 normalized_coordinate = tex_coord * 2.0 - 1.0;

    return normalized_coordinate * max(vec2(1.0), resolution / resolution.yx);
}

float Edge(float stuff, float thickness)
{
    thickness *= 0.5;
    stuff = abs(stuff);

    return step(stuff, thickness);
}

float Circle(vec2 circle_position, float circle_radius, vec2 current_position)
{
    return length(current_position - circle_position) - circle_radius;
}

float Equilateral_Triangle(vec2 triangle_position, float side_length, vec2 current_position)
{
    float special_length = side_length * (sqrt(3.0) / 6.0);

    vec2 offset = current_position - triangle_position;
    vec2 offset_direction = normalize(offset);
    float offset_length = length(offset);

    vec2 base_direction = vec2(0.0, -1.0);
    float offset_side_degree = degrees(acos(dot(base_direction, offset_direction)));

    float degree_count = floor(offset_side_degree / 60.0);
    offset_side_degree -= degree_count * 60.0;

    degree_count = floor(1.0 - abs(1.0 - degree_count));
    offset_side_degree = abs(degree_count * 60.0 - offset_side_degree);

    float offset_side_cos = cos(radians(offset_side_degree));
    float offset_side_length = offset_length - special_length / offset_side_cos;

    return offset_side_length * offset_side_cos;
}

float Depth_Tester(vec2 light_position, vec2 ray_direction, float ray_length, out float actual_ray_length)
{
    vec2 current_position = light_position;
#ifdef Use_Jitter
    float jitter = texture(iChannel1, iTime * vec2(2.0, 1.0)).x;
#else
    float jitter = 1.0;
#endif

    float origional_step = 0.0025;
    float ray_step = origional_step * jitter;
    actual_ray_length = 0.0;

    float steps = 0.0, fallback_steps = 0.0;
    float steps_max = Depth_Test_Steps;
    float fallback_steps_max = Depth_Test_Fallback_Steps;


    while(steps < steps_max && fallback_steps < fallback_steps_max)
    {
        current_position += ray_direction * ray_step;
        actual_ray_length += ray_step;

        float stuff = 0.0;
        float circle = Circle(circle_position, circle_radius, current_position);
        float equilateral_triangle = Equilateral_Triangle(triangle_position, triangle_side_length, current_position);

        stuff = min(circle, equilateral_triangle);
        if (stuff <= ray_step)
        {
            if (stuff <= origional_step)
            {
                return 0.0;
            }

        #ifdef Depth_Test_Fallback
            fallback_steps++;
            if (fallback_steps >= fallback_steps_max)
            {
                return 0.0;
            }

            ray_step *= 0.5;
            current_position -= ray_direction * ray_step;
            actual_ray_length -= ray_step;
            ray_step *= 0.5;
            continue;
        #endif
        }

        float error = ray_length - actual_ray_length;
        if (error <= ray_step)
        {
            return 1.0;
        }

        ray_step += pow(steps * origional_step, 1.5);
        steps++;
    }

    return 0.0;
}

float Ray_Marching_2D(vec2 light_position, vec2 current_position)
{
    float light_strength = 0.0;


    vec2 origional_ray = current_position - light_position;
    vec2 ray_direction = normalize(origional_ray);
    float ray_length = length(origional_ray);

    float actual_ray_length;
    light_strength = Depth_Tester(light_position, ray_direction, ray_length, actual_ray_length);
#ifdef Decay_Using_Actual_Ray_Length
    light_strength *= pow(0.5, actual_ray_length) * (1.0 - brightness);
#else
    light_strength *= pow(0.5, ray_length) * (1.0 - brightness);
#endif

    return light_strength;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 tex_coord;
    vec2 coord = Coordinate_Normalize(tex_coord, fragCoord, iResolution.xy);

    vec2 temp_coord;
    vec2 mouse_coord = Coordinate_Normalize(temp_coord, iMouse.xy, iResolution.xy);

    vec2 default_light_position = vec2(sin(0.75 * (iTime * 2.0 - 3.14159265358)) * 0.97, cos(iTime * 3.0) * 0.92);
    if (iMouse.z < 0.5)
    {
        mouse_coord = default_light_position;
    }

    vec3 color = texture(iChannel0, tex_coord).rgb * 1.4;
    vec3 edge_color = vec3(1.0, 0.6, 0.1);
    vec3 light_color = vec3(1.0, 0.8, 0.5);

    float stuff = 0.0;
    float point_light_radius = edge_thickness * 0.5;
    float point_light = Circle(mouse_coord, point_light_radius, coord);
    float circle = Circle(circle_position, circle_radius, coord);
    float equilateral_triangle = Equilateral_Triangle(triangle_position, triangle_side_length, coord);

    stuff = step(point_light, 0.0);
    color = mix(color, stuff * vec3(1.0), stuff);

    stuff = step(min(circle, equilateral_triangle), 0.0);
    color = mix(color, stuff * texture(iChannel2, tex_coord).rgb * 1.4, stuff);

    stuff = Edge(min(circle, equilateral_triangle), edge_thickness);
    color = mix(color, stuff * edge_color, stuff);

    color *= Ray_Marching_2D(mouse_coord, coord) * light_color + brightness;
    color = pow(color, vec3(1.375));

#ifdef Enable_iChannel3
    //https://www.shadertoy.com/view/XsfGzn
    vec3 video = texture(iChannel3, tex_coord).rgb;
    float maxrb = max( video.r, video.b );
    float k = clamp( (video.g-maxrb)*5.0, 0.0, 1.0 );
    float dg = video.g; 
    video.g = min( video.g, maxrb*0.8 ); 
    video += dg - video.g;

    if (iMouse.z < 0.5)
    {
        color = mix(video, color, k);
    }
#endif

    // Output to screen
    fragColor = vec4(color, 1.0);
}

