#define N_PARTICLES 60.

vec2 R12(float t) 
{
    float x = fract(sin(t*674.4)*453.2);
    float y = fract(sin((t+x)*741.3)*263.2);
    return vec2(x,y);
}

float R21(vec2 p) 
{
    p = fract(p * vec2(123.34, 456.21));
    p += dot(p, p + 45.32);
    return fract(p.x * p.y);
}

float Explosion(vec2 uv, float ft) 
{
    float sparks = 0.;
    for (float i = 0.; i < N_PARTICLES; i ++)
    {
        vec2 direction = R12(i+1.)-.5;

        float dist = length(uv-direction*ft);
        float brigthness = .0005;

        brigthness *= sin(ft*10.+i)*.5+.5;

        sparks += brigthness/dist;

        sparks *= smoothstep(1., .1, dist);
    }

    return sparks;
}

vec3 FireWork(vec2 uv)
{
    float flt = floor(iTime);
    float frt = fract(iTime);

    uv = uv - flt;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 cid = vec2(0.);
    vec3 col = vec3(0.);

    vec3 color = sin(vec3(frt, uv.x, uv.y))*.56+.78;

    for (float y = -1.; y <= 1.; y ++)
    {
        for(float x = -1.; x <= 1.; x++)
        {
            vec2 offset = vec2(x,y);

            float rf = R21(id + offset);

            float explosion = Explosion(gv-offset-rf, fract(iTime-rf));

            float depth = 3. * rf;

            col += explosion*depth*color;

        }
    }

    col.rgb += color*.03;
    col /= frt;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord- .5*iResolution.xy)/iResolution.y;

    uv *= 2.;

    vec3 col = vec3(0.);

    vec3 fireworks = FireWork(uv);

    col += fireworks;

    fragColor = vec4(col,1.0);
}

//Most foundational code built from the Art of Code tutorials https://www.youtube.com/channel/UCcAlTqd9zID6aNX3TzwxJXg
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .01

vec2 smin(float a,float b, float smoothing){
float slope=clamp(.5+.5*(b-a)/smoothing,0.,1.);
//float ease=slope*(1.-slope); //good boolean intersect
//return slope*a+(1.-slope)*b-ease*.5;
return vec2(mix(b,a,slope) - smoothing*slope*(1.-slope),slope);
}

mat2 rot(float a){
    float s=sin(a);float c=cos(a);
    return mat2(c,-s,s,c);
}


vec3 getRayDir(vec2 uv, vec3 p, vec3 l, float z){
//l.x+=6.*sin(p.z*14.2);
    //z=sin(p.y)*4.; warpy
    vec3 f = normalize(l-p),
r=normalize(cross(vec3(0,1,0),f)),
u=cross(f,r),
c=f*z,
i=c+uv.x*r + uv.y*u,
d=normalize(i);
return d;
}

float sdPill(vec3 p,vec3 a, vec3 b, float r){
    //float v=floor(sin(p.x*4.)*2.)*(1.+cos(iTime));//floor(sin(p.x*2.)*4.)*2;
    vec3 offset=vec3(0,0,0);//+vec3(round(1.*abs(cos(iTime+p.x*2.))),0,0);
    a+=offset;
    b+=offset;
    vec3 ab=b-a;
    vec3 ap=p-a;
    float t = dot(ab,ap) /dot(ab,ab); //length(ab) works too but doestn connect
    t=clamp(t,0.,1.);
    vec3 c = a+ t*ab;
    return length(p-c) -r;
}

float sdSphere(vec3 p,vec3 a, float r){
return length(p-a) - r;
}

float sdTorus(vec3 p,vec2 r){
    float i=length(p.xy)-r.x;
    return length(vec2(i,p.z)) - r.y;
}

float sdBox(vec3 p, vec3 a){
    return length(max(abs(p)-a,0.)) -.2;
}

float sdOctahedron( vec3 p, float s){
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

vec2 getDist(vec3 p){

    vec3 a=vec3(4.,cos(iTime)*.5,2);//vec3(cos((p.y+iTime)*20.)/8.,sin(p.y*3.+iTime*3.)*2.+1.,6); //vec3(0,1,4);//
    vec3 b=vec3(3.,cos(iTime*2.),-4.);
    vec3 c=vec3(-3,0,2);

    float sphere=sdSphere(p,a,1.)*.5;//sdPill(p,a,b,1.);
    vec3 turn=(p+b);
    mat2 rotVal= rot(iTime);
    turn.xz*=rotVal;
    float box=sdBox(turn,vec3(1,1,1));
    turn=(p+c);
    turn.xy*=rotVal;
    float octa=sdOctahedron(turn,1.);
    float bulbs=(smoothstep(.03,0.0,cos(sin(2.*(p.z+iTime*2.))+p.x*4.)*.02)*1.);
    float planeDist=p.y+.92;
    //float d=smin(sphereD,torusD,.5);

    float blend=clamp(0.,sin(iTime),1.);
    vec2 mix1=smin(sphere,octa,blend);
    vec2 mix2=smin(box,mix1.x,blend);
    vec2 mix3=smin(planeDist,mix2.x,blend);

    vec2 mix4=smin(bulbs,mix3.x,.5*blend+.5);
    //float d=d2.x;
    //float last=mix4.y>.5?4.:(mix3.y>.5?3.:(mix2.y>.5?2.:(mix1.y>.5?1.:0.)));
    float last=mix4.y>(.2+sin(iTime*4.)*0.1)?4.:
    (mix3.y>.9?2.+(mix3.y):
    (mix2.y>.9?1.+(mix2.y):
    (mix1.y>.001?1.:0.) ));
    return vec2(mix4.x,last); //mix1.y+mix2.y+mix3.y+mix4.y
}

vec4 rayMarch(vec3 ro, vec3 rd){
    int i=0;
    float dO=0.;
    float glowDist = 1e9;
    for(i=0;i<MAX_STEPS;i++){
        vec3 p = ro +rd*dO;
        float dS =getDist(p).x;
        glowDist = min(glowDist, dS);
        dO+=dS;
        if(dO>MAX_DIST || dS<SURF_DIST)break;
    }
    /*if (getDist(ro +rd*dO).x < SURF_DIST){
        float col = (1.0 - float(i)/float(MAX_STEPS));
        return vec4(col, 0.0, 0.0,dO);
    }else{
        float v=max((0.05 - glowDist)*20.0, 0.0);
        return vec4(v,v,v,dO);
    }*/

    return vec4(1,1,1,dO);
}

vec4 getNormal(vec3 p){
    vec2 distOut=getDist(p);
    float dist=distOut.x;
    vec2 e=vec2(0.01,0);
    vec3 n = dist - vec3(getDist(p-e.xyy).x,getDist(p-e.yxy).x,getDist(p-e.yyx).x);
    return vec4(normalize(n),distOut.y);
}

//https://gist.github.com/patriciogonzalezvivo/20263fe85d52705e4530
vec3 getTriPlanarBlend(vec3 _wNorm){
	// in wNorm is the world-space normal of the fragment
	vec3 blending = abs( _wNorm );
	blending = normalize(max(blending, 0.00001)); // Force weights to sum to 1.0
	float b = (blending.x + blending.y + blending.z);
	blending /= vec3(b, b, b);
	return blending;
}

vec3 getLight(vec3 p){
    vec3 lightPos=vec3(cos(-iTime*2.)*3.,5,sin(iTime)); // 0 5 6 
    vec3 l =normalize(lightPos-p);
    vec4 nOut = getNormal(p);
    vec3 n=nOut.rgb;
    float diff=clamp(dot(l,n),0.,1.);
    vec4 distOut=rayMarch(p+n*SURF_DIST*2.,l);
    float dist=distOut.w;
    if(dist<length(lightPos -p)) diff*=0.6;

    float normalRepeat=.2;
    float normalScale=2.; 
    vec3 blending = getTriPlanarBlend(n);
	vec3 xaxis = texture( iChannel1, p.yz * normalRepeat).rgb;
	vec3 yaxis = texture( iChannel1, p.xz * normalRepeat).rgb;
	vec3 zaxis = texture( iChannel1, p.xy * normalRepeat).rgb;
	vec3 normalTex = xaxis * blending.x + yaxis * blending.y + zaxis * blending.z;

    normalTex=mix(vec3(.6,.8,.8),normalTex,.2); //vec3(.6,.8,.8)

    //vec4 t=texture(iChannel1,n.xy);
    //vec3(.99,.6,4.)
    float r=sin(p.x*1.5)*.5 +1.;
    float b=1.-r;

    float val=mod(nOut.w,1.);
    vec3 c=vec3(.4,.3,.9); //octa
    if(nOut.w==1.){ //sphere
        c=vec3(0,1.,0);
    }else if(nOut.w==2.){ //cube
        c=vec3(.9,.1,.2);
    }else if(nOut.w==3.){ //plane
        c=vec3(.8,.7,1);
    }else if(nOut.w==4.){ //wiggle
        c=vec3(r,0,b); 
    }

    vec3 colOut=mix(normalTex,c,.7);
    return mix(vec3(.2,.2,.2),colOut,diff);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    //camera
    vec3 ro=vec3(-1.+cos(iTime)*.5,6,sin(iTime)*.5-3.);

    //vec3 rd=normalize(vec3(uv.x,uv.y-.4,1.));
    vec3 rd=getRayDir(uv,ro,vec3(0,1,0),1.1);

    float dist=rayMarch(ro,rd).w;

    vec3 point = ro +rd*dist;
    vec3 diffuse= getLight(point);

    // Output to screen
    fragColor = vec4(diffuse,1.0);
}

float n = 3.0;
const float PI = acos(-1.0);

// function===============================================================
vec2 rot(vec2 pos, float r)
{
    mat2 m = mat2(cos(r), sin(r), -sin(r), cos(r));
    return m * pos + 0.5;
}

// primitive==============================================================
float box(vec2 pos, vec2 size)
{
    vec2 leftbottom = vec2(0.5) - size;
    vec2 uv = step(leftbottom, pos);
    uv *= step(leftbottom, 1.0 - pos);
    return uv.x* uv.y;
}

float makeThree(vec2 pos, float r)
{
    float d = 1e10;
    float d1 = 0.0;
    float d2 = 0.0;
    float d3 = 0.0;

    vec2 rotate = rot(pos, r);
    pos.xy = rotate;
    d1 = box(vec2(pos.x + 0.3, pos.y), vec2(0.1, 0.55)) * (1.0 - box(vec2(pos.x + 0.3, pos.y), vec2(0.07, 0.47)));
    d2 = box(vec2(pos.x, pos.y), vec2(0.1, 0.5)) * (1.0 - box(vec2(pos.x, pos.y), vec2(0.07, 0.47)));
    d3 = box(vec2(pos.x - 0.3, pos.y), vec2(0.1, 0.55)) * (1.0 - box(vec2(pos.x - 0.3, pos.y), vec2(0.07, 0.47)));

    d = d1 + d2 + d3;
    return d;
}

float wave(vec2 pos)
{
    float d = 0.0;
    pos = vec2(pos.x + (iResolution.x)/2.0, pos.y - (iResolution.y)/2.0);
    d = length((floor(pos * n) + 0.5) / n);
    return 1.0 - (sin(d / 0.8 + iTime*1.5));
}

// map====================================================================
float map(vec2 pos, float r)
{
    return makeThree(pos, r);
}

float map2(vec2 pos)
{
    return wave(pos);
}

//main====================================================================
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float mask = float(map2(pos));

    pos += 0.5;

    vec2 mulPos = pos * n;
    pos = fract(pos * n);

    float switchNum = mod(floor(mulPos.x) + floor(mulPos.y), 2.0) + 1.0;

    float three = float(map(pos - 0.5, PI / switchNum));
    vec3 mainCol = vec3(1.0, 1.0, 0.22);


    vec3 endCol = vec3(mask * three * mainCol);

    fragColor = vec4(endCol,1.0);
}

#define PI 3.1415926535897932384626433832795

struct Triangle {
    vec2 v1;
    vec2 v2;
    vec2 v3;
};

#define S_RECT 0
#define S_ELLIPSE 1
#define S_TRIANGLE 3

struct Shape {
    int type;
    vec2 pos;
    float angle;
    vec2 size;
    vec3 color;
};

vec4 drawShape(Shape shape, vec2 uv) {
    uv -= shape.pos;
    float c = cos(-shape.angle);
    float s = sin(-shape.angle);
    mat2 m = mat2(c, s, -s, c);
    uv = m * uv;
    uv /= shape.size / 2.0;
    if (shape.type == S_RECT) {
        if (abs(uv.x) <= 1.0 && abs(uv.y) <= 1.0) {
            return vec4(shape.color, 1.0);
        }
    } else if (shape.type == S_ELLIPSE) {
        if (dot(uv, uv) <= 1.0) {
            return vec4(shape.color, 1.0);
        }
    } else {
        if ((uv.y <= 1.0 + 2.0 * uv.x) && (uv.y <= 1.0 - 2.0 * uv.x) && (uv.y >= -1.0)) {
            return vec4(shape.color, 1.0);
        }
    }
    return vec4(0);
}

float rand(int a, int b) {
    float x = float(a * 100 + b);
    return fract(abs(sin(15123.9847 * x) * 9234.3521));
}


vec3 baseImage(vec2 uv) {
    for (int i = 0; i < 100; ++i) {
        vec2 A = vec2( rand(i,1)*2.0, rand(i,2)*2.0 );
        vec2 k = vec2( rand(i,3)*0.3, rand(i,4)*0.3 );
        vec2 pos = vec2( rand(i,5), rand(i,6) ) + A*sin(k*iTime);
        vec2 size = vec2( rand(i,7)*0.2+0.1, rand(i,8)*0.2+0.1);
        float angle = rand(i,9)*2.0*PI + (rand(i,10)*2.0-1.0)*PI*iTime;
        vec3 color = vec3( rand(i,11)*0.9, rand(i,12)*0.9, rand(i,13)*0.9);
        float r = rand(i,14);
        int type;
        if (r < 0.4) {
            type = S_RECT;
        } else if (r < 0.6) {
            type = S_ELLIPSE;
        } else {
            type = S_TRIANGLE;
        }
        Shape s = Shape(type, pos, angle, size, color);
        vec4 col = drawShape(s, uv);
        if (col.a > 0.0) {
            return col.rgb;
        }
    }

    return vec3(0);
}


