// updated version of the shader I 
// made during the shader jam https://livecode.demozoo.org/
// by bitnenfer
float hash(float n) { return fract(sin(n) * 753.5); }

struct Hit
{
    float dist;
    int id;
};

float fft = .0;
//iq :D
float torus(vec3 p, vec2 t)
{
    vec2 q = vec2(length(p.xz)-t.x, p.y);
    return length(q) - t.y;
}

float smin(float a, float b, float k)
{
    float h = clamp(0.5 + 0.5 * (b-a)/k, 0.0, 1.0);
    return mix(b, a, h) - k * h * ( 1. - h);
}

mat2 rot(float r)
{
    float c = cos(r), s = sin(r);
    return mat2(c, s, -s , c);
}

Hit opUnion(Hit a, Hit b)
{
    if (a.dist < b.dist) return a;
    return b;
}

Hit opaque(vec3 p)
{
    float t = iTime;
    float blob = length(p) - 1. + cos(fft * 100.0 + sin(p.z * 10.0 + fft) + sin(p.x * 10.0 + fft * fft) + sin(p.y  * 12.0 + sin(iTime * 10.0 + fft))) * 0.05;
    for (float i = 0.0; i < 3.0; i += 0.5)
    {
        blob = smin(blob, length(p + sin(p.x + p.y + p.z) * vec3(cos(t + i * 10.0), sin(t + i * 10.0), -cos(t - i * 10.0))) - 0.3, 0.5);
    }
    Hit blobHit = Hit(blob, 0);
    return blobHit;
}

Hit trans(vec3 p)
{
    vec3 p0 = p;
    vec3 p1 = p;
    p0.yz *= rot(iTime);
    p1.xy *= rot(iTime);
    float t0 = torus(p0, vec2(1.6, 0.1));  
    float t1 = torus(p1, vec2(1.8, 0.1));  
    Hit ringHit = Hit(abs(min(t0, t1)) - 0.01, 1);
    return ringHit;
}

Hit map(vec3 p) 
{
    return opUnion(trans(p), opaque(p));
}

vec3 norm(vec3 p)
{
    vec2 e = vec2(0, 0.001);
    return normalize(vec3(
        map(p + e.yxx).dist - map(p - e.yxx).dist,
        map(p + e.xyx).dist - map(p - e.xyx).dist,
        map(p + e.xxy).dist - map(p - e.xxy).dist
    ));
}

vec3 normOpaque(vec3 p)
{
    vec2 e = vec2(0, 0.001);
    return normalize(vec3(
        opaque(p + e.yxx).dist - opaque(p - e.yxx).dist,
        opaque(p + e.xyx).dist - opaque(p - e.xyx).dist,
        opaque(p + e.xxy).dist - opaque(p - e.xxy).dist
    ));
}

bool trace(vec3 ro, vec3 rd, out Hit hit)
{
    float t = 0.;
    for (int i = 0; i < 100; ++i)
    {
        Hit h = map(ro + rd * t);
        if (h.dist < 0.001) 
        {
            hit.dist = t;
            hit.id = h.id;
            return true;
        }
        t+=h.dist;
        if (t > 100.) break;
    }
    hit = Hit(t, -1);
    return false;
}

bool traceOpaque(vec3 ro, vec3 rd, out Hit hit)
{
    float t = 0.;
    for (int i = 0; i < 100; ++i)
    {
        Hit h = opaque(ro + rd * t);
        if (h.dist < 0.001) 
        {
            hit.dist = t;
            hit.id = h.id;
            return true;
        }
        t+=h.dist;
        if (t > 100.) break;
    }
    hit = Hit(t, -1);
    return false;
}

vec3 render(vec2 uv)
{
    fft = texture(iChannel0, vec2(0.0)).r * 0.15;
    vec3 ro = vec3(0, 0, -4. + sin(iTime) * 0.5);
    vec3 rd = normalize(vec3(uv, 1));
    vec3 color = vec3(0.05);
    ro.xz *= rot(iTime);
    rd.xz *= rot(iTime);

    Hit sceneHit;
    if (trace(ro, rd, sceneHit))
    {
        vec3 p = ro + rd * sceneHit.dist;
        vec3 matColor = vec3(0);
        if (sceneHit.id == 0)
        {
            matColor = vec3(1, 205.0/255.0,178.0/255.0);
        }
        else if (sceneHit.id == 1)
        {
            matColor = vec3(1);
        }
        vec3 v = normalize(p - ro);
        vec3 n = norm(p);
        vec3 l = vec3(0.5, 0.2, 0.3);
        vec3 l2 = -l;
        l.xz *= rot(iTime);
        l2.xz *= rot(iTime);
        vec3 h = normalize(l2 + v);
        float ndl = max(dot(n, l), 0.0);
        float sp = pow(max(0.0, dot(n, h)), 1.0);

        color = matColor * 0.02 + matColor * ndl + sp * vec3(0.0, 0.5, 0.9);

        if (sceneHit.id == 0)
        {
            {
                vec3 rd2 = l;
                vec3 ro2 = p + rd2 * 0.1;
                for (int j = 0; j < 62; ++j)
                {
                    float d = map(ro2 + rd2 * 0.35).dist * 0.8;
                    if (d < 0.0) break;
                    color += (matColor + vec3(0.8, 0.2, 0.1)) * (d * 0.1) * 0.25;
                }
            }

            {
                vec3 rd2 = l2;
                vec3 ro2 = p + rd2 * 0.1;
                for (int j = 0; j < 16; ++j)
                {
                    float d = map(ro2 + rd2 * 0.15).dist * 0.8;
                    if (d < 0.0) break;
                    color += (matColor + vec3(1.0, 0.5, 0.9)) * (d * 0.1) * 0.15;
                }
            }
        }
        else
        {
            color = vec3(0.0);
            color += pow(max(0.0, dot(n, h)), 32.) * vec3(1);

            {
                vec3 col = vec3(0);
                vec3 rd3 = refract(rd, n+ 0.1*(hash(n.x * n.y * n.z) * 2.0 - 1.0), 1./1.1);
                vec3 ro3 = p + rd3 * 0.2;
                Hit refHit;
                if (traceOpaque(ro3, rd3, refHit)) 
                {
                    vec3 p1 = ro3 + rd3 * refHit.dist;
                    vec3 matColor1 = vec3(0);
                    matColor1 = vec3(1, 205.0/255.0,178.0/255.0);
                    vec3 v1 = normalize(p1 - ro3);
                    vec3 n1 = norm(p1);
                    vec3 h1 = normalize(l2 + v1);
                    float ndl1 = max(dot(n1, l), 0.0);
                    float sp1 = pow(max(0.0, dot(n1, h1)), 1.0);
                    col += matColor1 * 0.02 + matColor1 * ndl1 + sp1 * vec3(0.0, 0.5, 0.9);
                }
                else
                    col = vec3(0.07);

                color.r += col.r;
            }

            {
                vec3 col = vec3(0);
                vec3 rd3 = refract(rd, n+ 0.1*(hash(n.x * n.y * n.z) * 2.0 - 1.0), 1./1.2);
                vec3 ro3 = p + rd3 * 0.2;
                Hit refHit;
                if (traceOpaque(ro3, rd3, refHit)) 
                {
                    vec3 p1 = ro3 + rd3 * refHit.dist;
                    vec3 matColor1 = vec3(0);
                    matColor1 = vec3(1, 205.0/255.0,178.0/255.0);
                    vec3 v1 = normalize(p1 - ro3);
                    vec3 n1 = norm(p1);
                    vec3 h1 = normalize(l2 + v1);
                    float ndl1 = max(dot(n1, l), 0.0);
                    float sp1 = pow(max(0.0, dot(n1, h1)), 1.0);
                    col += matColor1 * 0.02 + matColor1 * ndl1 + sp1 * vec3(0.0, 0.5, 0.9);
                }
                else
                    col = vec3(0.07);

                color.g += col.g;
            }

            {
                vec3 col = vec3(0);
                vec3 rd3 = refract(rd, n+ 0.1*(hash(n.x * n.y * n.z) * 2.0 - 1.0), 1./1.3);
                vec3 ro3 = p + rd3 * 0.2;
                Hit refHit;
                if (traceOpaque(ro3, rd3, refHit)) 
                {
                    vec3 p1 = ro3 + rd3 * refHit.dist;
                    vec3 matColor1 = vec3(0);
                    matColor1 = vec3(1, 205.0/255.0,178.0/255.0);
                    vec3 v1 = normalize(p1 - ro3);
                    vec3 n1 = norm(p1);
                    vec3 h1 = normalize(l2 + v1);
                    float ndl1 = max(dot(n1, l), 0.0);
                    float sp1 = pow(max(0.0, dot(n1, h1)), 1.0);
                    col += matColor1 * 0.02 + matColor1 * ndl1 + sp1 * vec3(0.0, 0.5, 0.9);
                }
                else
                    col = vec3(0.07);

                color.b += col.b;
            }
        }
    }

    return color;
}

