float getDigit(float index){
    return digitsPi[int(abs(mod(index, float(digitsPi.length()))))];
}
float getDigit(int index){
    return getDigit(float(index));
}
float getRandom(vec2 index){
    float _seed = mod(float(seed), 1000.);
    index = mod(index, 65535.);
    float id = length(index) * _seed + length(index) + _seed;
    return fract(sin(dot(index, vec2(getDigit(id+265.), getDigit(id+159.))))* getDigit(id + 314.));
}
float getRandom(vec3 index){
    float _seed = mod(float(seed), 1000.);
    index = mod(index, 65535.);
    float id = length(index) * _seed + length(index) + _seed;
    return fract(sin(dot(index, vec3(getDigit(id+265.), getDigit(id+159.), getDigit(id+582.))))* getDigit(id + 314.));
}
float smoothMix(vec2 from, vec2 to, vec2 t){
    t = smoothstep(0.,1.,t);
    return mix(
        mix(getRandom(from), getRandom(vec2(to.x, from.y)), t.x),
        mix(getRandom(vec2(from.x, to.y)), getRandom(to), t.x),
    t.y);
}
float smoothMix(vec3 from, vec3 to, vec3 t){
    t = smoothstep(0.,1.,t);
    return mix(
        mix(
            mix(getRandom(from), getRandom(vec3(to.x, from.yz)), t.x),
            mix(getRandom(vec3(from.x, to.y, from.z)), getRandom(vec3(to.xy, from.z)), t.x),
        t.y),

        mix(
            mix(getRandom(vec3(from.xy, to.z)), getRandom(vec3(to.x, from.y, to.z)), t.x),
            mix(getRandom(vec3(from.x, to.yz)), getRandom(to), t.x),
        t.y),

    t.z);
}
float getNoise(vec2 index){
    vec2 low = floor(index);
    float _output = 0.;
    float h = 0.;

    for(float i = 1.; i <= float(detail); i ++){
        vec2 low = floor(index);
        _output += smoothMix(low, low + vec2(1,1), fract(index)) * (float(detail)/i);
        h += (float(detail)/i);
        index += mod(float(seed), 50.) + i;
        index *= 2.;
    }
    return _output / h;
}
float getNoise(vec3 index){
    float _output = 0.;
    float h = 0.;

    for(float i = 1.; i <= float(detail); i ++){
        vec3 low = floor(index);
        _output += smoothMix(low, low + vec3(1,1,1), fract(index)) * (float(detail)/i);
        h += (float(detail)/i);
        index += mod(float(seed), 50.) + i;
        index *= 2.;
    }
    return _output / h;
}

vec3 getColor(float height){
    for(int i = 0; i < heights.length(); i++){
        if(height <= heights[i])
            return mix(colors[heightColorRelation[i-1]], colors[heightColorRelation[i]], (height - heights[i - 1]) / (heights[i] - heights[i -1]));
    }
    return vec3(colors[heightColorRelation[heights.length()-1]]);
}
