{
    // y in [-3,3], x somewhere like [-5.3, 5.3]
    vec2 uv = (fragCoord - iResolution.xy/2.) / iResolution.y * 3.;

    // Output to screen
    fragColor = vec4(color(julia(uv)), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = vec4(.0);

	vec2 res = iResolution.xy;


	//------ create polar coordinates -----

	vec2 nUV = (uv - .5) * res / res.y * 1.3;

	vec2 V = normalize(nUV);

	vec2 refV = vec2(1., 0.);

	float cosA = dot(refV, V);

	float alpha = acos(cosA);

	alpha = alpha * step(.0, nUV.y) + (radians(360.) - alpha) * step(nUV.y, .0); // full 2PI circle

	//--------------------------------------

	float alphaP = alpha + length(nUV) * 16. / exp(length(nUV));

	float edge = cos(alphaP * 5. + iTime * 3.);

	edge += 1.;

	edge /= 2.;

	edge *= length(nUV) * exp(length(nUV) / 3.);

	vec3 rainbow;

	rainbow.r = (sin(alphaP - edge * 20. - iTime * 10. + radians(360.) * 0. / 360.) + 1.) / 2.;
	rainbow.g = (sin(alphaP - edge * 20. - iTime * 10. + radians(360.) * 120. / 360.) + 1.) / 2.;
	rainbow.b = (sin(alphaP - edge * 20. - iTime * 10. + radians(360.) *240. / 360.) + 1.) / 2.;



	color.rgb = rainbow * edge;

	color.a = 1.;

    // Output to screen
    fragColor = color;
}

#define s(a,b,t) smoothstep(a,b,t)

float N(float t){
    return fract(sin(t*458.25)*2564.36);
}

float N21(vec2 p){
    p = fract(p*vec2(123.34,345.45));
    p += dot(p, p+34.3456);
    return fract(p.x*p.y);
}

vec4 N14(float t){
    return fract(sin(t*vec4(58.36, 623.25, 7198.23, 9486.64))*vec4(5689.74,2564.36,528.49,89.75));
}

struct ray{
    vec3 o, d;
};

ray getRay(vec2 uv, vec3 camPos, vec3 lookat, float zoom){
    ray a;
    a.o = camPos;
    vec3 wup = vec3(0,1,0);

    vec3 f = normalize(lookat-camPos);
    vec3 r = normalize(cross(wup,f));
    vec3 u = cross(f, r);

    vec3 c = a.o + f*zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    a.d = normalize(i-a.o);

    return a;
}

vec3 closestPoint (ray r, vec3 p){
    return r.o + max(0., dot(p-r.o,r.d))*r.d;
}

float distRay(ray r, vec3 p){
   return length(p-closestPoint(r,p));
}

float Bokeh (ray r, vec3 p, float size, float blur){

    float d = distRay(r, p);
    size *= length(p);
    float c = s(size,size*(1.-blur),d);
    c *= mix(0.6, 1., s(size*.8,size,d));
    return c;
}

vec3 streetLights(ray r, float t){
    float side = step(r.d.x,0.);
    r.d.x = abs(r.d.x);

    float s = 1./10.;
    float m = 0.;
    for(float i=0.; i<1.; i+=s){
        float ti = fract(t+i+side*s*.5);
        vec3 p = vec3(2,2,100.-ti*100.);
        m += Bokeh(r,p,0.05,0.1)*ti*ti*ti;    
    }
    vec3 col = vec3(1,0.7,.3)*m;
    return col;
}

vec3 headLights(ray r, float t){
    t *= 2.;
    float w1 = 0.25;
    float w2 = w1*1.2;
    float s = 1./30.;
    float m = 0.;

    for(float i=0.; i<1.; i+=s){
        float n = N(i);

        if(n>0.25) continue; // limiting no. of headlights
        float ti = fract(t+i);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = s(0.9,1.,ti);
        float size = mix(0.05,0.03,focus);

        //headlights 1
        m += Bokeh(r,vec3(-1.-w1,0.15,z),size,0.1)*fade; 
        m += Bokeh(r,vec3(-1.+w1,0.15,z),size,0.1)*fade; 

        //headlights 2
        m += Bokeh(r,vec3(-1.-w2,0.15,z),size,0.1)*fade; 
        m += Bokeh(r,vec3(-1.+w2,0.15,z),size,0.1)*fade;

        //reflections
        float ref = 0.;
        ref += Bokeh(r,vec3(-1.-w2,-0.15,z),size*3.,1.)*focus; 
        ref += Bokeh(r,vec3(-1.+w2,-0.15,z),size*3.,1.)*focus;

        m += ref*focus;
    }
    vec3 col = vec3(.9,.9,1.)*m;
    return col;
}

vec3 tailLights(ray r, float t){

    t *= 0.25;
    float w1 = 0.25;
    float w2 = w1*1.2;
    float s = 1./30.;
    float m = 0.;

    for(float i=0.; i<1.; i+=s){
        float n = N(i);

        if(n>.5) continue; // limiting no. of headlights

        float lane = step(.25,n);
        float ti = fract(t+i);
        float z = 100.-ti*100.;
        float fade = ti*ti*ti*ti*ti;
        float focus = s(0.9,1.,ti);
        float size = mix(0.05,0.03,focus);
        float laneShift = s(1.,0.96,ti);

        //headlights 1
        float blinker = step(0.96,ti);
        float x = 1.5 - lane * laneShift;
        float blink = step(0.,sin(t*1000.))*lane*blinker*7.;
        m += Bokeh(r,vec3(x-w1,0.15,z),size,0.1)*fade; 
        m += Bokeh(r,vec3(x+w1,0.15,z),size,0.1)*fade; 

        //headlights 2
        m += Bokeh(r,vec3(x-w2,0.15,z),size,0.1)*fade; 
        m += Bokeh(r,vec3(x+w2,0.15,z),size,0.1)*fade*(1.+blink);

        //reflections
        float ref = 0.;
        ref += Bokeh(r,vec3(x-w2,-0.15,z),size*3.,1.)*focus; 
        ref += Bokeh(r,vec3(x+w2,-0.15,z),size*3.,1.)*focus*(1.+blink*0.1);

        m += ref*focus;
    }
    vec3 col = vec3(1.,.1,.03)*m;
    return col;
}

vec3 envLights(ray r, float t){
    float side = step(r.d.x,0.);
    r.d.x = abs(r.d.x);

    float s = 1./15.;
    vec3 c = vec3(0.);
    for(float i=0.; i<1.; i+=s){
        float ti = fract(t+i+side*s*.5);
        float fade = ti*ti*ti;
        vec4 n = N14(i+side*100.);
        float occlusion = sin(ti*6.283*10.*n.x)*0.5+0.5;
        fade = occlusion;
        float x = mix(2.5,10.,n.x);
        float y = mix(0.1,1.5,n.y);
        vec3 p = vec3(x,y,50.-ti*50.);

        vec3 col = n.wzy;
        c += Bokeh(r,p,0.05,0.1)*fade*col*0.5;    
    }

    return c;
}

vec2 Rain(vec2 uv, float t){

    t *= 40.;
    uv *= 4.;

    vec2 a = vec2(3.,1.);
    vec2 st = uv*a;

    vec2 id = floor(st);
    st.y += t*0.22;

    st.y += N(id.x);
    uv.y += N(id.x);
    id = floor(st);
    st = fract(st)-0.5;




    float n = N21(id);
    t += n*6.2831; 
    float w = uv.y*10.;
    float x = (n - 0.45)*0.6;
    x += (0.45*0.6-abs(x))*sin(3.*w)*pow(abs(sin(w)),6.)*0.45;
    float y = -sin(t+sin(t+sin(t)*0.5))*0.40;
    //y -= (st.x-x)*(st.x-x);

    vec2 p1 = vec2(x,y);
    vec2 dropPos = (st-p1)/a;
    float drop = s(0.07,0.05,length(dropPos));

    vec2 p2 = vec2(x,t*0.25);
    vec2 trailPos = (st-p2)/a;
    trailPos.y = (fract(trailPos.y*8.)-0.5)/8.;
    float trail = s(0.06,0.0,length(trailPos));
    float fogTrail = s(-0.05,.05,dropPos.y);
    fogTrail *= s(0.5,y,st.y);


    fogTrail *= s(0.05,0.04,abs(dropPos.x));
    trail += fogTrail;
    //if (st.x>0.46 || st.y > 0.49) drop = 1.;

    return vec2(drop*dropPos*30.+trail*trailPos*10.+fogTrail*dropPos*0.2*5.);


}

vec2 Rain2(vec2 uv, float t){

    t *= 40.;
    uv *= 6.;

    vec2 a = vec2(2.,1.);
    vec2 st = uv*a;

    vec2 id = floor(st);
    st.y += t*0.22;

    st.y += N(id.x);
    uv.y += N(id.x);
    id = floor(st);
    st = fract(st-0.5)-0.5;




    float n = N21(id);
    t += n*6.2831; 
    float w = uv.y*10.;
    float x = (n - 0.45)*0.6;
    x += (0.45*0.6-abs(x))*sin(3.*w)*pow(abs(sin(w)),6.)*0.45;
    float y = -sin(t+sin(t+sin(t)*0.5))*0.40;
    y -= (st.x-x)*(st.x-x);

    vec2 p1 = vec2(x,y);
    vec2 dropPos = (st-p1)/a;
    float drop = s(0.07,0.05,length(dropPos));

    vec2 p2 = vec2(x,t*0.25);
    vec2 trailPos = (st-p2)/a;
    trailPos.y = (fract(trailPos.y*8.)-0.5)/8.;
    float trail = s(0.06,0.0,length(trailPos));
    float fogTrail = s(-0.05,.05,dropPos.y);
    fogTrail *= s(0.5,y,st.y);


    fogTrail *= s(0.05,0.04,abs(dropPos.x));
    trail += fogTrail;
    //if (st.x>0.46 || st.y > 0.49) drop = 1.;

    return vec2(drop*dropPos*30.+trail*trailPos*10.+fogTrail*dropPos*0.2*5.);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//*******************************************
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
//*******************************************
    float t = iTime*.05;//+m.x;
    float xoffs = (sin(t)*.5-0.5)*0.1;
    vec3 camPos = vec3(.5+xoffs,.2,0.);
    vec3 lookat = vec3(.5,.2,1);



    vec2 rainDistort = Rain(uv, t);
    rainDistort += Rain2(uv,t);

    ray r= getRay(uv-rainDistort,camPos,lookat,2.);



    vec3 col = streetLights(r,t);
    col += headLights(r,t);
    col += tailLights(r,t);
