float hash( float n )
{
    return fract(sin(n)*43758.5453);
}


// credit: iq/rgba
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + 113.0*p.z;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}


float fbm(in vec3 x)
{
	float i = 1.0;

	float n = noise(x * i) / i; i *= 2.0;
	n+= noise(x * i) / i; i *= 2.0;
	n+= noise(x * i) / i; i *= 2.0;
	n+= noise(x * i) / i; i *= 2.0;
	n+= noise(x * i) / i; i *= 2.0;

	return n;
}


float pointRayDistance(vec3 ro, vec3 rd, vec3 p)
{
	vec3 u = p-ro;
	float cosvu = dot(rd,u);
	if (cosvu<0.0) return length(p-ro);
	vec3 puv = rd * (cosvu / length(rd));
	vec3 qq = ro + puv;
	return length(p-qq);
}

// intersection through participating media (like atmospheres)
struct iMediaIntersection
{
    float tnear;   // first intersection
    float tfar;    // last intersection
    uint mnear;    // material first hit (atmosphere?)
    uint mfar;     // material on second hit (atmosphere or ground)
    vec3 nor;      // normal at solid intersection (material dependent)
};


// intersect ray ro+rd with sphere sph, with a ground height sphgnd
iMediaIntersection mSphere(in vec3 ro, in vec3 rd, in vec4 sph, in float sphgnd){

    iMediaIntersection res;
    res.tnear = -1.0;
    res.tfar = -1.0;
    res.mnear = 0u;
    res.mfar = 0u;
    res.nor = vec3(0.0,0.0,0.0);

    // outer sphere intersection
	float r = sph.w; //radius
	vec3 oc = ro - sph.xyz; //origin = position sphere
	float b = 2.0*dot(oc,rd);
	float c = dot(oc,oc)-r*r;
	float h = b*b - 4.0*c;

	if (h >= 0.0)
    {
        float hsqrt = sqrt(h);
        res.tnear = (-b - hsqrt) / 2.0;
        res.mnear = 1u;  // atmospheric hit

        res.tfar = (-b + hsqrt)/2.0;
        res.mfar = 1u;  // atmospheric exit

        // inner sphere intersection
        r = sphgnd;
        c = dot(oc,oc)-r*r;
        h = b*b - 4.0*c;

        if (h >= 0.0){
            // ground hit
            res.tfar = (-b - sqrt(h)) / 2.0;
            res.mfar = 2u;  // ground hit
            res.nor = normalize(ro + rd * res.tfar - sph.xyz);  // ground normal
        }
    }

    return res;
}



float iSphere (in vec3 ro, in vec3 rd, in vec4 sph){
	//a sphere centered at the origin has eq: |xyz| = r
	//meaning, |xyz|^2 = r^2, meaning <xyz,xyz> = r^2
	// now, xyz = ro + t*rd, therefore |ro|^2 + t^2 + 2<ro,rd>t - r^2 = 0
	// which is a quadratic equation. so
	float r = sph.w; //radius
	vec3 oc = ro - sph.xyz; //origin = position sphere
	float b = 2.0*dot(oc,rd);
	float c = dot(oc,oc)-r*r;
	float h = b*b - 4.0*c;
	if (h <0.0) return -1.0;
	float t = (-b - sqrt(h))/2.0;
	return t;
}

float iSphere2 (in vec3 ro, in vec3 rd, in vec4 sph){
	//a sphere centered at the origin has eq: |xyz| = r
	//meaning, |xyz|^2 = r^2, meaning <xyz,xyz> = r^2
	// now, xyz = ro + t*rd, therefore |ro|^2 + t^2 + 2<ro,rd>t - r^2 = 0
	// which is a quadratic equation. so
	float r = sph.w; //radius
	vec3 oc = ro - sph.xyz; //origin = position sphere
	float b = 2.0*dot(oc,rd);
	float c = dot(oc,oc)-r*r;
	float h = b*b - 4.0*c;
	if (h <0.0) return -1.0;
	float t = (-b + sqrt(h))/2.0;
	return t;
}

vec3 nSphere (in vec3 pos, in vec4 sph){
	return (pos - sph.xyz) / sph.w;
}

float iPlane(in vec3 ro, in vec3 rd){
	//eq. of a plane, y=0 = ro.y + t*rd.y
	return -ro.y/rd.y;
}

vec3 nPlane( in vec3 pos){
	return vec3(0.0,1.0,0.0);
}



