// https://iquilezles.org/www/articles/smin/smin.htm
float smin(in float a, in float b, in float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

// Random cycling points
vec2 getPoint(in vec2 cell) {
    float freq = Hash11(dot(cell, vec2(393.84, 673.48))) * 6.0 - 3.0;
    float phase = Hash11(dot(cell, vec2(348.46, 183.37)));
    float amp = Hash11(dot(cell, vec2(275.35, 741.69)));
    float t = iTime * freq + phase;
    return cell + 0.5 + 0.5 * vec2(cos(t), sin(t)) * amp;
}

float voronoi(in vec2 p, in float smoothness) {
    vec2 cell = floor(p);
    float minDist = 1e20;
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            vec2 curCell = cell + vec2(i, j);
            vec2 curPoint = getPoint(curCell);
            minDist = smin(minDist, length(p - curPoint), smoothness);
        }
    }

    return sin(minDist * 40.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragColor = vec4(smoothstep(0.5, 0.0, voronoi(uv * 4.0, 0.2) - 0.5));
}

