#define sat(a) clamp(a,0.,1.)
mat2 r2d(float a){float ca = cos(a),sa=sin(a);return mat2(ca,-sa,sa,ca);}
#define PI 3.14159265

float _bbox(vec3 p, vec3 s)
{
  vec3 l = abs(p)-s;
  return max(l.x,max(l.y,l.z));
}

vec2 map(vec3 p)
{
  float t = iTime*.01;
  float shp = -p.y;//-texture2D(noise, p.xz*.01+vec2(sin(t),cos(t))).x*.05;
  p+=vec3(0.,-.75,0.);
  p.yz*= r2d(PI*.2);
  p.xy*= r2d(PI*.25);

  shp = min(shp,_bbox(p,vec3(1.)));
  return vec2(shp,0.);
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
  vec3 p = ro;
  for (int i = 0; i<steps;++i)
  {
    vec2 res = map(p);
    if (res.x<0.01)
      return vec3(res.x,distance(p,ro),res.y);
    p+= rd*res.x;
  }
  return vec3(-1.);
}

vec3 getCam(vec3 rd, vec2 uv)
{
  float fov = 1.;
  vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
  vec3 u = normalize(cross(rd,r));
  return normalize(rd+fov*(r*uv.x+u*uv.y));
}

vec3 getNorm(vec3 p, float d)
{
  vec2 e = vec2(0.01,0.);
  return normalize(vec3(d)-vec3(map(p-e.xyy).x,map(p-e.yxy).x,map(p-e.yyx).x));
}

vec3 rdr(vec2 uv)
{
  vec3 col;

  vec3 ro = vec3(0.,-5.,-1.);
  vec3 ta = vec3(0.,0.,0.);
  vec3 rd = normalize(ta-ro);

  rd = getCam(rd,uv);

  vec3 res = trace(ro,rd,32);
  if (res.y>0.)
  {
    vec3 p = ro+rd*res.y;
    vec3 n = getNorm(p,res.x);
    col = n*.5+.5;
    vec3 lpos = vec3(sin(iTime),-1./3.,cos(iTime))*10.;
    vec3 ldir = lpos-p;
    vec3 nldir = normalize(ldir);
    vec3 h = normalize(rd-ldir);
    col = vec3(1.)*pow(sat(-dot(n,h)),.75);
    vec3 ressh = trace(p+n*0.02,nldir,64);
    if (ressh.y >0.)
    {

       col*= .75;
    }
    col += vec3(.4,.5,.7)*.5;
    float dao = 0.6;
    col *= sat(pow(sat(map(p+n*dao).x/dao),.5)+.1);
    col+= pow(1.-sat(-dot(rd,n)),.25)*vec3(1.,.5,.7).yzx*.25;
  }
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy-vec2(.5)*iResolution.xy) / iResolution.xx;
  uv *= 2.;
  vec3 col = rdr(uv);
  vec3 o = col;
  vec3 inv = 1.-sat(col.zyx);
  inv = mix(inv,1.-inv,sat((uv.x-uv.y+sin((uv.y+uv.x)*5.+iTime*2.)*.1)*400.));
  col = mix(col,inv,1.-sat((sin((uv.y+uv.x)*20.)+.4)*400.));
  col = pow(col,vec3(.5));
  col = mix(col,o,sat((length(uv)-.4)*400.));
  fragColor = vec4(col, 1.0);
}

float seed = 0.0;

vec3 bgColor = vec3(0, 1, 1);

// random number between 0 and 1
float random() {
    return fract(sin(seed++)*43758.5453123);
}

// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};


// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t = 1000000.0; // initialize to a large number
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return t;
}


Sphere spheres[4];
Light  lights[2];
int numLights = 2;
int numSpheres = 2;

mat3 zRotation(float angle) {
    return mat3(cos(angle),  sin(angle), 0,
                -sin(angle), cos(angle), 0,
                0,           0,          1);
}

vec3 tranformAroundCenter(mat3 transform, vec3 center, vec3 point) {
    return center + transform * (point - center);
}


void makeScene(int f) {
    spheres[0] = Sphere(0.5, vec3(0, 0, -6), vec3(1, 1, 0.2));
    spheres[1] = Sphere(0.24, vec3(1, 1, -6), vec3(1, 0.2, 1));
    //lights[0] = Light(vec3(-1.0, 0.0, -4.0), vec3(1, 1, 1));
    lights[0] = Light(vec3(10.0*sin(float(f)*0.03), 0.0, 0.0), vec3(1, 0.5, 0));
    lights[1] = Light(vec3(0.0, 10.0*sin(float(f)*0.01), 0.0), vec3(0, 0.5, 1));
    numLights = 2;

    mat3 r = zRotation(float(f)*0.1);

    spheres[1].center = tranformAroundCenter(r, spheres[0].center, spheres[1].center);
}

int numSamples = 1;


/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner

   makeScene(iFrame);

   vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);
       float minT = 1000000.0;
       int minI = 0;

       for(int i=0; i<numSpheres; i++) {
           Sphere sphere = spheres[i];

           float t = raySphereIntersect(ray, sphere);
           if (t < minT) {
               // TBD: verify hit is behind the screen
               minT = t;
               minI = i;
           }
       }

       if(minT<1000000.0) {
           Sphere sphere = spheres[minI];
           vec3 hit = ray.origin + minT*ray.direction;
           vec3 norm = normalize(hit - sphere.center);
           vec3 hitPointToEye = normalize(vec3(0, 0, 0)-hit);
           float rayCAngle = dot(norm, hitPointToEye);
           for(int l=0;l<numLights; l++) {
               vec3 hitPointToLight = normalize(lights[l].location-hit);         
               float lightCAngle = dot(hitPointToLight,norm);          
               if(lightCAngle>0.0) { /* back is black */
                  samp = samp + rayCAngle*lightCAngle*(sphere.color * lights[l].color);
               }
           }
       }

       // 2D overlay
       //float ampl = 2.0*texture(iChannel0,vec2(0.8,.25)).x;
       vec2 c = vec2(0.0, 0.0);
       vec2 xy = vec2(x,y);
       vec2 dxy = xy-c;
       float r = length(dxy);
       float a = atan(dxy.y, dxy.x)+3.1415;
       float signal = 2.0*texture(iChannel0,vec2(a/2.0/3.1415, 0.75)).x;
       float R = 0.5;

       /*spheres[1].radius = wave * 0.24; */
       if(abs(signal*r-R)< 0.01)
           samp += vec3(signal*0.5, r, float(iFrame%100)/100.0);
   }


/*   float wave = texelFetch( iChannel0, ivec2(float(512), 0),0 ).x;
   spheres[1].radius = wave * 0.24; 
*/
   fragColor = vec4(samp/float(numSamples), 1.0);
}

// Copyright © 2021 Sashasan https://www.facebook.com/profile.php?id=100005029953825
// Reverse raytracing ray march physically correct refraction. Show back surface.
// There are lots of tips and forms here - https://iquilezles.org

const int MAX_MARCHING_STEPS = 256;
const float NearP = 0.4;
const float FarP = 96.0;
const float MinD = 0.002;
const float NminR = 0.04;
const float NmaxR = 96.0;
const float stepB = 0.5;

const float kr_air = 1.0002926;
const float kr_glass = 1.4874;

vec3	lp;

#define itime iTime*0.05

struct res
{
	float	d;
	int		m;
};


//func sdf
float sdPlane( vec3 p )
{
	return p.y;
}
float sdSphere( vec3 p, float r )
{
	return length(p)-r;
}
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}
float sdTriPrism( vec3 p, vec2 h )
{
	p.xy = mat2x2(cos(itime),sin(itime),-sin(itime),cos(itime))*p.xy;
	vec3 q = abs(p);
	return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}
float sdBox( vec3 p, vec3 b )
{
	p.xz = mat2x2(cos(-itime),sin(-itime),-sin(-itime),cos(itime))*p.xz;
	vec3 d = abs(p) - b;
	return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
//refract func
vec3 refractk(vec3 I, vec3 N, float eta) {
	vec3 R;
	float k = 1.0 - eta * eta * (1.0 - dot(N, I) * dot(N, I));
	return	R = eta * I - (eta * dot(N, I) + sqrt(k)) * N;
}
// to take negative steps into account, compare the absolute values
res opU( res d1, res d2 )
{
	if (abs(d1.d) < abs(d2.d)){
		d2=d1;
	}
	return d2;
}
res sceneSDF(vec3 p)
{
	res oh,th;
	vec3 varp;
	oh=res(sdPlane(p+0.5),1);

	varp = p-vec3(-1.0+fract(itime*1.0)*14.0*(1.0-fract(itime*1.0)),fract(itime*0.1)*8.0*(1.0-fract(itime*0.1)),-1.0);
	th=res(sdSphere(varp,1.0),2);
	oh=opU(oh,th);

	varp = p-lp;
	th=res(sdSphere(varp,0.4),3);
	oh=opU(oh,th);

	varp = p-vec3(-8.0+fract(itime*1.0)*40.0*(1.0-fract(itime*1.0)),fract(itime*3.0)*10.0*(1.0-fract(itime*3.0)),-1.0);
	th=res(sdCapsule(varp,vec3(3.0,0.0,0.0),vec3(3.0,3.0,-1.0),0.05),4);
	oh=opU(oh,th);

    varp = p-vec3(-2.5,fract(itime*0.1)*20.0*(1.0-fract(itime*0.1)),-1.5);
	th=res(sdTriPrism(varp,vec2(1.0,2.0)),2);
	oh=opU(oh,th);

    varp = p-vec3(3.0,fract(itime*0.1)*15.0*(1.0-fract(itime*0.1)),-3.0);
	th=res(sdBox(varp,vec3(0.5,1.5,1.2)) ,2);
	oh=opU(oh,th);

	return oh;
}
//Rey March + Neg. value
res RayMarch(vec3 ro, vec3 rd, float Min_Dist, float Max_Dist) {
	res t0 = res(Min_Dist,0);
	res h;
	for (int i = 0; i < MAX_MARCHING_STEPS && t0.d<=Max_Dist; i++) {
		h = sceneSDF(ro + t0.d * rd);
		if (abs(h.d) < MinD) {
			t0.m=h.m;
			break;
		}
		t0.d=abs(h.d)*stepB+t0.d;
	}
	if (t0.d > Max_Dist) {t0.m=0;t0.d=Max_Dist;}
	return t0;
}
//MatView
vec3 RayD(float fieldOfView, vec2 size, vec2 tc0) {
	vec2 xy = tc0 - size / 2.0;
	float z = size.y / tan(radians(fieldOfView) / 2.0);
	return normalize(vec3(xy, -z));
}
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv =          ( cross(cu,cw) );
	return mat3( cu, cv, cw );
}
//GetNormal
vec3 GradeSdfNormal(vec3 p) {
	return normalize(vec3(	sceneSDF(vec3(p.x + MinD, p.y, p.z)).d  - sceneSDF(vec3(p.x - MinD, p.y, p.z)).d,
							sceneSDF(vec3(p.x, p.y + MinD, p.z)).d  - sceneSDF(vec3(p.x, p.y - MinD, p.z)).d,
							sceneSDF(vec3(p.x, p.y, p.z  + MinD)).d - sceneSDF(vec3(p.x, p.y, p.z - MinD)).d));
}
vec3 RayRr (in vec3 ro, in vec3 rd, int dr) {
	vec3 n,col,r_d,io;
	col=vec3(0.0);
	res t0 = RayMarch(ro,rd,NearP,FarP);
	for (int i = 0; i < dr; i++) {
		if (t0.m==1) {
			ro = ro + t0.d * rd;
			n = GradeSdfNormal(ro);
            if (dot(rd,n)>=0.0){n=-n;}
			rd = normalize(lp-ro);
			r_d = vec3(dot(rd,n)*0.9);
			float f = mod( floor(2.0*ro.z) + floor(2.0*ro.x), 2.0);
			col -= (vec3( 0.5*f)+vec3(0.2,0.2,0.2))*(1.0-r_d);
			t0 = RayMarch(ro,rd,NminR,NmaxR);
            continue;
		}
		else if (t0.m==2) {
			ro = ro + t0.d * rd;
			n = GradeSdfNormal(ro);
			if (dot(rd,n)<0.0){io.x=kr_air/kr_glass;}else{io.x=kr_glass/kr_air;n=-n;}
			r_d = vec3(dot(rd,n)*0.1);
			col -= vec3(0.1,0.1,0.01)*(1.0-r_d);
            vec3 rd2=rd;
			rd = refract(rd,n,io.x);
            if (rd==vec3(0.0)){rd=reflect(rd2,n);}
			t0 = RayMarch(ro,rd,NminR,NmaxR);
            continue;
		}
		else if (t0.m==3) {
			col += vec3(1.0,1.0,0.9);
			break;
		}
		if (t0.m==4) {
			ro = ro + t0.d * rd;
			n = GradeSdfNormal(ro);
			if (dot(rd,n)>=0.0){n=-n;}
			rd = normalize(lp-ro);
			r_d = vec3(dot(rd,n)*0.9);
			col -= (vec3(0.1,0.3,0.2))*(1.0-r_d);;
			t0 = RayMarch(ro,rd,NminR,NmaxR);
            continue;
		}
		else {
			ro = ro + t0.d * rd;
			float sun =dot(rd,normalize(ro-lp));
			vec3 clo=0.9*(sun*(vec3(0.65,0.75,0.9)-(rd.y*0.5))) ;
			col+=clo.xyz-vec3(0.01,0.01,0.01);
			break;
		}

	}


	return clamp(col,0.0,1.0) ;
}
