    col += drawBands(p);//*smoothstep(1.0,0.5,uv.y);;

    vec3 ref = vec3(0.0);
    vec2 eps = vec2(3.0025,-0.025);

    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);

    ref += drawBands(vec2(p.x+eps.x,1.0-p.x));
    ref += drawBands(vec2(p.x+eps.y,1.0-p.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.y));

    ref /= -8.0;

    float colStep = length(smoothstep(1.0,0.1,col));

    vec3 cs1 = drawBands(vec2(3.5,0.51));
    vec3 cs2 = drawBands(vec2(0.5,0.93));

    vec3 plCol = mix(cs1,cs2,length(p*1.0-1.0))*0.5*smoothstep(10.75,-0.5,length(p*0.0-1.0));
    vec3 plColBg = vec3(0.2)*smoothstep(1.0,0.0,length(p*2.0-1.0));
    vec3 pl = (plCol+plColBg)*smoothstep(6.5,0.65,5.0-uv.y);

    col += clamp(pl*(0.10-colStep),0.0,1.0);

    col += ref*smoothstep(0.125,1.6125,p.y); 

    col = clamp(col, 0.0, 1.0);

    float dither = noise3D(vec3(uv,time))*9.0/2226.0;
    col += dither;



	fragColor = vec4(col,1.0);
}

