/*
The code for the atmospheric scattering came from some of my other projects and was modified to work on a flat world
The code for the clouds was writen fully for this program dispite using this same method to render volumetric clouds in some of my other shaders
The stary sky is using the same method as one of my space (earth) shaders (The method is to use triplanar mapping and taking the color of the stars and putting it to a large power to single out the stars)
The noise function was from another shadertoy as I'm not very good at making/inplementing gradient noise algerithms myself

I hope you enjoy this shader as It took me over 4.5 hours
*/


// from another shadertoy shader
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

// from another shadertoy shader
vec4 noised( in vec3 x )
{
    // grid
    vec3 i = floor(x);
    vec3 w = fract(x);

    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);

    // gradients
    vec3 ga = hash( i+vec3(0.0,0.0,0.0) );
    vec3 gb = hash( i+vec3(1.0,0.0,0.0) );
    vec3 gc = hash( i+vec3(0.0,1.0,0.0) );
    vec3 gd = hash( i+vec3(1.0,1.0,0.0) );
    vec3 ge = hash( i+vec3(0.0,0.0,1.0) );
	vec3 gf = hash( i+vec3(1.0,0.0,1.0) );
    vec3 gg = hash( i+vec3(0.0,1.0,1.0) );
    vec3 gh = hash( i+vec3(1.0,1.0,1.0) );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return vec4( va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + u.x*u.y*(va-vb-vc+vd) + u.y*u.z*(va-vc-ve+vg) + u.z*u.x*(va-vb-ve+vf) + (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z,    // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.z*(ge-ga) + u.x*u.y*(ga-gb-gc+gd) + u.y*u.z*(ga-gc-ge+gg) + u.z*u.x*(ga-gb-ge+gf) + (-ga+gb+gc-gd+ge-gf-gg+gh)*u.x*u.y*u.z +   // derivatives
                 du * (vec3(vb,vc,ve) - va + u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) ));
}


// samples 3D nosie and stores it in one channel (x aka a float)
float SampleNoise(vec3 p)
{
    float h = noised(p).x;
    h += noised(p*2. + 100.).x * 0.5;
    h += noised(p*4. - 100.).x * 0.25;
    h += noised(p*8. + 1000.).x * 0.125;
    return h / (1.865);
}


vec4 sampleNoise(vec3 p)
{
    vec4 h = noised(p);
    h += noised(p*2. + 100.).x * 0.5;
    h += noised(p*4. - 100.).x * 0.25;
    h += noised(p*8. + 1000.).x * 0.125;
    return h / (1.865);
}


// stores data on specualar refleciton and diffusion
struct SpecularLight
{
    float highlight;  // the specular reflection/highlight
    float diffuse;    // basic diffuse lighting (dosent give the best ouput in this shader and some others)
};


// calculates the speucalar reflection and diffusion
SpecularLight Specular(float smoothness, vec3 normal, vec3 rd, vec3 sun_dir)
{
    float specularAngle = acos(dot(normalize(sun_dir - rd), normal));
    float specularExponent = specularAngle / (1. - smoothness);
    // finding the specular highlight
    float specularHighlight = exp(-specularExponent * specularExponent);
    // fidning the amount of diffused light
    float diffseLighting = clamp(dot(normal, sun_dir), 0., 1.);
    return SpecularLight(specularHighlight, diffseLighting);
}


// the density at a given point in the atmosphere
float GetDensity(float dstToPlannet)
{
    float density_fall_off = 6.5;  // the falloff speed for the atmospheric density
    float h = dstToPlannet * 0.2;  // normalizeing the distance to the plannet based on the difference between the raidius of the atmosphere and the plannet
    float density = exp(-h * density_fall_off) * (1. - h);
    return density;
}


// uses ray marching to find the distance through the plannet (for a given position and direction)
float raySphere(vec3 ro, vec3 rd)
{
    float lp;
    float dst;
    vec3 p = ro;
    float dfs = 0.;
    // stepping and moving the ray closer and closer
    for (int s = 0; s < 80; s++)
    {
        dst = -(p.y - 5.);

        // checking if its super close (to speed it up some sense its called so many times)
        if (dst < 0.01) break;
        dfs += dst;
        p += rd * dst;
    }
    // returning the distance through the sphere
    return dfs;
}


// gets the optical depth based on the ray direction, length, and position
float opticalDepth(vec3 ro, vec3 rd, float rl)
{
    // the rays position
    vec3 p = ro;
    // the step size
    float step_size = rl / 11.;
    vec3 v3_step_size = rd * step_size;
    // the optical depth
    float odepth = 0.;
    // the density
    float ldense;
    // stepping throught the atmosphere
    for (int s = 0; s < 10; s++)
    {
        // moving the ray/point
        p += v3_step_size;
        // sampling the atmospheric density
        ldense = GetDensity(p.y);
        // adding up the optical depth
        odepth += ldense * step_size;
    }
    // returning the optical depth
    return odepth;
}


// triplannar mapping https://www.youtube.com/watch?v=lctXaT9pxA0&t=1031s
vec3 TriplannarStars(vec3 p, vec3 normal)
{
    // the scale of the texture
    float scale = 1.;
    // the sharpness of the blending between different axises
    float blendSharpness = 5.;
    // finding the different axise's color
    vec3 colX = texture(iChannel1, p.zy * scale).rgb;
    vec3 colY = texture(iChannel1, p.xz * scale).rgb;
    vec3 colZ = texture(iChannel1, p.xy * scale).rgb;

    // finding the blending amount for each axis
    vec3 bw = pow(abs(normal), vec3(blendSharpness));
    // making it so the total (x + y + z) is 1
    bw /= dot(bw, vec3(1.));

    // finding the final color
    return colX * bw.x + colY * bw.y + colZ * bw.z;
}


// gets the density of a cloud at a 3D point
float CloudDensity(vec3 p)
{
    return max(noised(p * 0.01).z * 0.9 + 0.1, 0.);
}


float GetDst(vec3 p)
{
    return p.y + texture(iChannel0, p.xz * 0.5, 0.).g;
}


// returns the normal based on a signed distance feild
vec3 GroundNoraml(vec3 p)
{
	float d = GetDst(p);
    vec2 e = vec2(0.075, 0);

    vec3 normal = d - vec3(
        GetDst(p - e.xyy),
        GetDst(p - e.yxy),
        GetDst(p - e.yyx));

    return normalize(normal);
}


// finding the scattering coefficents based on the wave lengths of different light colors
vec3 scatteringCoefficents = pow(vec3(400.) / vec3(700, 530, 440), vec3(4.)) * 1.5;  // the coefficents for the scattering of different wave lengths/colors of light


vec4 RenderAtmo(vec3 rd, vec3 ro, vec3 waterCp, vec3 sun_dir, vec3 col)
{
    float dstThroughAtmosphere;
    if (rd.y <= 0.)
    {
        dstThroughAtmosphere = min(length(waterCp - ro) / 20., 5.);
    }
    else dstThroughAtmosphere = 5.;  // - min(ro.y, 5.);
    // finding the step size going through the atmosphere
    float step_size_f = dstThroughAtmosphere / 11.;
    vec3 step_size = rd * step_size_f;
    vec3 p = vec3(0.);

    // initizalizing some terms
    float density;
    float sun_length;
    vec3 transmittance;
    float sunRayOpticalDepth;
    float veiwRayOpticalDepth;
    vec3 inScatteredLight = vec3(0.);
    // stepping through the atmosphere
    for (int s = 0; s < 10; s++)
    {
        // moving the ray/point
        p += step_size;
        // fining the length through the atmosphere in the direction of the sun
        sun_length = raySphere(p, sun_dir);
        // finding the optical depth for the view ray and sun ray
        sunRayOpticalDepth = opticalDepth(p, sun_dir, sun_length);
        veiwRayOpticalDepth = opticalDepth(p, -rd, step_size_f * float(s));
        // finding the transmittance
        transmittance = exp(-(sunRayOpticalDepth + veiwRayOpticalDepth) * scatteringCoefficents);
        // finding the atmospheric density at this point
        density = GetDensity(p.y);

        // finding the amount of addition light
        inScatteredLight += density * transmittance * scatteringCoefficents * step_size_f;
    }
    // applying the atmosphere
    return vec4(inScatteredLight, exp(-veiwRayOpticalDepth));
}


vec3 renderSky(vec3 ro, vec3 rd, vec3 waterCp, vec3 sun_dir, vec3 sun_col, float time, vec3 col)
{
    // rendering atmosphere
    // finding the distance through the atmosphere
    float density;
    vec4 inscat = RenderAtmo(rd, ro, waterCp, sun_dir, col);
    col = col * inscat.w + inscat.rgb;

    // checking if the sky needs to be rendered
    if (rd.y > 0.)
    {
        // adding the stars
        col += pow(TriplannarStars(rd + time * 0.1, rd), vec3(2.75)) * pow(1. - length(col), 5.) * 0.7;
        // adding the sun
        col = mix(col, sun_col + inscat.rgb, pow(max(dot(rd, sun_dir), 0.), 275.));

        // rendering clouds
        // finding the distance to the cloud layer
        float toClouds = (40. - ro.y) / rd.y;
        // checking if the cloud layer is within a reasonable range
        if (toClouds < 920.)
        {
            // finding the distance throught the cloud layer
            float throughClouds = 50. / rd.y;

            // finding the step size going through the cloud layer
            float step_size_f = min(throughClouds / 21., 15.);
            vec3 step_size = rd * step_size_f;

            // finding where the point collides with the cloud layer (with some shifting so the cloud move)
            vec3 p = vec3(-ro.x * 4. + iTime * 12., ro.y, -ro.z * 4. +  + iTime * 12.) + rd * toClouds;
            float oy = p.y;

            // defining some terms
            vec3 sun_p;
            vec3 sunStep;
            float sunStepF;

            vec3 inScattered = vec3(0.);
            float transmittion = 1.;
            float totalLightDensity;

            vec4 inscat2 = RenderAtmo(sun_dir, p, vec3(9999999999.), sun_dir, vec3(0.));

            // stepping through the clouds
            for (int s = 0; s < 20; s++)
            {
                // moving the point/ray
                p += step_size;

                // sampling the density
                density = CloudDensity(p) * step_size_f;  // replace with noise function

                // stepping twords the sun
                if (density >= 0.)
                {
                    // reseting some terms
                    sun_p = p;
                    totalLightDensity = 0.;
                    if (sun_dir.y >= 0.)
                    {
                        sunStepF = min((50. - (p.y - 40.)) / sun_dir.y / 11., 10.);
                    }
                    else
                    {
                        sunStepF = min(((p.y - 40.) / abs(sun_dir.y) / 11.), 10.);
                    }
                    sunStep = sun_dir * sunStepF;
                    // stepping through the cloud twords the sun
                    for (int s = 0; s < 10; s++)
                    {
                        sun_p += sunStep;
                        // sampling the density at the current point
                        totalLightDensity += CloudDensity(sun_p) * sunStepF;
                        // moving the ray/point
                    }
                    // finding the transmittance based on the amount of stuff (cloud) in the way e^-density
                    transmittion *= exp(-density * 0.1);
                    // finding the amount of inScatterd light
                    //vec3 c = RenderAtmo(rd, p * vec3(1., 0.075, 1.), vec3(999999999.), sun_dir, vec3(0.)).rgb;
                    // (vec3(1.) - vec3(0., 0.8, 1.) * (1. - c.b))
                    inScattered += (vec3(1.) - vec3(0., 0.8, 1.) * (1. - inscat2.b)) * (density * transmittion * exp(-totalLightDensity * 0.1) * 0.1);
                    //inScattered = sun_col + c;
                    //col = c;
                    //return col;
                }

                if (transmittion < 0.05) break;  // an optomization as the transmittion is so low that anything lower dosent vissually change much
            }
            // fading the clouds out at the edges so it looks like they are within the atmosphere
            transmittion = mix(transmittion, 1., (toClouds / 920.));
            // checking if the clouds are being shadowed by the planet (night time)
            // add the clouds on top of the current color based on the transmittion
            if (sun_dir.y <= 0.)
            {
                col = mix(col * transmittion, vec3(inScattered * 0.8 + 0.2), 1. - transmittion);
                //col = mix(col * transmittion, mix(col * transmittion, vec3(inScattered * 0.8 + 0.2), 1. - transmittion), max(min(oy / abs(sun_dir.y) / 2500., 1.), 0.));
            }
            else
            {
                col = mix(col * transmittion, vec3(inScattered * 0.8 + 0.2), 1. - transmittion);
            }

        }
    }

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // the uv coord
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    // the scaled time
    float time = iTime * 0.1;

    vec3 sun_col = vec3(1., 0.8, 0.65) * 1.4;
    //vec3 sun_dir = normalize(vec3(0.2, 0.3, 1.));
    vec3 sun_dir = normalize(vec3(sin(time) * 0.2 + 0.2, sin(time), cos(time)));

    // the ray direction and ray orgin
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.));
    vec3 ro = vec3(2. - iMouse.x / iResolution.x * 4. - 2., 2., 2. - iMouse.y / iResolution.y * 4. - 2.);

    // the color (which will be the output)
    vec3 col = vec3(0.);

    // the waters collide point
    vec3 waterCp = ro + rd * (ro.y / rd.y);
    if (rd.y <= 0.)
    {
        // finding a noise value at the position of where the ray collides with the water plane
        vec4 noise = (1. - abs(sampleNoise(vec3(waterCp.xz - time, iTime * 0.5)))) * 2. - 1.;
        // finding the water normal (for refraction)
        vec3 waterNorm = normalize(vec3((noise.x * 0.5), 12., (noise.w * 0.5)));
        // fining the refracted ray direction (to sample the texture)
        vec3 refracted = refract(rd, waterNorm, 0.075);
        // fidning where the refracted ray hits the bottom of the river
        vec3 cp = waterCp + refracted * ((waterCp.y + 2.) / refracted.y);

        // fidning the river bottoms texture
        vec3 text = texture(iChannel0, cp.xz * 0.5, 0.).rgb;
        // setting the color to the texture
        col = text;
        // adding specular reflection/highlight to the waters surface
        SpecularLight spec = Specular(0.6, waterNorm, rd, sun_dir);
        col = col + spec.highlight;

        // add a bit of diffuse lighting to the river bed (i think its diffuse lighting at least)
        vec3 groundNormal = GroundNoraml(cp);
        col *= dot(groundNormal, sun_dir) * 0.25 + 0.75;

        float waterDepth = max(length(cp - waterCp) - groundNormal.y * 5., 0.);

        // haveing the water be dark during the night (sense the lighting dosent make it very dark)
        col *= min(sun_dir.y * 0.3 - 0.7, 0.) + 1.;

        col = mix(col, vec3(0.1, 0.1, 0.4), 1. - exp(-waterDepth * 0.25));

        vec3 reflected = reflect(rd, waterNorm);
        col = mix(col, renderSky(waterCp, reflected, waterCp, sun_dir, sun_col, time, col), min(1. + dot(-sun_dir, reflected), 1.));
    }
    col = renderSky(ro, rd, waterCp, sun_dir, sun_col, time, col);

    // setting the final color to col
    fragColor = vec4(col,1.0);
}

/*
This turned out pretty clean, simple, and understandable! Also it never evaluates
more than four typically expensive functions (e.g. trig, square roots, etc.)
which is one up on my other cubic solvers.

I also added handling for when p=0 which simply blows up the whole thing but I'm not
checking for degenerate cubic equations (please don't solve linear equations
with this, ok? ;))
*/

// Special cube root to fix GLSL's negative pow() bug
float cbrt(in float x) { return sign(x) * pow(abs(x), 1.0 / 3.0); }
int solveCubic(in float a, in float b, in float c, in float d, out vec3 roots) {
    float u = b / (3.0 * a);

    // Depress to x^3 + px + q by substituting x-b/3a
    // This can be found by substituting x+u and solving for u so that the x^2
    // term gets eliminated (then of course dividing by the leading coefficient)
    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;

    // Everything blows up when p=0 so give this case special treatment
    if (abs(p) < 1e-9) { roots.x = cbrt(-q) - u; return 1; }

    // In the case of one root, this construction does not work
    float h = 0.25 * q * q + p * p * p / 27.0;
    if (h > 0.0) { // Check depressed cubic discriminant
        h = sqrt(h);
        float o = -0.5 * q;
        roots.x = cbrt(o - h) + cbrt(o + h) - u; // Cardano's formula (see https://en.wikipedia.org/wiki/Cubic_equation)
        return 1;
    }

    // Solve by mapping an inverse smoothstep between the critical points
    // I found a whole lot simplified right out so now it probably looks rather obfuscated
    float m = sqrt(-p / 3.0);
    roots.x = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    // Factor out the root to solve for the rest as a quadratic
    h = sqrt(-3.0 * roots.x * roots.x - 4.0 * p);
    roots.yz = 0.5 * vec2(h - roots.x, -h - roots.x);
    roots -= u; // Undo the change in variable

    return 3;
}

