void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Animated coefficients
    float a = sin(iTime) * 2.0;
    float b = sin(iTime) * 5.0;
    float c = cos(iTime * 0.75);
    float d = sin(iTime * 0.5);

    float f = a * uv.x * uv.x * uv.x + b * uv.x * uv.x + c * uv.x + d;
    float g = 3.0 * a * uv.x * uv.x + 2.0 * b * uv.x + c; // Derivative for distance estimate
    float cubic = abs(uv.y - f) / sqrt(1.0 + g * g); // Distance estimate

    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, abs(uv.y))); // X axis
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, cubic)); // Cubic function

    // Solve for the roots of the derivative (a quadratic)
    // to get the extrema
    float da = 3.0 * a;
    float db = 2.0 * b;
    float dc = c;

    float discr = db * db - 4.0 * da * dc;
    vec2 ex1 = vec2(0.0), ex2 = vec2(0.0);
    if (discr > 0.0) {
        discr = sqrt(discr);
        da *= 2.0;

        // Extrema 1
        ex1 = vec2((-db + discr) / da, 0.0);
        ex1.y = ((a * ex1.x + b) * ex1.x + c) * ex1.x + d;

        // Extrema 2
        ex2 = vec2((-db - discr) / da, 0.0);
        ex2.y = ((a * ex2.x + b) * ex2.x + c) * ex2.x + d;

        // Draw the segment between the extrema where the inverse smoothstep
        // gets fitted
        if (min(ex1.x, ex2.x) < uv.x && uv.x < max(ex1.x, ex2.x)) {
            color = mix(color, vec3(1.0, 0.8, 0.0), smoothstep(unit, 0.0, cubic - 0.025));
        }

        // Draw the extrema
        color = mix(color, vec3(1.0, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - ex1) - 0.05));
        color = mix(color, vec3(1.0, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - ex2) - 0.05));
    }

    // Draw roots
    vec3 roots;
    int nroots = solveCubic(a, b, c, d, roots);
    for (int n=0; n < nroots; n++) {
        vec3 rootColor = vec3(1.0);
        if (n == 0 && sign(ex1.y) != sign(ex2.y)) rootColor.rb = vec2(0.0); // Draw inverse smoothstepped root in green
        color = mix(color, rootColor, smoothstep(unit, 0.0, length(uv - vec2(roots[n], 0.0)) - 0.05));
    }

    fragColor = vec4(color, 1.0);
}

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float pi = acos(-1.);
float frame( vec3 p, vec3 b, float e )
{
  p = abs(p)-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float box( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 min2(vec2 a , vec2 b)
{
    if(b.x < a.x)
    {
        a = b;
    }
    return a;
}

vec3 random3(vec3 c) {
    float j = 4096.0*sin(dot(c,vec3(17.0, 59.4, 15.0)));
    vec3 r;
    r.z = fract(512.0*j);
    j *= .125;
    r.x = fract(512.0*j);
    j *= .125;
    r.y = fract(512.0*j);
    return r-0.5;
}

//https://www.shadertoy.com/view/XsX3zB
/* skew constants for 3d simplex functions */
const float F3 =  0.3333333;
const float G3 =  0.1666667;

/* 3d simplex noise */
float simplex3d(vec3 p) {
    /* 1. find current tetrahedron T and it's four vertices */
    /* s, s+i1, s+i2, s+1.0 - absolute skewed (integer) coordinates of T vertices */
    /* x, x1, x2, x3 - unskewed coordinates of p relative to each of T vertices*/

    /* calculate s and x */
    vec3 s = floor(p + dot(p, vec3(F3,F3,F3)));
    vec3 x = p - s + dot(s, vec3(G3,G3,G3));

    /* calculate i1 and i2 */
    vec3 e = step(vec3(0.,0.,0.), x - x.yzx);
    vec3 i1 = e*(1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy*(1.0 - e);

    /* x1, x2, x3 */
    vec3 x1 = x - i1 + G3;
    vec3 x2 = x - i2 + 2.0*G3;
    vec3 x3 = x - 1.0 + 3.0*G3;

    /* 2. find four surflets and store them in d */
    vec4 w, d;

    /* calculate surflet weights */
    w.x = dot(x, x);
    w.y = dot(x1, x1);
    w.z = dot(x2, x2);
    w.w = dot(x3, x3);

    /* w fades from 0.6 at the center of the surflet to 0.0 at the margin */
    w = max(0.6 - w, 0.0);

    /* calculate surflet components */
    d.x = dot(random3(s), x);
    d.y = dot(random3(s + i1), x1);
    d.z = dot(random3(s + i2), x2);
    d.w = dot(random3(s + 1.0), x3);

    /* multiply d by w^4 */
    w *= w;
    w *= w;
    d *= w;

    /* 3. return the sum of the four surflets */
    return dot(d, vec4(52.0,52.0,52.0,52.0));
}

float smoothMin(float d1, float d2, float k){
    float h = exp(-k * d1) + exp(-k * d2);
    return -log(h) / k;
}

