int octantX(vec3 point, vec3 center, out vec3[8] corners)
{
    vec3 res = iResolution.xyy/iResolution.xxx;
    corners = vec3[8](
        vec3(center.x-res.x,center.y      ,center.z      ),vec3(center.x,center.y      ,center.z      ),
        vec3(center.x-res.x,center.y-res.y,center.z      ),vec3(center.x,center.y-res.y,center.z      ),
        vec3(center.x-res.x,center.y      ,center.z-res.z),vec3(center.x,center.y      ,center.z-res.z),
        vec3(center.x-res.x,center.y-res.y,center.z-res.z),vec3(center.x,center.y-res.y,center.z-res.z));

    if (point.x > center.x+res.x ||
        point.y > center.y+res.y ||
        point.z > center.z+res.z ||
        point.x < center.x-res.x ||
        point.y < center.y-res.y ||
        point.z < center.z-res.z)
    {
        return 8;
    }
    if (point.x > center.x)
    {
        if (point.y > center.y)
        {
            if (point.z > center.z)
            {
                for (int i = 0; i< 8; i++)
                {
                    corners[i].xyz += res.xyz;
                }
                return 0;
            }
            for (int i = 0; i< 8; i++)
            {
                corners[i].xy += res.xy;
            }
            return 1;
        }
        if (point.z < center.z)
        {
            for (int i = 0; i< 8; i++)
            {
                corners[i].xz += res.xz;
            }
            return 3;
        }
        for (int i = 0; i< 8; i++)
        {
            corners[i].x += res.x;
        }
        return 2;
    }
    if (point.y > center.y)
    {
        if (point.z > center.z)
        {
            for (int i = 0; i< 8; i++)
            {
                corners[i].yz += res.yz;
            }
            return 5;
        }
        for (int i = 0; i< 8; i++)
        {
            corners[i].y += res.y;
        }
        return 6;
    }
    if (point.z > center.z)
    {
        for (int i = 0; i< 8; i++)
        {
            corners[i].z += res.z;
        }
        return 4;
    }
    for (int i = 0; i< 8; i++)
    {
        //corners[i].x += res.x;
    }
    return 7;
}

