float calcNoise3d(vec3[8] pos, vec3[8] height, vec3 center, vec3 point)
{
    vec3 res = iResolution.xyy/iResolution.xxx;
    float a, b, c, d, x, y, z;
    x = smoothy((pos[0].x-point.x)/res.x);
    y = smoothy((pos[0].y-point.y)/res.y);
    z = smoothy((pos[0].z-point.z)/res.z);
    a = mix(dot(point-pos[0],height[0]), dot(point-pos[1],height[1]), x);
    b = mix(dot(point-pos[2],height[2]), dot(point-pos[3],height[3]), x);
    c = mix(dot(point-pos[4],height[4]), dot(point-pos[5],height[5]), x);
    d = mix(dot(point-pos[6],height[6]), dot(point-pos[7],height[7]), x);
    return mix(mix(a,b,y),mix(c,d,y),z);
}

