float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba * h);
}

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash11(in float x) {
    x = fract(x * 0.1031);
    x *= x + 33.33;
    x *= x + x;
    return fract(x);
}

float anim(in float seed) {
    float a = Hash11(seed * 193.84 + 673.48);
    float b = Hash11(seed * 348.46 + 183.37);
    float c = Hash11(seed * 275.35 + 741.69);
    return 2.0 * sin(iTime * a + b) * c;
}

