    superSample += raytrace(uv+vec2(1./iResolution.x/2., -1./iResolution.x/2.));
    superSample += raytrace(uv+vec2(-1./iResolution.x/2., -1./iResolution.x/2.));
    superSample += raytrace(uv+vec2(1./iResolution.x/2., 1./iResolution.x/2.));
    superSample += raytrace(uv+vec2(-1./iResolution.x/2., 1./iResolution.x/2.));
    superSample /= 4.;
    fragColor = vec4(pow(superSample, vec3(0.4545)), 1.0);
}

