                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float M_PI = 3.14159265358979323846264338327950288;
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = vec2(uv*5.0);
    float n = noise(pos);
    float cx=uv.x+0.5*sin(iTime/5.0);
    float cy=uv.y+0.5*cos(iTime/3.0);
    float v=sin(sqrt(100.0*(cx*cx+cy*cy)+1.0)+iTime);
    v+=sin(10.0*(uv.x*sin(iTime/2.0))+uv.x*cos(iTime/3.0)+iTime);
    v+=sin(uv.x*10.0+iTime);
    vec3 col=vec3(cos(v*M_PI), sin(v*M_PI*n),sin(v*M_PI));
    fragColor = vec4(col,1.0);
}

// Adapted from: https://timcoster.com/2020/02/11/raymarching-shader-pt1-glsl/
// The Art of Code: The Art Of Code Youtube
// Inigo Quilez: http://www.iquilezles.org

// Constants
#define PI 3.1415925359
//#define TWO_PI 6.2831852
#define MAX_STEPS 100 // Mar Raymarching steps
#define MAX_DIST 100. // Max Raymarching distance
#define SURFACE_DIST .01 // Surface Distance

///////////////////////
// Boolean Operators
///////////////////////

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}
//////////////////////////

mat2 Rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c,-s,s,c);
}

float sphereSDF( vec3 p, float s ) {
  return length(p)-s;
}

float boxSDF( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float GetDist(vec3 p) 
{   
    // Rotate the whole scene
    p.xz *=Rotate(iTime / 2.0);

    // Sphere. xyz is position w is radius
    vec4 s0 = vec4(0,1,0,1);
    float sphereDist_0 = sphereSDF(p-s0.xyz,s0.w);

    // Box
    vec3 b0s = vec3(.8,.8,.8); //box size
    vec3 b0p = vec3(0,1,0); // box position
    float boxDist_0 = boxSDF(p-b0p,b0s);

    // Plane
    float planeDist  = p.y;

    float d = 0.0;
    d = intersectSDF(boxDist_0,sphereDist_0);
    // Uncomment to view the box
    //d = min(boxDist_0,sphereDist_0);
    d = min(d,planeDist);

    return d;
}

float RayMarch(vec3 ro, vec3 rd) 
{
    float dO = 0.; //Distane Origin
    for(int i=0;i<MAX_STEPS;i++)
    {
        vec3 p = ro + rd * dO;
        float ds = GetDist(p); // ds is Distance Scene
        dO += ds;
        if(dO > MAX_DIST || ds < SURFACE_DIST) 
            break;
    }
    return dO;
}

vec3 GetNormal(vec3 p)
{ 
    float d = GetDist(p); // Distance
    vec2 e = vec2(.01,0); // Epsilon

    vec3 n = d - vec3(
        GetDist(p-e.xyy),  // e.xyy is the same as vec3(.01,0,0). The x of e is .01. this is called a swizzle
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p)
{ 
    // Directional light
    vec3 lightPos = vec3(5.*sin(iTime),5.,5.0*cos(iTime)); // Light Position
    vec3 l = normalize(lightPos-p); // Light Vector
    vec3 n = GetNormal(p); // Normal Vector

    float dif = dot(n,l); // Diffuse light
    dif = clamp(dif,0.,1.); // Clamp so it doesnt go below 0

    // Shadows
    float d = RayMarch(p+n*SURFACE_DIST*2., l);

    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,1,-6); // Ray Origin/Camera
    vec3 rd = normalize(vec3(uv.x,uv.y,1)); // Ray Direction

    float d = RayMarch(ro,rd); // Distance

    vec3 p = ro + rd * d;
    float dif = GetLight(p); // Diffuse lighting
    d*= .2;
    vec3 color = vec3(dif);

    // Set the output color
    fragColor = vec4(color,1.0);
}

#define AMBIENT_LIGHTING 1.0

// Map value to the [0, 1] range
float map(float a, float b, float val)
{
    return (val - a) / (b - a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Map coordinates to the [-1, 1] range
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;

    // Even aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    // Default color (background)
    vec3 diffColor = vec3(0.4);

    // Ray to be thrown
    vec3 rayOrg = vec3(0);
    vec3 rayDir = normalize(vec3(uv, 1));

    // Sphere properties
    vec3 sphCenter = vec3(0, 0, 4);
    sphCenter.xz += vec2(sin(iTime), cos(iTime));
    float sphRadius = 1.5;

    //
    // Ray-Sphere interesection calculation
    //

    // Midpoint between the two intersection points
    // NOTE: for rays tangent to the surface, it is the intersection point itself
    float midPoint = dot(sphCenter - rayOrg, rayDir);
    vec3 currPoint = rayOrg + rayDir * midPoint;

    // Compute color for the sphere fragments
    float y = length(sphCenter - currPoint);
    if (y < sphRadius)
    {
        float x = sqrt(sphRadius * sphRadius - y * y); 
        float intersectPoint = midPoint - x;

        float distToSphere = abs(sphCenter.z - rayOrg.z - sphRadius);
        float distToPoint = abs(intersectPoint - rayOrg.z);

        vec3 localColor = vec3(AMBIENT_LIGHTING) * vec3(0.5, 1, 1);
        diffColor = localColor * vec3(map(distToSphere + sphRadius, distToSphere, distToPoint));
     }

    // Output to screen
    fragColor = AMBIENT_LIGHTING * vec4(diffColor, 1);
}

float getDist(vec3 p){
    vec4 sphere = vec4(0, 1, 6, 1);
    float planeDist = p.y;
    float speed = 0.1;
    float dis = 2.0;
    float d = distance(sphere.xyz,p) - sphere.w;
    for (int i = 0; i < 10; i+=1){
        float j = float(i);
        sphere = vec4(0.0 + sin(iTime*j*speed)*dis*j,1.0, 6.0 + cos(iTime*j*speed)*dis*j, 1.0-j*0.05);
        d = min(distance(sphere.xyz, p) - sphere.w,d);
    }
    d = min(d, planeDist);
    return d;
}
float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.0;
    for(int i = 0; i < 100; i+=1){
        vec3 p = ro + rd*dO;
        dO += getDist(p);
        if(dO > 100.0 || dO < 0.1) break;
    }
    return dO;
}
vec3 getNormal(vec3 p){
    float d = getDist(p);
    vec2 e = vec2(0.01,0.0);
    vec3 n = d - vec3(getDist(p - e.xyy), getDist(p - e.yxy), getDist(p - e.yyx));
    return normalize(n);
}
float getLight(vec3 p){
    vec3 lo = vec3(0, 8, 2);
    vec3 l = normalize(lo - p);
    vec3 n = getNormal(p);
    return dot(n,l);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.yy;

    vec3 ro = vec3(0, 2.0+sin(iTime),-3.0+cos(iTime));
    vec3 dr = normalize(vec3(uv, 1.0));
    float d = RayMarch(ro, dr);
    vec3 p = ro + dr * d;
    fragColor.rgb = vec3(d/12.0, getLight(p), d/5.0);
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,n=vec3(-.5,-.707,.5),
    d=normalize(vec3((C-.5*r.xy),r.y));  
    for(float i=0.,e,g=0.;
        ++i<99.;
        O.xyz+=mix(vec3(1),H(length(p)*.5+iTime*3.),.7)*.05*exp(-.03*i*i*e)
    )
    {
        p=g*d;
        p.z-=10.;
        p=R(p,normalize(vec3(-1,-2,2)),iTime*.5);
        for(int j=0;j<4;j++)
            p.xy=abs(p.xy),
            p-=2.*min(0.,dot(p,n))*n;
        p.z=fract(log(p.z)-iTime*.5)-.5;
        g+=e=abs(min(length(p.yz),length(p.xz))-.03)+.001;
    }
}

#define B 10.0
#define N 200
#define PERIOD 10.0

float mandelbrot(vec2 c) {
   vec2 z = vec2(.0);
   float n = .0;
   for(int i = 0; i < N; ++i) {
     z = vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ) + c; // z = z^2 + c
     if( dot(z,z)>(B*B) ) break; // break if z is too high
     n += 1.0;
   }
   return (n == 200.0)? .0: n/float(N);
}

vec3 image(vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy) * 2.0 - 1.0; // remap uv to [-1, 1]
    uv.x *= iResolution.x / iResolution.y;

    // calculate and apply zoom
    float zoom = .62 + .38*cos(iTime/PERIOD);
    zoom = pow(zoom, 7.0);
    vec2 c = vec2(-.745,.186) + uv*zoom;

    float f = mandelbrot(c)*2.0 - .5;

    // calculate color
    vec3 col = mix(vec3(.81,.06,.13), vec3(.80,.40,.0), f*f);

    return (f*f*(8.0 - 5.0*f))*col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	// anti-aliasing
	fragColor = vec4(image(fragCoord.xy + vec2(0,0)), 1.0f);
	fragColor.rgb += image(fragCoord.xy + vec2(.5,.0));
	fragColor.rgb += image(fragCoord.xy + vec2(.0,.5));
	fragColor.rgb += image(fragCoord.xy + vec2(.5,.5));
    fragColor.rgb += image(fragCoord.xy + vec2(.25,.25));
	fragColor.rgb += image(fragCoord.xy + vec2(.75,.25));
	fragColor.rgb += image(fragCoord.xy + vec2(.25,.75));
	fragColor.rgb += image(fragCoord.xy + vec2(.75,.75));
	fragColor.rgb /= 8.0;
}

