// Plot Spherical Harmonics (in the simplest way possible on GPU)

/*** math heavy part for spherical harmonics ***/

//#define PI 3.14159265359
#define SQRT2PI 2.506628274631

// factorial
float fac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i--)
        res *= float(i);
    return res;
}

// double factorial
float dfac(int n) {
    float res = 1.0;
    for (int i = n; i > 1; i-=2)
        res *= float(i);
    return res;
}

// fac(l-m)/fac(l+m) but more stable
float fac2(int l, int m) {
    int am = abs(m);
    if (am > l)
        return 0.0;
    float res = 1.0;
    for (int i = max(l-am+1,2); i <= l+am; i++)
        res *= float(i);
    if (m < 0)
        return res;
    return 1.0 / res;
}

// complex exponential
vec2 cexp(vec2 c) {
    return exp(c.x)*vec2(cos(c.y), sin(c.y));
}

// complex multiplication
vec2 cmul(vec2 a, vec2 b) {
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}

// complex conjugation
vec2 conj(vec2 c) { return vec2(c.x, -c.y); }

// complex/real magnitude squared
float sqr(float x) { return x*x; }
float sqr(vec2 x) { return dot(x,x); }

// associated legendre polynomials
float legendre_poly(float x, int l, int m) {
    if (l < abs(m))
        return 0.0;
    if (l == 0)
        return 1.0;
    float mul = m >= 0 ? 1.0 : float((~m&1)*2-1)*fac2(l,m);
    m = abs(m);
    // recursive calculation of legendre polynomial
    float lp1 = 0.0;
    float lp2 = float((~m&1)*2-1)*dfac(2*m-1)*pow(1.0-x*x, float(m)/2.0);
    for (int i = m+1; i <= l; i++) {
        float lp = (x*float(2*i-1)*lp2 - float(i+m-1)*lp1)/float(i-m);
        lp1 = lp2; lp2 = lp;
    }
    return lp2 / mul;
}

// spherical harmonics function
vec2 sphere_harm(float theta, float phi, int l, int m) {
    float abs_value = 1.0/SQRT2PI*sqrt(float(2*l+1)/2.0*fac2(l,m))
                        *legendre_poly(cos(theta), l, m);
    return cexp(vec2(0.0,float(m)*phi))*abs_value;
}

/*** now all the rendering ***/

vec3 rotateX(vec3 pos, float angle) {
    return vec3(pos.x, cmul(pos.yz, cexp(vec2(0.,-angle))));
}

vec3 rotate(vec3 pos, float phi, float theta) {
    return rotateX(rotateX(pos, theta).yzx, phi).zxy;
}

vec3 colormap(float v) {
    return vec3(1.0) - smoothstep(0., 1., sqrt(abs(v)))*(v > 0.0 ? vec3(1,1,0) : vec3(0,1,1));
}

vec3 sphere(vec2 uv, float phi, float theta, int l, int m) {
    if (length(uv) < 1.0 && abs(m) <= l) {
        vec3 pos = vec3(uv, sqrt(1.0-dot(uv,uv)));
        float z = pos.z;

        // rotate sphere
        pos = rotate(pos, phi, theta);

        // get spherical coordinates
        float sin_theta = length(pos.xz);
        float ball_phi = sin_theta > 0.0 ? atan(pos.x, pos.z) : 0.0;
        float ball_theta = atan(sin_theta, pos.y);

        // evaluate spherical harmonics
        float off = m == 0 ? 0.0 : iTime/float(m);
        vec2 sph_harm = sphere_harm(ball_theta, ball_phi + off, l, m);

        // normalization for visual purposes
        sph_harm *= SQRT2PI/2.0;

        // return color mapped real part of the function (.x)
        return colormap(sph_harm.x) * sqrt(sqrt(z));
    }
    return vec3(0.0);
}

vec3 sphere_superposition(vec2 uv, float phi, float theta) {
    if (length(uv) < 1.0) {
        vec3 pos = vec3(uv, sqrt(1.0-dot(uv,uv)));
        float z = pos.z;

        // rotate sphere
        pos = rotate(pos, phi, theta);

        // get spherical coordinates
        float sin_theta = length(pos.xz);
        float ball_phi = sin_theta > 0.0 ? atan(pos.x, pos.z) : 0.0;
        float ball_theta = atan(sin_theta, pos.y);

        // evaluate spherical harmonics
        vec2 tmul = cexp(vec2(0.0,iTime));

        vec2 Y00 = sphere_harm(ball_theta, ball_phi, 0, 0);
        vec2 Y10 = sphere_harm(ball_theta, ball_phi, 1, 0);
        vec2 Y11 = sphere_harm(ball_theta, ball_phi, 1, 1);
        vec2 Y1n1 = sphere_harm(ball_theta, ball_phi, 1, -1);

        vec2 s = Y00; // s orbit
        vec2 px = 1.0/sqrt(2.0)*(Y11+conj(Y11)); // px orbit
        vec2 py = -cmul(vec2(0,1),1.0/sqrt(2.0)*(Y11-conj(Y11))); // py orbit
        vec2 pz = Y10; // pz orbit

        vec2 sph_harm = cmul(tmul, 0.25*(s + px + py + pz)); // sp3 hybrid
        //vec2 sph_harm = cmul(tmul, (Y11 + Y1n1) / sqrt(2.0));
        //vec2 sph_harm = vec2(sqr(Y10) + sqr(Y11) + sqr(Y1n1)) / 3.0 - sqr(Y00); // zero

        // normalization for visual purposes
        sph_harm *= SQRT2PI/2.0;

        // return color mapped real part of the function (.x)
        return colormap(sph_harm.x) * sqrt(sqrt(z));
    }
    return vec3(0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 m = iMouse.xy/iResolution.xy;

    // This value selects the visible region
    float zoom = 5.0;
    vec2 move = iMouse.z > 0.0 ? vec2(2.0, 3.0)*(m-vec2(0.8, 0.5)) : vec2(0.0);
    vec2 uv2 = (uv - move - vec2(0.0, 0.5))*zoom-vec2(0.5,0.0);
    uv = uv * 2.0 - 1.0;

    m = m * 2.0 - 1.0;

	m.x *= iResolution.x/iResolution.y;
    uv.x *= iResolution.x/iResolution.y;
    uv2.x *= iResolution.x/iResolution.y;

    // draw grid of all spherical harmonics
    ivec2 cell = ivec2(floor(uv2 + 0.5));
    vec2 cell_local = fract(uv2 + 0.5) * 2.0 - 1.0;
    vec3 col = sphere(cell_local, m.x, -m.y*0.6, cell.x, cell.y);

    // superposition of spherical harmonics for atomic orbits
    //vec3 col = sphere_superposition(uv, m.x, -m.y);

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Thanks Iquizles
float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

// Thanks Iquizles
float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// Thanks Iquizles
float opRepBox( in vec3 p, vec3 b, in vec3 c )
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return sdBox( q, b );
}

// Thanks Iquizles
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// Thanks Iquizles
float sdBoxFrame( vec3 p, vec3 b, float e )
{
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}


// For complex scenes features such as as shadows become more difficult in shadertoy since
// each reflecting / refreacting ray needs to be tested against the rest of the scene,
// it's for this reason that I'm opting to move the entire scene into a single sdf
float sceneSDF(vec3 testPoint)
{
    float cubeSet0Time = iTime;
    float cubeSet1Time = iTime + 3.14159;
    float cubeSet2Time = iTime - 2.0;
    float cubeSet0Y = sin(cubeSet0Time) * 0.25;
    float cubeSet0X = cos(cubeSet0Time);
    float cubeSet1Y = sin(iTime * 0.7 + 3.14159) * 0.25;
    float cubeSet2Y = sin(cubeSet2Time) * 0.25;
    float cubeSet2z = cos(cubeSet2Time);

    float sdfSphere = sdSphere(testPoint - vec3(0.0, 1.0, 0.0), 1.0);
    float frame = sdBoxFrame(testPoint - vec3(0.0, 1.0, 0.0), vec3(4.0, 4.0, 4.0), 0.2);
    float sdfCubeSet0 = opRepBox(testPoint + vec3(cubeSet0X, cubeSet0Y, 0.0),vec3(0.5, 0.5, 0.5), vec3(2.0, 0.0, 2.0));
    float sdfCubeSet1 = opRepBox(testPoint + vec3(0.0, cubeSet1Y, 0.0),vec3(0.5, 0.75, 0.5), vec3(2.0, 0.0, 2.0));
    float sdfCubeSet2 = opRepBox(testPoint + vec3(0.0, cubeSet2Y, cubeSet2z),vec3(0.5, 0.5, 0.5), vec3(2.0, 0.0, 2.0));


    float sdf = opSmoothUnion(sdfCubeSet0, sdfCubeSet1, 0.2);
    sdf = opSmoothUnion(sdf, sdfCubeSet2, 0.2);
    sdf = opSmoothUnion(sdf, sdfSphere, 0.5);
    sdf = opSmoothUnion(sdf, frame, 0.5);
    return sdf;
}

// Thanks Iquizles
vec3 calcNormal( in vec3 p ) // for function f(p)
{
    const float h = 0.0001; // replace by an appropriate value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sceneSDF( p + k.xyy*h ) + 
                      k.yyx*sceneSDF( p + k.yyx*h ) + 
                      k.yxy*sceneSDF( p + k.yxy*h ) + 
                      k.xxx*sceneSDF( p + k.xxx*h ) );
}



