bool sphereCast(vec3 rayOrigin, vec3 rayDirection, int iterations, out vec3 hitPosition)
{
    const float epsilon = 0.001;

    vec3 currentPosition = rayOrigin;
    for(int i = 0; i < iterations; ++i)
    {
        float distanceFrom = sceneSDF(currentPosition);
        if(distanceFrom <= epsilon)
        {
            hitPosition = currentPosition;
            return true;
        }

        currentPosition += (rayDirection * distanceFrom);
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 camera_pos = vec3(0.0, 3.0, -7.0);
    vec3 camera_lookat = vec3(0.0, 0.0, 0.0);

    // Animate camera
    camera_pos.x += sin(iTime * 0.3) * 5.0;
    camera_pos.y += sin(iTime * 0.7);

    // Screen
    vec3 camera_z = normalize(camera_lookat - camera_pos);
    vec3 camera_x = normalize(cross(vec3(0.0, 1.0, 0.0), camera_z));
    vec3 camera_y = normalize(cross(camera_z, camera_x));

    float fov_y = 1.0;
    float fov_x = iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from -1 to 1)
    vec2 screen_uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    vec3 pos_on_screen = camera_pos + camera_z + camera_x * fov_x * screen_uv.x + camera_y * fov_y * screen_uv.y;

    vec3 ray_origin = camera_pos;
    vec3 ray_dir = normalize(pos_on_screen - camera_pos);

    vec3 lightPosition1 = vec3(0.0, 3.0, -1.0);
    lightPosition1.x += sin(iTime) * 2.0;
    lightPosition1.z += cos(iTime) * 2.0;

    // Collect our first ray color
    vec3 castHitPosition;// The position in worldspace if the sdf intersection
    if (sphereCast(ray_origin, ray_dir, 64, castHitPosition))
    {
        vec3 lightToHitPostitionDirection = normalize(castHitPosition - lightPosition1);
        // Cast a ray from our lightsource in the direction of the hit point on the sdf
        vec3 lightHitPosition;
        bool lightHit = sphereCast(lightPosition1, lightToHitPostitionDirection, 32, lightHitPosition);

        if(lightHit)
        {
            vec3 normal = calcNormal(castHitPosition);
            float cosAng = dot(-normal, lightToHitPostitionDirection);
            cosAng = clamp(cosAng, 0.0, 1.0);

            // Hit sphere.
            fragColor = vec4(vec3(231.0 / 255.0, 236.0 / 255.0, 134.0 / 255.0) * cosAng, 1.0) * float( (distance(lightHitPosition, castHitPosition) < 0.01));
            fragColor.a = 1.0;
        }
    }



    fragColor.a = 1.0;
}

