	vec3 NEO_Shade = vec3(s(-.5, .3, uv2.y), s(-.2, .5, uv2.y)*.75, s(.0, -1., uv2.y));

	float n = clamp(N(uv2, vec2(-.5, .0)), .0, 1.);
	float e = E(uv2, vec2(.0));
	float o = O(uv2, vec2(.5, .0));

	col += n*NEO_Shade;
