// Distance to an arc of a circle in the L-infinity metric space.
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH

float dist( in vec2 p, in vec2 c )
{
    p = abs(p-c);
    return max(p.x,p.y);
}

float sdArc( in vec2 p, float rb, float w, float an )
{
    vec2 u = vec2(cos(an),sin(an));
    vec2 v = vec2(-u.y,u.x);
    float h = sqrt(rb*rb-w*w);

    // bounding points: arc extremes
    float d = min(dist(p, u*w+h*v),
                  dist(p, u*w-h*v));
    // bounding points: cardinals
    if( -rb*u.x<w ) d = min(d,dist(p,vec2(-rb,0.0)));
    if(  rb*u.x<w ) d = min(d,dist(p,vec2( rb,0.0)));
    if( -rb*u.y<w ) d = min(d,dist(p,vec2(0.0,-rb)));
    if(  rb*u.y<w ) d = min(d,dist(p,vec2(0.0, rb)));

    // circular section
    float b1 = 0.5*(p.x+p.y);
    float b2 = 0.5*(p.x-p.y);
    float c = dot(p,p) - rb*rb;
    float h1 = b1*b1-c*0.5;
    float h2 = b2*b2-c*0.5;
    if( h1>0.0 )
    {
        vec2 t = vec2(-b1,b1)+sqrt(h1);
        if( dot(p+vec2( 1, 1)*t.x,u)<w ) d=min(d,abs(t.x));
        if( dot(p+vec2(-1,-1)*t.y,u)<w ) d=min(d,abs(t.y));
    }
    if( h2>0.0 )
    {
        vec2 t = vec2(-b2,b2)+sqrt(h2);
        if( dot(p+vec2( 1,-1)*t.x,u)<w ) d=min(d,abs(t.x));
        if( dot(p+vec2(-1, 1)*t.y,u)<w ) d=min(d,abs(t.y));
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0*iMouse.xy - iResolution.xy) / iResolution.y;
    p *= 1.5;
    m *= 1.5;

    if( iMouse.x<0.01 ) m=sin(-0.17*iTime*vec2(1.1,1.3)+vec2(0,1));

    float ra = 0.9;
    float w = 0.5 + 0.35*cos(iTime);
    float an = iTime*0.7;


    float d = sdArc(p, ra, w, an);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    d = sdArc(m, ra, w, an);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.015, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.015, length(p-m)-0.015));

	fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = vec4(.0);

	vec2 res = iResolution.xy;


	//------ create polar coordinates -----

	vec2 nUV = (uv - .5) * res / res.y;

	vec2 V = normalize(nUV);

	vec2 refV = vec2(1., 0.);

	float cosA = dot(refV, V);

	float alpha = acos(cosA);

	alpha = alpha * step(.0, nUV.y) + (radians(360.) - alpha) * step(nUV.y, .0); // full 2PI circle

	//--------------------------------------

	float alphaP = alpha * 2. + length(nUV) * 7. * (1. + sin(iTime / 3.) * .4);
	float alphaM = alpha * 3. - length(nUV) * 5. * (1. + cos(iTime / 4.) * .5);

	float edge = cos(alphaP * 3. - iTime * 7.) * sin(alphaM * 3. + iTime * 5.);

	edge += 1.;

	edge /= 1.;

	edge *= length(nUV)  + .1;

	vec3 rainbow;

	rainbow.r = (sin(iTime / 2. + edge * 16. + radians(360.) * 0. / 360.) + 1.) / 2.;
	rainbow.g = (sin(iTime / 3. + edge * 16. + radians(360.) * 120. / 360.) + 1.) / 2.;
	rainbow.b = (sin(iTime / 4. + edge * 16. + radians(360.) * 240. / 360.) + 1.) / 2.;

	color.rgb = rainbow * edge;

	color.a = 1.;

    // Output to screen
    fragColor = color;
}

#define lofi(i,j) (floor((i)/(j))*(j))
#define fs(i) (fract(sin((i)*114.514)*1919.810))

const float PI=acos(-1.);
const float TAU=PI*2.;

float time;
float seed;

float random(){
  seed=fs(seed+1.);
  return seed;
}

mat2 r2d(float t){
  return mat2(cos(t),sin(t),-sin(t),cos(t));
}

mat3 orthBas(vec3 z){
  z=normalize(z);
  vec3 x=normalize(cross(abs(z.y)>.99?vec3(0,0,1):vec3(0,1,0),z));
  return mat3(x,cross(z,x),z);
}

vec3 randomSphere(){
  float phi=TAU*random();
  float theta=acos(random()*2.-1.);
  return vec3(cos(phi)*sin(theta),sin(phi)*sin(theta),cos(theta));
}

vec3 sampleLambert(vec3 n){
  float phi=TAU*random();
  float cosTheta=random();
  cosTheta=cos(asin(sqrt(cosTheta)));
  float sinTheta=sqrt(1.0-sqrt(cosTheta*cosTheta));
  return orthBas(n)*vec3(
    cos(phi)*sinTheta,
    sin(phi)*sinTheta,
    cosTheta
  );
}

vec3 randomHemisphere(vec3 n){
  vec3 d=randomSphere();

  return dot(d,n)<.0?-d:d;
}

float sdbox(vec3 p,vec3 s){
  vec3 d=abs(p)-s;
  return min(0.,max(d.x,max(d.y,d.z)))+length(max(d,vec3(0.)));
}

vec3 ifs(vec3 p,vec3 r,vec3 s){
  mat3 b=orthBas(r);
  for(int i=0;i<7;i++){
    p.xyz=p.zyx;
    s*=b;
    s*=0.58;
    p=abs(p)-abs(s);
    p.xy=p.x<p.y?p.yx:p.xy;
    p.yz=p.y<p.z?p.zy:p.yz;
    p.zx=p.z<p.x?p.xz:p.zx;
  }
  return p;
}

float ease(float t){
  return 0.5+0.5*cos(PI*exp(-t*5.0));
}

vec4 map1(vec3 p){
  vec3 pt=p;
  pt.zx=r2d(0.1*time)*pt.zx;
  vec3 cell=lofi(pt+1.0,2.0);
  pt-=cell;
  float w=abs(pt.x+pt.y+pt.z);
  float ph=time+step(0.0,sign(p.y))*0.5;
  ph*=140.0/120.0;
  vec3 rot=2.0+mix(
    fs(vec3(.7,.5,-.3)+sign(p.y)+floor(ph)),
    fs(vec3(.7,.5,-.3)+sign(p.y)+floor(ph+1.0)),
    ease(fract(ph))
  );
  vec3 s=1.0+1.0*mix(
    fs(vec3(.1,.5,.7)+sign(p.y)+floor(ph)),
    fs(vec3(.1,.5,.7)+sign(p.y)+floor(ph+1.0)),
    ease(fract(ph))
  );
  pt=ifs(pt,rot,s);
  pt=mod(pt-0.2,0.4)-0.2;
  float d=sdbox(pt,vec3(.05));
  d=max(d,0.2-abs(p.y));
  //d=min(d,1.0-abs(p.y));
  return vec4(d,1,min(abs(pt.x),abs(pt.z)),w);
}

vec4 map(vec3 p){
  vec4 isect=map1(p);
  //vec4 isectb=map2(p);
  //isect=isectb.x<isect.x?isectb:isect;
  return isect;
}

vec3 nmap1(vec3 p,vec2 d){
  return normalize(vec3(
    map1(p+d.yxx).x-map1(p-d.yxx).x,
    map1(p+d.xyx).x-map1(p-d.xyx).x,
    map1(p+d.xxy).x-map1(p-d.xxy).x
  ));
}

float aomap(vec3 p,vec3 n){
  float accum=0.;
  for(int i=0;i<20;i++){
    vec3 pt=p+sampleLambert(n)*(0.1+0.5*random()*random());
    accum+=smoothstep(0.0,-0.01,map(pt).x)/20.;
  }
  return 1.0-sqrt(2.*accum);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  vec2 p=uv*2.-1.;
  p.x*=iResolution.x/iResolution.y;

  seed=10.0*texture(iChannel0,4.0*uv).x;
  seed+=fract(iTime);

  vec3 col=vec3(0);
  vec3 colRem=vec3(1);

  time=iTime;
  time+=0.02*random();

  vec3 rd=normalize(vec3(p,-1.+0.5*length(p)));
  rd.xy=r2d(0.1*iTime)*rd.xy;
  vec3 ro=vec3(0,0,5);

  for(int iR=0;iR<2;iR++){
    float rl=1E-2;
    vec3 rp=ro+rd*rl;
    vec4 isect;

    for(int i=0;i<96;i++){
      isect=map(rp);
      rl+=.7*isect.x;
      rp=ro+rd*rl;

      if(abs(isect.x)<1E-4){break;}
    }

    float fog=exp(-0.02*max(rl-0.0,0.0));
    col+=colRem*(1.0-fog)*10.0*vec3(0.8,1.1,1.8);

    vec3 n=nmap1(rp,vec2(0,1E-4));
    float f=1.0-max(0.0,dot(rd,-n));
    f=pow(f,5.0);
    col+=colRem*f*vec3(0.8,1.1,1.8);

    if(abs(isect.x)<1E-3){
      if(isect.y==1.){
        float ao=1.0;//aomap(rp,n);
        float edge=smoothstep(0.001,0.0005,isect.z);
        col+=colRem*vec3(0.1)*(1.0-edge)*fog*vec3(ao);
        col+=colRem*4.0*vec3(.2,.8,2.1)*fog*edge*exp(-5.0*fract(isect.w+time));
        colRem*=0.1+0.9*f;

        ro=rp+n*1E-2;
        rd=reflect(rd,n);
        rd=normalize(rd+0.04*randomSphere());
      }
    }else{
      break;
    }
  }

  col=pow(col,vec3(.4545));
