float GetWhiteNoise(float x, float y) {
    float nx = cos(x * 1759710525.0f + 1000000.0f);
    float ny = sin(y * 1677591740.0f + 1000000.0f);
    return (nx + ny) / 2.0f;
}

float GetSmoothNoise(float x, float y) {
    float xp = floor(x);
    float yp = floor(y);

    float bl = GetWhiteNoise(xp, yp);
    float tl = GetWhiteNoise(xp, yp + 1.0f);
    float br = GetWhiteNoise(xp + 1.0f, yp);
    float tr = GetWhiteNoise(xp + 1.0f, yp + 1.0f);

    float b = mix(bl, br, x - xp);
    float t = mix(tl, tr, x - xp);
    float n = mix(b, t, y - yp);

    return n;
}

float GetHeight(vec2 pos) {
    return GetSmoothNoise(pos.x / 10.0f, pos.y / 10.0f) * 10.0f;
}

float GetSDF(vec3 test_pos) {
    //return distance(test_pos, sphere) - radius;
    return test_pos.y - (GetHeight(test_pos.xz));
}

vec2 BoxIntersection(vec3 origin, vec3 box_pos, vec3 dir, float d) {

    vec3 box_min = floor(box_pos * d) / d;
    vec3 box_max = box_min + vec3(1.0f);

    vec3 tMin = (box_min - origin) / dir;
    vec3 tMax = (box_max - origin) / dir;
    vec3 t1 = min(tMin, tMax);
    vec3 t2 = max(tMin, tMax);

    float tNear = max(max(t1.x, t1.y), t1.z);
    float tFar = min(min(t2.x, t2.y), t2.z);

    return vec2(tNear, tFar);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float deg_to_rad = 3.14f / 180.0f;

    if (last_mouse.x == -1.0f) {
        last_mouse = vec2(iMouse.x, iMouse.y);
    }

    vec3 sun = vec3(cos(iTime), -sin(iTime), 0);
    sun = vec3(0.0f, -1.0f, 1.0f);
    sun = normalize(sun);

    vec3 pos = vec3(iTime, 15, 0);
    vec3 look = vec3(fragCoord.x - iResolution.x / 2.0f, fragCoord.y - iResolution.y / 2.0f, length(iResolution));

    look = normalize(look);

    int iterations = 100;

    vec3 test_pos = vec3(pos.x, pos.y, pos.z);
    vec3 vox_pos = floor(test_pos);

    bool hit = false;
    bool sun_hit = false;

    float lambda = 0.05f;

    float dist_mul = 1.0f;

    for (int i = 0; i < iterations; i++) {

        float sdf = GetSDF(floor(vox_pos * dist_mul) / dist_mul);

        if (sdf <= lambda) {
            hit = true;
            break;
        }

        vec2 intersect = BoxIntersection(pos, vox_pos + look * sdf * 0.75f, look, dist_mul);

        vox_pos = pos + look * (intersect.y + lambda);

    }

    vec3 cur_pos = floor(vec3(vox_pos.x, vox_pos.y, vox_pos.z));
    float height = GetHeight(cur_pos.xz) / 10.0f;

    vec3 stpos = vec3(vox_pos.x, vox_pos.y, vox_pos.z);
    vox_pos -= sun * 0.4f;

    for (int i = 0; i < iterations / 2; i++) {
        float sdf = GetSDF(floor(vox_pos * dist_mul) / dist_mul);

        if (sdf < 0.0f) {
            sun_hit = true;
            break;
        }

        vec2 intersect = BoxIntersection(stpos, vox_pos - sun * sdf * 0.75f, -sun, dist_mul);

        vox_pos = stpos - sun * (intersect.y + lambda);
    }

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;



    // Time varying pixel color
    vec3 col = vec3(0);

    if (hit) {

        col = vec3(1, 0.5f, 0.25f);
        if (cur_pos.y >= height) col = vec3(0.0f, 1.0f, 0.0f);
        if (sun_hit) col *= 0.5f;
    }

    // Output to screen
    fragColor = vec4(col,1.0);

    last_mouse = vec2(iMouse.x, iMouse.y);
}

