//thx IQ
float sdSphere(vec3 p, float s) {
  return length(p)-s;
}

float sdRoundBox(vec3 p, vec3 b, float r) {
  vec3 q = abs(p) - b;
  return length(max(q, 0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdPlane(vec3 p, vec3 n) {
    return dot(p, n);
}

vec2 getDistance(vec3 p) {

    vec2 p1 = vec2(sdPlane(p, normalize(vec3(0.0, 1.0, 0.0))), 4);
    vec2 res = vec2(.0);

    vec3 s1t = vec3(-.0, .15,  0.0);
    vec3 s2t = vec3(-.3, .2, -0.3);

    vec3 b1t = vec3( .3, .2,  0.3);
    vec3 b2t = vec3( .3, .1, -0.3);    

    vec2 s1 = vec2(sdSphere(p - s1t, 0.1), 0);
    vec2 s2 = vec2(sdSphere(p - s2t, 0.13), 1);

    vec3 b1p = p - b1t; b1p.xz *= rotate(1.1*iTime); b1p.yz *= rotate(-0.1*iTime);
    vec2 b1 = vec2(sdRoundBox(b1p, vec3(.1), 0.01), 3);

    vec3 b2p = p - b2t; b2p.xz *= rotate(-4.1*iTime);
    vec2 b2 = vec2(sdRoundBox(b2p, vec3(.1, 0.02, .05), 0.01), 3);

#ifdef USE_BRANCHING
    vec2 s = s1;
    if (s2.x < s.x) s = s2;

    vec2 b = b1;
    if (b2.x < b.x) b = b2;

    res = s;
    if (b.x < res.x) res = b;
    if (p1.x < res.x) res = p1;

#else
    float sd = min(s1.x, s2.x);
    float sm = s1.y*step(s1.x, sd + MIN_DISTANCE) + s2.y*step(s2.x, sd + MIN_DISTANCE);

    float bd = min(b1.x, b2.x);
    float bm = b1.y*step(b1.x, bd + MIN_DISTANCE) + b2.y*step(s2.x, bd + MIN_DISTANCE);

    float d1 = min(sd, bd);
    float m1 = sm*step(sd, d1+MIN_DISTANCE) + bm*step(bd, d1+MIN_DISTANCE);

    res.x = min(p1.x, d1);
    res.y = p1.y*step(p1.x, res.x+MIN_DISTANCE) + m1*step(d1, res.x+MIN_DISTANCE);

#endif

    return res;
}

float castRay(vec3 ro, vec3 rd) {
    float d = .0;
    for (int i=0; i<MAX_STEP; i++) {
        vec3 p = ro + d*rd;
        float ds = getDistance(p).x;
        d += ds;
        if (d > MAX_DISTANCE || ds < MIN_DISTANCE) break;
    }
    return d;
}

vec3 getNormal(vec3 p, float d) {
    vec3 v = vec3(MIN_DISTANCE, .0, .0);
    return normalize(d - vec3(
        getDistance(p - v.xyy).x,
        getDistance(p - v.yxy).x,
        getDistance(p - v.yyx).x
    ));
}

vec3 getLight(vec3 p, vec3 lightPos, vec3 lightCol) {
    vec3 col = vec3(.8, .82, 1.0);

    vec2 dm = getDistance(p);
    if (dm.x < MAX_VISIBILITY) {
        int mat = int(dm.y);
        // ambient
        vec3 ambColor = ambient * skyColor;
        // diffuse
        vec3 l = normalize(lightPos - p);
        vec3 n = getNormal(p, dm.x);
        float diff = clamp(dot(l, n), 0., 1.);
        vec3 diffColor = clamp(vec3(lightCol) * 0.8 * diff, .0, .5);
        // specular
        vec3 lightReflect = reflect(-l, n);
        float sf = max(dot(normalize(p), lightReflect), .0);
        float spec = pow(sf, 20.0);
        vec3 specColor = vec3(lightCol * 1.2 * spec);
        // cast shadow
        float sd = castRay(p + 2.*n*MIN_DISTANCE, l);
        //float falloff = 10.*(1.0 - clamp(length(lightDir - p), .0, 1.));
        float sh = 1.;
        if (sd < MAX_DISTANCE) sh = clamp(4.*sd, .0, 1.);

        col = colors[mat] * (ambColor + sh*(clamp(diffColor + specColor, .0, 1.)));
        if (mat == 4) {
            col *= .5 + .5*step(.5, fract(2.*p.x + .5*step(.5, fract(2.*p.z))));
        }
    }

    return clamp(col, .0, 1.);
}

vec3 render(vec3 p) {
    vec3 col  = getLight(p, lights[0], vec3(.6, .4, .4));
         col += getLight(p, lights[1], vec3(.0, .6, .0));
         col += getLight(p, lights[2], vec3(.0, .0, .7));
    return col;    
}

