void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;

    //fisheye update 2021 06 17
    uv = normalize(uv) * tan(asin(length(uv) * 0.9));
    //move the world with time
    uv.x+=iTime/10.;

    //prepare a vector for coordinate subsystem updates

    //scale world by 4 to see more stuff
    vec2 newST = uv*5.;
    //get init ID for quads
    vec2 newID = floor(newST);
    //get the decimal part and shift it to -0.5 to 0.5 (not super nessesary)
    vec2 newCoor = fract(newST)-0.5;



    //7 levels
    //At each level you partition the space into for subspaces
    //each with coord -0.5 to 0.5 for x and y
    //you get an ID for each quad using floor
    //send the ID to rand and get a random number between 0 and 1
    //if the rand number is less than half then end at this level and
    //move on to colouring otherwise keep going.
    for(float k = 1.; k < 8.; k++){

        //scaled up pixel coord X2 each time through loop
        newST = newST*2.;
        //get 2d vector id for this pixel's 1x1 block
        newID = floor(newST);
        //get the -0.5 - 0.5 coord of this pixel for quad partitioning
        newCoor = fract(newST)-0.5;

        //start by checking that the whole quadrant should be partitioned
        float rnd = rand(newID);
        //if not then break out of the loop and move to coloring
        if(step(0.5, rnd) < 0.5){
            break;
            }

    }
    //black as init color
    vec3 col = vec3(0.);
    //thickness for the black borders around each square
    float th = 0.05;

    //decide with the quad should be colored at all
    //newID is left from the last reached level
    float rnd = rand(newID);
    //I check if it's less than 0.2 so I am basically leaving
    //lot's of squares not colored but mostly in lower levels.
    //For some reason I needed to push the threshhold way down to even
    //affect the top layer quads.
    if(rnd < 0.2){

    //calculate a color using the intro color palete with a shift based on the 
    //random value from each quad ID
    vec3 pallete = 0.5 + 0.5*cos(vec3(rnd)+vec3(0,2,4)/2.+rnd*1000.+iTime);

    //cut out black borders on x axis
    float f = 1.0-step(0.5-th, newCoor.x)+step(-0.5+th, newCoor.x);
    //mix the black in
    col = mix(pallete, vec3(0.), f);
    //cut out black borders on y axis
    f = 1.0-step(0.5-th,newCoor.y)+step(-0.5+th,newCoor.y);
    //mix the black in
    col = mix(col, vec3(0.), f);
    }

    //you could delete the else statement and be fine it's just there to
    //show the logic
    else{

    //y'know!
    col = vec3(0.);

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 hash( vec2 p ) {
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

    //The output must be between 0.0 and 1.0
	return -1.0 + 2.0*fract(sin(p) * 43758.5453123);
}

float noise( in vec2 p ) { //Simplex noise
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y) * K1 );

    vec2 a = p - i + (i.x+i.y) * K2;
    vec2 o = step(a.yx,a.xy);
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

#define OCTAVES 6
float fbm (in vec2 st) {

    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;


    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.; //lacunarity: steps in which we increment de frequencies
        amplitude *= .5; //gain: decreases the amplitude
    }
    return value * 0.5 + 0.5;
}

