// a Light is defined by a location and a color
struct Light {
    vec3 location;
    vec3 color;
};

// Sphere is defined by a center and radius and material: color
struct Sphere {
	float radius;
	vec3 center;
    vec3 color;
};

// Ray is define by an origin point and a direction vector
struct Ray {
    vec3 origin;
    vec3 direction;
};


// Intersection code for Ray-Sphere    
float raySphereIntersect(in Ray ray, in Sphere sphere) {

    vec3 rayToSphere = ray.origin - sphere.center;
    float b = dot(rayToSphere, ray.direction);
    float c = dot(rayToSphere, rayToSphere) - (sphere.radius * sphere.radius);
	float disc = b*b - c;
    float t = MAXX; // initialize to a large number
    if (disc > 0.0) {
        t = -b - sqrt(disc);
        if (t > 0.00001) {
            return t;
        }
        t = -b + sqrt(disc);
        if (t > 0.00001) {
            return t;
        }  
    }
    return t;
}


const int NUM_SPHERE = 43;
Sphere spheres[NUM_SPHERE];
Light  lights[1];

float theta = 0.0;

void rotateSceneTenDegrees(int f) {
    theta += float(f)/5.0 * 3.1415/45.0;
    //theta = 0.0;
    for (int i=0; i<6; i++) {
        // rotate each child sphere about y-axis 
        // through the center of the large one
        vec3 v1 = spheres[i+1].center - spheres[0].center;
        vec3 axis = normalize(vec3(0.0, spheres[0].radius, 0.0));
        vec3 newv = v1*cos(theta) + cross(axis, v1)* sin(theta) + axis*dot(axis, v1)*(1.0-cos(theta));
        spheres[i+1].center = newv + spheres[0].center;
    }
}

int addSixSpheres(Sphere parentSphere, int count) {
    float radius = parentSphere.radius;
    vec3 center = parentSphere.center;
    float childRadius = radius/2.8;
    spheres[++count] = Sphere(childRadius, vec3(center.x, center.y+radius+childRadius-0.1, center.z), vec3(random(), random(), random()));
    spheres[++count] = Sphere(childRadius, vec3(center.x, center.y-radius-childRadius+0.1, center.z), vec3(random(), random(), random()));
    spheres[++count] = Sphere(childRadius, vec3(center.x+radius+childRadius-0.1, center.y, center.z), vec3(random(), random(), random()));
    spheres[++count] = Sphere(childRadius, vec3(center.x-radius-childRadius+0.1, center.y, center.z), vec3(random(), random(), random()));
    spheres[++count] = Sphere(childRadius, vec3(center.x, center.y, center.z+radius+childRadius-0.1), vec3(random(), random(), random()));
    spheres[++count] = Sphere(childRadius, vec3(center.x, center.y, center.z-radius-childRadius+0.1), vec3(random(), random(), random()));
    return count;
}

void makeScene(int f) {
    float radius=0.8;
    vec3 center = vec3(0, 0, -6); 
    vec3 color = vec3(random(), random(), random());
    int count = -1;
    spheres[++count] = Sphere(radius, center, color);
    count = addSixSpheres(spheres[0], count);
    count = addSixSpheres(spheres[1], count);
    count = addSixSpheres(spheres[2], count);
    count = addSixSpheres(spheres[3], count);
    count = addSixSpheres(spheres[4], count);
    count = addSixSpheres(spheres[5], count);
    count = addSixSpheres(spheres[6], count);

    lights[0] = Light(vec3(0.0, 0.0, 0.0), vec3(1, 1, 1));
    rotateSceneTenDegrees(f);   
}



int numSamples = 2;


/* The main entry point:
   * This is called for every pixel on the screen 
*/
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

   // fragCoord ranges from 
   //   in x: 0.5 to iResolution.x-0.5
   //   in y: 0.5 to iResolution.y-0.5
   // pixel (0,0) is at the bottom left corner
   if (iFrame==0) theta = 0.0;

   makeScene(iFrame);

   vec3 rayOrigin = vec3(0.0, 0.0, 0.0);

   float screenDepth = -2.0;

   float width = iResolution.x;
   float height = iResolution.y; 

   vec3 samp = vec3(0, 0, 0);
   seed = 0.0;
   for (int i=0; i<numSamples; i++) {
       float x = fragCoord.x + random() - 0.5;
       float y = fragCoord.y + random() - 0.5;


       // map (0.5, w-0.5) to (-1, 1)
       // and (0.5, h-0.5) to (-1, 1)
       x = (x/width)*2.0 - 1.0;
       y = (y/height)*2.0 - 1.0;

       // account for the non-square window
       float aspectRatio = width/height;
       y = y/aspectRatio;

       vec3 rayDirection = normalize(vec3(x, y, screenDepth));

       Ray ray = Ray(rayOrigin, rayDirection);
       float t = MAXX;
       int iSphere = -1;
       float minT = MAXX;
       for (int i=0; i < NUM_SPHERE; i++) {
           Sphere sphere = spheres[i];

           t = raySphereIntersect(ray, sphere);

           if (t < minT) {
               minT = t;
               iSphere = i;
           }
       }
       if (iSphere > -1 && minT < MAXX) {
           Sphere sphere = spheres[iSphere];
           vec3 hit = ray.origin + minT*ray.direction;
           vec3 norm = normalize(hit - sphere.center);
           vec3 hitPointNormal = normalize(hit-sphere.center);
           vec3 hitPointToEye = normalize(vec3(0, 0, 0)-hit);
           float angle = dot(hitPointNormal, hitPointToEye);
           samp = samp + angle*sphere.color;

       }
   }
   fragColor = vec4(samp/float(numSamples), 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float unit = 6.0 / iResolution.y; // Approximately pixel size
    float transition = 0.5 + 0.5 * sin(iTime);

    // The grid is complete when skew=0.5 and stretch=sqrt(0.75)
    float skew = 0.5 * transition;
    uv.x /= mix(1.0, sqrt(0.75), transition); // Scale width to form equilateral triangles
    uv.y += skew * uv.x; // Skew vertically

    uv *= 2.0; // Zoom out to see more cells
    uv = fract(uv); // Subdivide into a grid

    float sTriId = step(0.0, uv.x - uv.y); // Upper left of the cell: 0, lower right of the cell: 1

    // Cell coloring show UVs and local IDs
    fragColor = mix(vec4(uv, 0.0, 1.0), vec4(sTriId, 0.0, 1.0 - sTriId, 1.0), transition);
    fragColor.rgb = mix(fragColor.rgb, vec3(1.0), smoothstep(unit, 0.0, abs(uv.y - uv.x)));
}

#define TWO_PI 6.283185307179586

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 8.0;
    vec2 i = floor(uv);
    vec2 f = fract(uv);

   float minDist = 100.0;
   for(int y = -1; y <= 1; y++){
       for(int x = -1; x <= 1; x++){
           vec2 neigbhour = vec2(float(x), float(y));
           vec2 p = hash22(i + neigbhour);

           p = 0.5 + 0.5*sin(iTime + TWO_PI*p);

           p += neigbhour;
           vec2 d = f - p;
           float dist = min(abs(d.x) , abs(d.y));
           minDist = min(minDist, dist);
       }
   }
    vec3 col = vec3(minDist);

    fragColor = vec4(1. - col,1.0);
}

// Filmic tonemapping curve by milesWaugh

const float epsilon = 0.001;
const float focalLength = 1.;
const float renderDist = 85.;
const float pi = 3.141592653589793;

vec3 camera;
float nearestDist;
vec3 ray;

vec3 surfCol;
vec3 surfNormal;
float surfRoughness;
bool updateMat;

float diffuse = 0.;
float specular;
vec3 ambient = 1.*vec3(.2, 0.4, 0.6);

vec3 rayDirection( float focal, vec2 xy ) {
    return (normalize(vec3(xy, focal)));
}

// SDF's

vec4 Sphere( vec3 rayOrigin, vec3 rayDir, vec3 pos, float radius ) {
    vec3 offset = pos - rayOrigin;
    float dot1 = dot(offset, rayDir);
    float dot2 = dot(offset, offset);
    if (dot1 < 0.) {
        return vec4(-1., 0, 0, 0);
    }
    if (dot2 - dot1 * dot1 < radius * radius) {
        float near = sqrt(radius * radius - (dot2 - (dot1 * dot1)));
        return vec4(dot1 - near, (rayOrigin + (rayDir * (dot1 - near))) - pos);
    }
    return vec4(-1., 0, 0, 0);
}

vec4 Plane( vec3 rayOrigin, vec3 rayDir, vec3 normal, float dist ) {
    float dot1 = dot(normal, rayDir);
    float near = (dot(normal, rayOrigin) + dist)/dot1;
    if (!(dot1 == 0.) && near < 0.)
    {
        return vec4(-near, normal);
    }
    return vec4(-1., 0, 0, 0);
}

void addIntersector( vec4 surface, vec3 col, float roughness ) {
    float dist = surface.x;
    if ( dist < nearestDist && dist > 0. ) {
        nearestDist = dist;
        if (updateMat) {
        	surfCol = col;
            surfRoughness = roughness*roughness;
            surfNormal = normalize(surface.yzw);
        }
    }
}

float scene( vec3 rayOrigin, vec3 rayDir)
{

    vec3 sinVector = vec3(1., 0.81, 1.23);

    vec3 colx = vec3(0.25*sin((ray/10.)*sinVector)+0.5);

    addIntersector( Sphere( rayOrigin, rayDir, vec3(0, 0, 6), 1.), vec3(0.4,.2, 0.01), .1 );
    addIntersector( Sphere( rayOrigin, rayDir, vec3(3, 1, 22), 2.), vec3(0.1,0.2, 0.8), .1 );
    addIntersector( Sphere( rayOrigin, rayDir, vec3(10, 7, 100), 8.), vec3(.5,0.03, 0.01), .1 );
    addIntersector( Plane( rayOrigin, rayDir, vec3(0, 1, 0), 1.), vec3(0.1, 0.3, 0.1), 0.1 );

    return nearestDist;
}

vec4 shootRay( vec3 rayDir, vec3 start, float renderDist, bool updateMaterial ) {
    updateMat = updateMaterial;

    nearestDist = renderDist;

    scene(start, rayDir);

    if ( nearestDist < renderDist ) {
        return vec4(1., rayDir * nearestDist + start);
    }
    else {
        return vec4(0., rayDir * nearestDist + start);
    }
}

float GGX (float halfDot, float roughness) {
    float r2 = roughness*roughness;
    float a = halfDot * halfDot * (r2 - 1.) + 1.;
    return r2 / (a * a);
}

void directionalLight(vec3 ray, vec3 rayDir, vec3 lightDir, vec3 surfNormal, float intensity, float lightDist) {
    if ( shootRay(lightDir, ray + (surfNormal * epsilon), lightDist, false).x == 0. ) {
        float lambertian = dot(surfNormal, lightDir);
        diffuse += max(intensity * lambertian, 0.);

        vec3 halfway = normalize(lightDir - rayDir);
        float halfDot = dot(surfNormal, halfway);
    	specular += max(lambertian * intensity * GGX(halfDot, surfRoughness), 0.);

    }
}

void pointLight( vec3 ray, vec3 rayDir, vec3 position, float intensity, vec3 surfNormal ) {
    vec3 lightDir = normalize(position - ray);
    directionalLight( ray, rayDir, lightDir, surfNormal, intensity, distance(position, ray) );
}

void sceneLights(vec3 ray, vec3 rayDir, vec3 surfNormal) {
    pointLight( ray, rayDir, vec3(-10, 50, 20), 1., surfNormal);
    //directionalLight( ray, rayDir, normalize(vec3(-2,2,-1)), surfNormal, 1., 0. );
}

// My implementation of the tonemapping curve by milesWaugh
vec3 filmic(vec3 color) {
    //return color;
    return 1. - (1./(5. * pow(color, vec3(2.)) + 1.));
}

float fresnel(float IOR, float roughness, vec3 surfNormal, vec3 incoming) {
    float F0 = (1. - IOR)/(1. + IOR);
    F0 *= F0;
    return F0 + (1. - roughness)*((1. - F0) * pow(1. - dot(surfNormal, incoming),5.));
}

vec3 lighting(vec3 ray, vec3 rayDir, vec3 surfNormal) {
    diffuse = 0.;
    specular = 0.;

    sceneLights(ray, rayDir, surfNormal);

    float fog = clamp(length(ray - camera)/(renderDist), 0., 1.);
    fog = fog*fog*fog;
    //fog = fog*fog;
    fog = smoothstep(0., 1., fog);

    vec3 finalCol = mix(surfCol * (diffuse + ambient), specular + ambient, fresnel(1.5, surfRoughness, surfNormal, -rayDir));
    finalCol = mix(finalCol, ambient, fog);

    return finalCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float cosTime = cos(iTime/5.);
    cosTime = 6.*-cosTime+6.;
    camera = vec3(.5*cosTime, .6*cosTime+0.3, 5.*cosTime-5.);
    //camera = vec3(0,0,0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - iResolution.xy/2.)/iResolution.x;

    vec3 rayDir = rayDirection( focalLength, uv );

    vec4 rayData = shootRay( rayDir, camera, renderDist, true );

    vec3 col;

    if (rayData.x == 1.) {
    	col = lighting( rayData.yzw, rayDir, surfNormal );
    }
    else {
        col = ambient;
    }

    // Output to screen, filmic tonemapping, and gamma correction.
    fragColor = vec4(pow(filmic(col),vec3(1./2.2)),1.0);
}

