// Result after a bit of random coding on saturday afternoon

#define PI              3.141592654
#define TAU             (2.0*PI)
#define TIME            iTime
#define RESOLUTION      iResolution
#define ROT(a)          mat2(cos(a), sin(a), -sin(a), cos(a))
#define PCOS(x)         (0.5+0.5*cos(x))

#define TOLERANCE       0.00001
#define MAX_RAY_LENGTH  10.0
#define MAX_RAY_MARCHES 50
#define NORM_OFF        0.0001

const vec3  std_gamma  = vec3(2.2);
const float smoothing  = 0.043;

float tanh_approx(float x) {
//  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}

// From: https://stackoverflow.com/a/17897228/418488
vec3 hsv2rgb(vec3 c) {
  const vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 postProcess(vec3 col, vec2 q) {
  col = clamp(col, 0.0, 1.0);
  col = pow(col, 1.0/std_gamma);
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  col *=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

float boxf(vec3 p, vec3 b, float e) {
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.0))+min(max(p.x,max(q.y,q.z)),0.0),
      length(max(vec3(q.x,p.y,q.z),0.0))+min(max(q.x,max(p.y,q.z)),0.0)),
      length(max(vec3(q.x,q.y,p.z),0.0))+min(max(q.x,max(q.y,p.z)),0.0));
}

float pmin(float a, float b, float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float pmax(float a, float b, float k) {
  return -pmin(-a, -b, k);
}

vec3 pmin(vec3 a, vec3 b, float k) {
  vec3 h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 pabs(vec3 a, float k) {
  return -pmin(a, -a, k);
}

vec3 refl(vec3 p, vec3 n) {
  p -= n*pmin(0.0, dot(p, n), smoothing)*2.0;
  return p;
}

float sphered(vec3 ro, vec3 rd, vec4 sph, float dbuffer) {
    float ndbuffer = dbuffer/sph.w;
    vec3  rc = (ro - sph.xyz)/sph.w;

    float b = dot(rd,rc);
    float c = dot(rc,rc) - 1.0;
    float h = b*b - c;
    if( h<0.0 ) return 0.0;
    h = sqrt( h );
    float t1 = -b - h;
    float t2 = -b + h;

    if( t2<0.0 || t1>ndbuffer ) return 0.0;
    t1 = max( t1, 0.0 );
    t2 = min( t2, ndbuffer );

    float i1 = -(c*t1 + b*t1*t1 + t1*t1*t1/3.0);
    float i2 = -(c*t2 + b*t2*t2 + t2*t2*t2/3.0);
    return (i2-i1)*(3.0/4.0);
}

float df(vec3 p) {
  const float zf = 2.5;
  const int mid  = 1;
  const int end  = 3;
  const vec3 n0  = normalize(vec3(-1.0,  1.0,  1.0));
  const vec3 n1  = normalize(vec3( 1.0, -1.0,  1.0));
  const vec3 n2  = normalize(vec3( 1.0,  1.0, -1.0));
  const vec3 off = normalize(vec3(1.0, 1.0, 1.0)); 

  float d = 1E6;
  float z = 1.0;
  vec3  cp = off;

  for (int i = 0; i < mid; ++i) {
    p = pabs(p, smoothing);
    p = refl(p, n0);
    p = refl(p, n1);
    p = refl(p, n2);
    p -= off*0.33;
    p *= zf;
    z *= zf;
  }

  for (int i = mid; i < end; ++i) {
    p = pabs(p, smoothing);
    p = refl(p, n0);
    p = refl(p, n1);
    p = refl(p, n2);
    p -= off*0.24;
    p *= zf;
    z *= zf;

    vec3 pp = p;
    float dd0 = boxf(pp, 0.1*vec3(1.0), 0.0125)-0.0125;
    float dd1 = length(pp)- 0.075;
    float dd = dd0;
    dd = min(dd, dd1);
    dd /= z;
    d = min(d, dd);
  }

  return d;
}

float rayMarch(vec3 ro, vec3 rd, out int iter) {
  float t = 0.0;
  int i = 0;
  for (i = 0; i < MAX_RAY_MARCHES; i++) {
    float d = df(ro + rd*t);
    if (d < TOLERANCE || t > MAX_RAY_LENGTH) break;
    t += d;
  }
  iter = i;
  return t;
}

vec3 normal(vec3 pos) {
  vec2  eps = vec2(NORM_OFF,0.0);
  vec3 nor;
  nor.x = df(pos+eps.xyy) - df(pos-eps.xyy);
  nor.y = df(pos+eps.yxy) - df(pos-eps.yxy);
  nor.z = df(pos+eps.yyx) - df(pos-eps.yyx);
  return normalize(nor);
}

float softShadow(vec3 pos, vec3 ld, float ll, float mint, float k) {
  const float minShadow = 0.25;
  float res = 1.0;
  float t = mint;
  for (int i=0; i<24; i++) {
    float d = df(pos + ld*t);
    res = min(res, k*d/t);
    if (ll <= t) break;
    if(res <= minShadow) break;
    t += max(mint*0.2, d);
  }
  return clamp(res,minShadow,1.0);
}

vec3 render(vec3 ro, vec3 rd) {
  vec3 lightPos = vec3(1.0);
  float alpha   = 0.05*TIME;

  const vec3 skyCol = vec3(0.0);

  int iter    = 0;
  float t     = rayMarch(ro, rd, iter);

  float sr    = 0.4;
  float sd    = sphered(ro, rd, vec4(vec3(0.0), sr), t);

  vec3 gcol   = sd*1.5*vec3(2.0, 1.0, 0.75)*1.0;

  if (t >= MAX_RAY_LENGTH) {
    return gcol;
  }

  vec3 pos    = ro + t*rd;
  vec3 nor    = normal(pos);
  vec3 refl   = reflect(rd, nor);
  float ii    = float(iter)/float(MAX_RAY_MARCHES);
  float ifade = 1.0-tanh_approx(1.25*ii);
  float h     = fract(-1.0*length(pos)+0.1);
  float s     = 0.25;
  float v     = tanh_approx(0.4/(1.0+40.0*sd));
  vec3 color  = hsv2rgb(vec3(h, s, v));

  vec3 lv   = lightPos - pos;
  float ll2 = dot(lv, lv);
  float ll  = sqrt(ll2);
  vec3 ld   = lv / ll;
  float sha = softShadow(pos, ld, ll*0.95, 0.01, 10.0);

  float dm  = 4.0/ll2;
  float dif = pow(max(dot(nor,ld),0.0),2.0)*dm;  
  float spe = pow(max(dot(refl, ld), 0.), 20.);
  float ao  = smoothstep(0.5, 0.1 , ii);
  float l   = mix(0.2, 1.0, dif*sha*ao);

  vec3 col = l*color + 2.0*spe*ao*exp(-20.0*sd)*sha;
//  return vec3(ao);
  return gcol+col*ifade;
}

vec3 effect3d(vec2 p, vec2 q) {
  float z   = TIME;
  vec3 cam  = 1.2*vec3(1.0, 0.5, 0.0);
  float rt  = TAU*TIME/20.0;;
  cam.xy   *= ROT(sin(rt*sqrt(0.5))*0.5+0.0);
  cam.xz   *= ROT(sin(rt)*1.0-0.75);
  vec3 la   = vec3(0.0);
  vec3 dcam = normalize(la - cam);
  vec3 ddcam= vec3(0.0);

  vec3 ro = cam;
  vec3 ww = normalize(dcam);
  vec3 uu = normalize(cross(vec3(0.0,1.0,0.0)+ddcam*2.0, ww ));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

  return render(ro, rd);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 col = effect3d(p, q);

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

vec4 lookup(sampler2D src, float x, float y)
{
	return texture(src, vec2(x / iResolution.x, y / iResolution.y));
}

float rayStrength(vec2 raySource, vec2 rayRefDirection, vec2 coord, float seedA, float seedB, float speed)
{
	vec2 sourceToCoord = coord - raySource;
	float cosAngle = dot(normalize(sourceToCoord), rayRefDirection);

	return clamp(
		(0.45 + 0.15 * sin(cosAngle * seedA + iTime * speed)) +
		(0.3 + 0.2 * cos(-cosAngle * seedB + iTime * speed)),
		0.0, 1.0) *
		clamp((iResolution.x - length(sourceToCoord)) / iResolution.x, 0.5, 1.0);
}

float bubbleStrength(vec2 startPos, vec2 waveOffset, float radius, float speed, vec2 coord)
{
	vec2 curPos = vec2(
		mod(startPos.x + waveOffset.x * 0.5, iResolution.x + radius * 2.0) - radius,
		mod(waveOffset.y - iTime * speed, iResolution.y + radius * 2.0) - radius);
	return 1.0 - smoothstep(0.0, radius, length(coord - curPos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y = 1.0 - uv.y;
	vec2 coord = vec2(fragCoord.x, iResolution.y - fragCoord.y);

	// Calculate the lookup transformation offset for the current fragment
	float offsetX = (0.1112 * iResolution.x * cos(1.44125 * (iTime + uv.y))) + (26.77311 * iTime);
	float offsetY = (0.08447 * iResolution.y * sin(2.14331 * (iTime + uv.x)));

	// Use parallax transformed lookup on the texture layers
	vec4 tex1 = lookup(iChannel0, coord.x + offsetX, coord.y + offsetY);
	vec4 tex2 = lookup(iChannel1, coord.x * 2.0 + offsetX * 1.6, coord.y * 2.0 + offsetY * 1.5);
	vec4 tex3 = lookup(iChannel2, coord.x * 3.0 + offsetX * 1.3, coord.y * 3.0 + offsetY * 1.2);
	vec4 tex4 = lookup(iChannel3, coord.x * 0.5 + offsetX * 0.8, coord.y * 0.5 + offsetY * 0.9);

	// Set the parameters of the sun rays
	vec2 rayPos1 = vec2(iResolution.x * 0.1, iResolution.y * -0.4);
	vec2 rayRefDir1 = normalize(vec2(1.0, -0.116));
	float raySeedA1 = 36.2214;
	float raySeedB1 = 21.11349;
	float raySpeed1 = 1.5;

	vec2 rayPos2 = vec2(iResolution.x * 1.2, iResolution.y * -0.6);
	vec2 rayRefDir2 = normalize(vec2(1.0, 0.241));
	float raySeedA2 = 22.39910;
	float raySeedB2 = 18.0234;
	float raySpeed2 = 1.1;

	// Calculate the colour of the sun rays on the current fragment
	vec4 rays1 =
		vec4(1.2, 1.0, 1.0, 1.0) *
		rayStrength(rayPos1, rayRefDir1, coord, raySeedA1, raySeedB1, raySpeed1);

	vec4 rays2 =
		vec4(1.0, 1.2, 1.0, 1.0) *
		rayStrength(rayPos2, rayRefDir2, coord, raySeedA2, raySeedB2, raySpeed2);


	// Calculate the colours contribution of each bubble on the current fragment.
	float bubbleScale = iResolution.x / 600.0;

	vec4 bubble1 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(0.0, 0.0), vec2(offsetX * 0.2312, 0.0), 20.0 * bubbleScale, 60.0, coord);

	vec4 bubble2 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(40.0, 400.0), vec2(offsetX * -0.06871, offsetY * 0.301), 7.0 * bubbleScale, 25.0, coord);

	vec4 bubble3 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(300.0, 70.0), vec2(offsetX * 0.19832, offsetY * 0.1351), 14.0 * bubbleScale, 45.0, coord);

	vec4 bubble4 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(500.0, 280.0), vec2(offsetX * -0.0993, offsetY * -0.2654), 12.0 * bubbleScale, 32.0, coord);

	vec4 bubble5 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(400.0, 140.0), vec2(offsetX * 0.2231, offsetY * 0.0111), 10.0 * bubbleScale, 28.0, coord);

	vec4 bubble6 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(200.0, 360.0), vec2(offsetX * 0.0693, offsetY * -0.3567), 5.0 * bubbleScale, 12.0, coord);

	vec4 bubble7 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(0.0, 0.0), vec2(offsetX * -0.32301, offsetY * 0.2349), 16.0 * bubbleScale, 51.0, coord);

	vec4 bubble8 =
		vec4(1.0, 1.0, 1.0, 1.0) *
		 bubbleStrength(vec2(130.0, 23.0), vec2(offsetX * 0.1393, offsetY * -0.4013), 8.0 * bubbleScale, 24.0, coord);


	// Blend all of the elements together.
	// This deliberately saturates the colour in places.
	fragColor = 
		tex1 * 0.45 +
		tex2 * 0.25 +
		tex3 * 0.15 +
		tex4 * 0.1 +
		rays1 * 0.5 +
		rays2 * 0.4 +
		bubble1 * 0.25 +
		bubble2 * 0.1 +
		bubble3 * 0.18 +
		bubble4 * 0.13 +
		bubble5 * 0.15 +
		bubble6 * 0.05 +
		bubble7 * 0.12 +
		bubble8 * 0.11;

	// Uncomment this line if you want to see just the rays:
	//fragColor = rays1 * 0.5 + rays2 * 0.5;

	// Attenuate brightness towards the bottom, simulating light-loss due to depth.
	// Give the whole thing a blue-green tinge as well.
	float brightness = 1.0 - (coord.y / iResolution.y);
	fragColor.x *= 0.2 + (brightness * 0.8);
	fragColor.y *= 0.3 + (brightness * 0.7);
	fragColor.z *= 0.4 + (brightness * 0.6);

}

// Desmos graph: https://www.desmos.com/calculator/peu1crmp2n

// Constants
#define EPSILON 1e-3
#define INFINITY 1e20

#define PI 3.14159265359
#define TAU 6.28318530718

// Settings
#define ZOOM 1.0
#define AA 2

// Convenience structs
struct Ray { vec3 pos; vec3 dir; };
struct Light { vec3 pos; vec3 color; };
struct TraceInfo { bool hit; float dist; vec3 pos; vec3 nor; vec3 mat; };

// Axis aligned bbox of a transformed box
// The vector returned gives the upper and lower bounds
// relative to the box's center
vec3 transformedBoxAABB(in vec3 dims, in mat3 mat) {
    vec3 w = mat[0] * dims.x;
    vec3 h = mat[1] * dims.y;
    vec3 d = mat[2] * dims.z;
    vec3 a = w + h, b = w - h;
    return max(max(abs(a + d), abs(a - d)),
               max(abs(b + d), abs(b - d)));
}

// Box intersector adapted from https://www.shadertoy.com/view/ld23DV
bool testAABB(in vec3 pos, in vec3 dims, in Ray ray) {
    ray.pos -= pos;
    vec3 n = ray.pos / ray.dir;
    vec3 k = dims / abs(ray.dir);
    vec3 t1 = -k - n, t2 = k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    return tN < tF && tF > 0.0;
}

void addBox(in vec3 pos, in vec3 dims, in mat3 transform, in vec3 mat, in Ray ray, inout TraceInfo scene) {
    ray.pos -= pos;

    mat3 inv = inverse(transform);
    ray.pos = inv * ray.pos;
    ray.dir = inv * ray.dir;

    vec3 n = ray.pos / ray.dir;
    vec3 k = dims / abs(ray.dir);
    vec3 t1 = -k - n, t2 = k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);

    if (tN > tF || tF < 0.0) tN = -1.0;
    vec3 nor = -sign(ray.dir) * step(t1.yzx, t1.xyz) * step(t1.zxy, t1.xyz);

    if (tN > 0.0 && (scene.hit ? tN < scene.dist : true)) {
        scene.hit = true;
        scene.dist = tN;
        scene.pos = transform * (ray.pos + ray.dir * tN);
        scene.nor = transform * nor;
        scene.mat = mat;
        scene.pos += pos;
    }
}

// Very inefficient
void addCylinder(in vec3 pos, in int align, in float height, in float radius, in vec3 mat, in Ray ray, inout TraceInfo scene) {
    ray.pos -= pos;
    if (align == 0) {
        ray.pos.xy = ray.pos.yx;
        ray.dir.xy = ray.dir.yx;
    }

    if (align == 2) {
        ray.pos.yz = ray.pos.zy;
        ray.dir.yz = ray.dir.zy;
    }

    // Quadratic coefficients
    float a = dot(ray.dir.xz, ray.dir.xz);
    float b = 2.0 * dot(ray.pos.xz, ray.dir.xz);
    float c = dot(ray.pos.xz, ray.pos.xz) - radius * radius;

    // Solve with a plane for the base and top
    float discr = b * b - 4.0 * a * c;
    if (discr > 0.0) {
        float hh = 0.5 * height;
        float tBase = +(hh - ray.pos.y) / ray.dir.y;
        float tTop  = -(hh + ray.pos.y) / ray.dir.y;
        vec4 roots = vec4(-(vec2(-1, 1) * sqrt(discr) + b) / a * 0.5, tBase, tTop);

        float t = INFINITY;
        vec3 hitPos, nor = vec3(0.0, -1.0, 0.0);
        int nClosest;
        for (int n=0; n < 4; n++) {
            vec3 hitCandid = ray.pos + ray.dir * roots[n];
            if (roots[n] > 0.0 && roots[n] < t  && abs(hitCandid.y) < hh + EPSILON && dot(hitCandid.xz, hitCandid.xz) < radius * radius + EPSILON) {
                t = roots[n];
                hitPos = hitCandid;
                nClosest = n;
            }
        }

        if (nClosest < 2) nor = vec3(hitPos.xz / radius, 0.0).xzy; // Sides
        else nor.y = sign(2.5 - float(nClosest)); // Base and top
        if (t > 0.0 && t < INFINITY && (scene.hit ? t < scene.dist : true)) {
            scene.hit = true;
            scene.dist = t;
            scene.pos = hitPos + pos;
            scene.nor = nor;
            scene.mat = mat;
            if (align == 0) {
                scene.pos.xy = scene.pos.yx;
                scene.nor.xy = scene.nor.yx;
            }

            if (align == 2) {
                scene.pos.yz = scene.pos.zy;
                scene.nor.yz = scene.nor.zy;
            }
        }
    }
}

// Main scene
TraceInfo traceScene(in Ray ray) {
    TraceInfo scene = TraceInfo(false, -1.0, vec3(0.0), vec3(0.0), vec3(0.0));

    vec3 dims = vec3(1.0);
    dims = vec3(0.75, 1.5, 0.75);

    float y = iTime; // Yaw
    float p = 0.5;   // Pitch
    float r = iTime; // Roll

    float cy = cos(y), sy = sin(y);
    float cp = cos(p), sp = sin(p);
    float cr = cos(r), sr = sin(r);

    mat3 mat = mat3(cy * cr + sy * sp * sr, cp * sr,  sy * cr - cy * sp * sr,
                    sy * sp * cr - cy * sr, cp * cr, -sy * sr - cy * sp * cr,
                                  -sy * cp,      sp,                 cy * cp);

    mat[0].x += sin(iTime);
    mat[2].y += cos(iTime);

    vec3 bbox = transformedBoxAABB(dims, mat);
    if (testAABB(vec3(0.0), bbox, ray)) addBox(vec3(0.0), dims, mat, vec3(1.0, 0.5, 0.0), ray, scene);

    float width = 2.0 * bbox.x;
    addCylinder(vec3(0.0, -bbox.y, -bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0,  bbox.y, -bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0,  bbox.y,  bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0, -bbox.y,  bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);

    float height = 2.0 * bbox.y;
    addCylinder(vec3(-bbox.x, 0.0, -bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, 0.0, -bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, 0.0,  bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(-bbox.x, 0.0,  bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);

    float depth = 2.0 * bbox.z;
    addCylinder(vec3(-bbox.x, -bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, -bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x,  bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(-bbox.x,  bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);

    return scene;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = ivec2(iMouse.xy) == ivec2(0) ? vec2(0.0) : (iMouse.xy - center) / iResolution.y * PI;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    Light light = Light(vec3(-10.0, 10.0, 10.0), vec3(0.8, 0.2, 0.2));

    // Mouse rotation
    vec2 c = cos(mouse), s = sin(mouse);
    mat2 yaw = mat2(c.x, -s.x, s.x, c.x);
    mat2 pitch = mat2(c.y, -s.y, s.y, c.y);

    // Take multiple subsamples for antialiasing
    float aaStep = 1.0 / float(AA);
    for (float xOffs=0.0; xOffs < 1.0; xOffs += aaStep) {
        for (float yOffs=0.0; yOffs < 1.0; yOffs += aaStep) {
            vec2 uv = (fragCoord + vec2(xOffs, yOffs) - center) / iResolution.y;
            Ray camRay = Ray(vec3(0.0, 0.0, 8.0), normalize(vec3(uv, -ZOOM)));

            // Rotate with mouse
            camRay.pos.yz *= pitch;
            camRay.pos.xz *= yaw;
            camRay.dir.yz *= pitch;
            camRay.dir.xz *= yaw;

            // Render the scene
            TraceInfo scene = traceScene(camRay);
            if (scene.hit) {
                vec3 lightDir = normalize(light.pos - scene.pos);
                float shade = max(0.0, dot(scene.nor, lightDir));
                fragColor.rgb += light.color * scene.mat * shade;
            }

            // Sample the background on a miss
            else fragColor += 1.15 - length(uv);
        }
    }

    // Average and tonemap
    fragColor.rgb = pow(fragColor.rgb / float(AA * AA), vec3(0.75));
}

const vec4 colorPalette = -vec4(0,23,21,0);
const vec4 finalColor = vec4(.0);
const int hueModulus = 30;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = exp(-iTime/10.0)*5.0;
    int recursion = int(iTime*iTime*8.0) + 50, i = 0;
    vec2 c = (fragCoord / iResolution.x - 0.5) * -zoom + -iMouse.xy/iResolution.xy, z = c;
    for(; i <= recursion && length(z) <= 2. ; i++ ) z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
    fragColor =  i - 1 == recursion ? finalColor : .6 + .6 * cos( 6.3 *  (float((i - 1) % hueModulus) / float(hueModulus)) + colorPalette);
}

