vec5 depth( vec3 origin, vec3 direction )
{
    float depth = 0.0;
    float light = 0.0;
    float iterations = 0.0;
    vec2 closestPoint = vec2(cameraFarPlane, 0);


    vec3 b = direction/length(direction);//b = normalized direction
    vec3 p = origin; //point or direction vector times depth

    float d = cameraFarPlane; //Distance

    float didCollide = 1.0;

    for(float i=0.;i<maxSteps;i++)
    {
        p = origin + (b*depth);
        d = DF(p).x;

        iterations++;

        if (d < closestPoint.x) closestPoint.x = d;

        if (d < minDistance || depth > cameraFarPlane)
        {
            if ((d > minDistance))
            {
                didCollide = 0.0;
            }

            break;
        }

        depth += d;

    }

    if (depth < cameraFarPlane)
    {
        light = evaluateLight(p);
    }


    depth /= cameraFarPlane;


    return vec5(vec4(depth, light, iterations/maxSteps, closestPoint.x), didCollide);
}

