/* MONOCHROME PHONG LIGHTING
   DITHERING ALGORITHM BY ALEX CHARLTON 
   http://alex-charlton.com/posts/Dithering_on_the_GPU/
*/

struct {
    vec2 screen;
} globals;

const int indexMatrix4x4[16] = int[](0,  8,  2,  10,
                                     12, 4,  14, 6,
                                     3,  11, 1,  9,
                                     15, 7,  13, 5);

float indexValue() {
    int x = int(globals.screen.x) % 4;
    int y = int(globals.screen.y) % 4;
    return float(indexMatrix4x4[(x + y * 4)]) / 16.0;
}

float dither(float color) {
    float closestColor = (color < 0.5) ? 0. : 1.;
    float secondClosestColor = 1. - closestColor;
    float d = indexValue();
    float distance = abs(closestColor - color);
    return (distance < d) ? closestColor : secondClosestColor;
}

float dist_sphere(vec3 center, float radius, vec3 eye, vec3 dir){
    vec3 oc = eye - center;
    float a = dot(dir, dir);
    float b = 2.0 * dot(oc, dir);
    float c = dot(oc,oc) - radius*radius;
    float d = b*b - 4.*a*c;
    if(d < 0.0){
        return -1.0;
    }
    else{
        float num = -b - sqrt(d);
        if (num > 0.0) {
        return num / (2.0 * a);
    }

    num = -b + sqrt(d);
    if (num > 0.0) {
        return num / (2.0 * a);
    }
    else {
        return -1.;
    }

    }
}

vec3 sphere_normal(vec3 eye, vec3 dir, float dist, vec3 center){
    return normalize((eye + dist * dir) - center);
}


float dist_plane(vec3 center, vec3 normal, vec3 eye, vec3 dir)
{

    float d = dot(normal, dir);
    if (abs(d) > 0.001f)
    {
        float t = dot(normal, (center - eye)) / d;
        if (t >= 0.001f) return t;
    }
    return -1.;
}

bool trace(vec3 eye, vec3 dir, vec3[3] sphere_center, float[3] sphere_radius, vec3 plane_center, vec3 plane_normal){

    for (int i = 0; i < 3; i++){
        float new_depth = dist_sphere(sphere_center[i], sphere_radius[i], eye, dir);
        if (new_depth != -1.) {
            return true;
        }
    }
    if (dist_plane(plane_center, plane_normal, eye, dir) > 0.) return true;
    return false;
}

vec3 raytrace(  vec3 eye, vec3 dir,
                vec3[3] sphere_center, float[3] sphere_radius, 
                vec3 plane_center, vec3 plane_normal, 
                vec3 light_pos, int iteration){

    float bias = 0.01;
    if (iteration < 1) return vec3(0.);
    vec3 col = vec3(0.);
    float depth = 99999.;
    vec3 normal = vec3(0.);    
    bool hit = false;
    int color_index = -1;

    // sphere intersection
    for (int i = 0; i < 3; i++){
        float new_depth = dist_sphere(sphere_center[i], sphere_radius[i], eye, dir);
        if (new_depth != -1. && new_depth < depth) {
            hit = true;
            depth = new_depth;
            normal = sphere_normal(eye, dir, new_depth, sphere_center[i]);
            color_index = i;
        }
    }

    // plane intersection
    float new_depth = dist_plane(plane_center, plane_normal, eye, dir);
        if (new_depth != -1. && new_depth < depth) {
            hit = true;
            depth = new_depth;
            normal = plane_normal;
            color_index = -1;
    }

    if (hit){    
        vec3 hit_point = eye + depth * dir;
        vec3 shadow_point = hit_point + normal * bias;
        vec3 light = vec3(0.);
        vec3 light_dir = light_pos - hit_point;

        // check shadow
        bool in_shadow = trace(shadow_point, light_dir, sphere_center, sphere_radius, plane_center, plane_normal);

        if (color_index == -1) col = vec3(1.);
        else {
            float phong = clamp(dot(normalize(light_pos - hit_point), normal), 0., 1.);
            phong = dither(phong);
            col = vec3(phong);
        }
        col = col * float(!in_shadow);
    }

    return col;
}

