vec3 drawSegmentsOrig(vec2 p, vec2 p1,vec2 p2,vec2 p3,vec2 p4,vec3 col){

    float t = 0.;

    //basic linear interpolation form
    vec2 d1 = p1*t + (1.0-t)*p2;
    vec2 d2 = p2*t + (1.0-t)*p3;
    vec2 d3 = p3*t + (1.0-t)*p4;

    vec2 m1 = d1*t + (1.0-t)*d2; 
    vec2 m2 = d2*t + (1.0-t)*d3; 

    vec2 g = m1*t + (1.0-t)*m2;


    //or 
    //vec2 g = p1*(t*t*t) + p2*3.*(1.-t)*(t*t) + p3*3.*(1.-t)*(t*t) + p4*(1.-t)*(1.-t)*(1.-t);

    //because if you substitute the right side of the d points
    //into the d's in the right side of the m points
    //and did that for the the m points into the g
    //and add like terms you get the above!

    //p1*t*t + (1.0-t)*p2*t + p2*(1.0-t)*t + (1.0-t)*(1.0-t)*p3;
    //p2*t*t + (1.0-t)*p3*t + p3*(1.0-t)*t + (1.0-t)*(1.0-t)*p4;
    //p1*t*t*t + (1.0-t)*p2*t*t + p2*(1.0-t)*t*t + (1.0-t)*(1.0-t)*t*p3;
    //p2*(1.0-t)*t*t + (1.0-t)*(1.0-t)*p3*t + p3*(1.0-t)*(1.0-t)*t + (1.0-t)*(1.0-t)*(1.0-t)*p4;


    float dt = 1./numSegments;

    vec2 prev = g;
    vec2 next;

    col = drawRing(0.01, 0.003, p, p1, col, magikCol*1.5);

    for(float i = 0.; i < numSegments; i++){

        t += dt;
        //basic linear interpolation form
        d1 = p1*t + (1.0-t)*p2;
        d2 = p2*t + (1.0-t)*p3;
        d3 = p3*t + (1.0-t)*p4;

        m1 = d1*t + (1.0-t)*d2;
        m2 = d2*t + (1.0-t)*d3;

        g = m1*t + (1.0-t)*m2;

        //or 
        //g = p1*(t*t*t) + p2*3.*(1.-t)*(t*t) + p3*3.*(1.-t)*(1.-t)*(t) + p4*(1.-t)*(1.-t)*(1.-t);

        next = g;

        col = drawLine(prev,next,p,col,segCol*vec3(t, 0.7, 0.9));
        col = drawRing(segRingRadius, 0.003, p, next, col, segCircleCol);
        prev = next;
    }

    return col;
}
