// Drawing utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

// SDFs
float sdIsosceles(in vec2 p, in float b, in float h) {
    p.x = abs(p.x);
    float q = clamp((b - p.x + p.y) / (b + h), 0.0, 1.0);
    vec2 re = vec2(p.x - b + b * q, p.y - h * q);
    vec2 be = vec2(p.x - b * min(p.x / b, 1.0), p.y);
    return sqrt(min(dot(re, re), dot(be, be))) * sign(p.x + (p.y - h) * b / h * sign(p.y));
}

float sqLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    vec2 toProj = pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return dot(toProj, toProj);
}

float sdVerticalLine(in vec2 p, in float h) {
    return length(vec2(p.x, p.y - h * clamp(p.y / h, 0.0, 1.0)));
}

float sdInfLine(in vec2 p, in vec2 a, in vec2 b) {
    return abs(dot(p - a, normalize(b - a).yx * vec2(-1.0, 1.0)));
}

float sdVectorArrow(in vec2 p, in vec2 v) {
    float m = length(v); vec2 n = v / m;
    p = vec2(dot(p, n.yx * vec2(1.0, -1.0)), dot(p, n));
    return min(sdVerticalLine(p, m) - 0.025, sdIsosceles(p - vec2(0.0, m), 0.1, 0.15));
}

// Parametric equation
vec2 posLimacon(in float t, in float alpha, in float beta) {
    float t2 = 2.0 * t;
    return vec2(cos(t2) + alpha * cos(t),
                sin(t2) +  beta * sin(t));
}

// Intersection
int solveReducedQuartic(in float b, in float c, in float d, in float e, inout vec4 roots) {
    // Depress the quartic to x^4 + px^2 + qx + r by substituting x-b/4a
    // This can be found by substituting x+u and the solving for the value
    // of u that makes the t^3 term go away
    float bb = b * b;
    float p = c - 0.375 * bb;
    float q = d -   0.5 * b * c + 0.125 * bb * b;
    float r = e -  0.25 * b * d +         bb * c / 16.0 - 3.0 * bb * bb / 256.0;
    int n = 0; // Root counter

    // Solve for a root to (t^2)^3 + 2p(t^2)^2 + (p^2 - 4r)(t^2) - q^2 which resolves the
    // system of equations relating the product of two quadratics to the depressed quartic
    float ra =  2.0 * p;
    float rb =  p * p - 4.0 * r;
    float rc = -q * q;

    float ru = ra / 3.0;
    float rp = rb - ra * ru;
    float rq = (ra * ra / 4.5 - rb) * ru + rc;

    float lambda;
    float sp = sqrt(3.0 / abs(rp)), k = 1.5 * rq * sp / rp;
    if (rp * rp * rp + 6.75 * rq * rq > 0.0) {
        if (rp < 0.0) lambda = cosh(acosh(abs(k)) / 3.0) * sign(rq);
        else lambda = sinh(asinh(k) / 3.0);
        lambda /= -sp;
    }

    else lambda = cos(acos(k) / 3.0) / sp;
    lambda = 2.0 * lambda - ru;

    // Solve two quadratics factored from the quartic using the cubic root
    if (lambda < 0.0) return n;
    float t = sqrt(lambda); // Because we solved for t^2 but want t
    float alpha = 2.0 * q / t, beta = lambda + ra;

    float u = 0.25 * b;
    t *= 0.5;

    float z = -alpha - beta;
    if (z > 0.0) {
        z = sqrt(z) * 0.5;
        float h = +t - u;
        roots.xy = vec2(h + z, h - z);
        n += 2;
    }

    float w = +alpha - beta;
    if (w > 0.0) {
        w = sqrt(w) * 0.5;
        float h = -t - u;
        roots.zw = vec2(h + w, h - w);
        if (n == 0) roots.xy = roots.zw;
        n += 2;
    }

    return n;
}

Hit4 iLimacon(in vec2 ro, in vec2 rd, in float alpha, in float beta) {
    Hit4 isect = Hit4(0, vec2[4](vec2(0.0), vec2(0.0), vec2(0.0), vec2(0.0)));

    vec2 uv = normalize(rd);
    float y = ro.x * uv.y - ro.y * uv.x; // Rotate ray so its parallel and we can solve for y only
                                         // instead of a trigonometric system (trig was meant for geometry! not algebra)

    float u = 0.5 * atan(rd.y, rd.x); // Translation in x to reduce
    float s = length(vec2(beta, alpha) * uv); // Amplitude of cosine ("s" for scale)
    float o = atan(beta * uv.x, alpha * uv.y) + u; // Offset of cosine
    float sso = s * sin(o);

    float b = sso;
    float c = 0.25 * s * s - 1.0;
    float d = -sso - 0.5 * y * s * cos(o);
    float e = 0.25 * (y * y - sso * sso);

    vec4 roots;
    isect.nHit = solveReducedQuartic(b, c, d, e, roots);
    for (int n=0; n < isect.nHit; n++) {
        float t = acos(roots[n]); // Undo change of variable to cos(x)

        // "Manually" select sign because acos() gives abs(cosineRoot)
        // (asin() is even more irrational, it sometimes gives sineRoot or π-sineRoot)
        t *= sign(abs(s * cos(t + o) - sin(2.0 * t) - y) - abs(s * cos(t - o) + sin(2.0 * t) - y));
        t -= u; // Undo change of variable to t+u

        float t2 = 2.0 * t;
        isect.pHit[n] = vec2(cos(t2) + alpha * cos(t), -sin(t2) - beta * sin(t));
    }

    return isect;
}

// Animation
vec2 Hash12(in float t) {
	vec3 p3 = fract(t * vec3(0.1031, 0.1030, 0.0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec4 getRay(in float t) {
    float id = floor(t), local = fract(t);
    vec2 a = Hash12(id), b = Hash12(id + 1.0), c = Hash12(id + 2.0);
    vec2 mid1 = 0.5 * (a + b), mid2 = 0.5 * (b + c);

    float tInv = 1.0 - local;
    vec2 pos = mid1 * tInv * tInv + 2.0 * b * tInv * local + mid2 * local * local;
    vec2 dir = normalize(mid1 * (local - 1.0) + (1.0 - 2.0 * local) * b + mid2 * local);

    return vec4(pos, dir);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;

    vec4 mouse = (vec4(iMouse.xy, abs(iMouse.zw)) - center.xyxy) / iResolution.y * 8.0;
    vec2 uv = (fragCoord - center) / iResolution.y * 8.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float animTime = 0.5 * iTime;
    float alpha = 2.5 * sin(animTime);
    float beta = 2.5 * cos(animTime);

    // Because this is my favorite shape it makes :)
    float trefoilTransition = 0.5 + 0.5 * sin(0.1 * iTime);
    alpha = mix(alpha, -1.35, trefoilTransition);
    beta = mix(beta, 1.35, trefoilTransition);

    vec4 ray = getRay(iTime * 0.25);
    vec2 ro = ray.xy * 8.0 - 4.0, rd = ray.zw;
    if (iMouse.z > 0.0) ro = mouse.zw, rd = mouse.xy - mouse.zw;

    // Draw limacon
    float limacon = 1e20;
    vec2 prev = posLimacon(-3.14, alpha, beta);
    float tStep = 0.1;
    for (float t=tStep - 3.14; t < 3.14 + tStep; t += tStep) {
        vec2 cur = posLimacon(t, alpha, beta);
        limacon = min(limacon, sqLine(uv, prev, cur));
        prev = cur;
    }

    drawSDF(sqrt(limacon) * 0.5 - 0.015, vec3(0.0, 0.0, 1.0));

    // Draw ray
    drawSDF(sdInfLine(uv * 0.5, ro * 0.5, (ro + rd) * 0.5) - 0.001, vec3(1.0, 0.8, 0.0));
    drawSDF(sdVectorArrow((uv - ro) * 0.5, rd * 0.5), vec3(1.0));

    // Find the closest hit in front of the camera
    Hit4 hit = iLimacon(ro, rd, alpha, beta);
    for (int n=0; n < hit.nHit; n++) {
        drawSDF(length(uv - hit.pHit[n]) - 0.1, vec3(1.0, 0.0, 0.0));
    }

    fragColor = vec4(color, 1.0);
}

