#define sat(a) clamp(a, 0., 1.)
#define PI 3.14159265
float _sph(vec3 p, float r)
{
    return length(p)-r;
}

float _cir(vec2 p, float r)
{
    return length(p)-r;
}
vec2 map(vec3 p)
{
    vec2 acc = vec2(10000., -1.);


    return vec2(_sph(p, 1.), 0.);
}

vec3 accLight;

vec3 lPos = vec3(5.,-5.,5.);

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.0001)
            return vec3(res.x, distance(p, ro), res.y);
        vec3 h = normalize(rd+(lPos-p));
        accLight += vec3(1.)*(1.-sat(res.x/.5))*.1*sat(sat(dot(normalize(p),h))+.3);//*sat(dot(normalize(p), rd));
        p+= rd*res.x;
    }
    return vec3(-1.);
}

vec3 getCam(vec3 rd, vec2 uv)
{
    vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
    vec3 u = normalize(cross(rd, r));

    return normalize(rd+r*uv.x+u*uv.y);
}

vec3 getNorm(vec3 p, float d)
{
    vec2 e = vec2(0.0001, 0.);
    return normalize(vec3(d)-vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 rdr(vec2 uv)
{
    accLight = vec3(0.);
    vec3 col = vec3(0.078,0.063,0.137);

    col = mix(col, vec3(0.239,0.216,0.565), 1.-sat((length(uv-vec2(.3))-.1)*3.));
    col = mix(col, vec3(0.239,0.216,0.565), 1.-sat((length(uv-vec2(-.3,.3))-.1)*3.));
    col = mix(col, vec3(0.408,0.216,0.565), 1.-sat((length(uv-vec2(.3,-.3))-.1)*3.));
    col += sat(length(uv*.75))*sat(pow(texture(iChannel1, uv*4.).x,20.)*5.*2500./iResolution.x)*texture(iChannel1, uv*5.+vec2(iTime*.25, 0.)).x;

    float rad = 5.;
    float t = 1.5+0.*iTime*.25;
    vec3 ro = vec3(sin(t)*rad,-3.,cos(t)*rad);
    vec3 ta = vec3(0.);
    vec3 rd = normalize(ta-ro);

    rd = getCam(rd, uv);
    float depth = -1.0f;
    // Might be good to use raytracing here, may be way lighter
    vec3 res = trace(ro, rd, 50);
    if (res.y > 0.)
    {
        depth = res.y;
        vec3 p = ro+rd*res.y;
        vec3 n = getNorm(p, res.x);
        //col = n*.5+.5;
        // If you look carefully and move the camera you'll notice 
        // that I don't account for seamless transition as originally 
        // I used a custom texture that does it pretty well
        // You know shadertoy custom texture...
        vec2 coords = vec2(atan(p.x, p.z)+iTime*.25, acos(p.y))*vec2(1./(PI*2.),1./(PI))-vec2(0.,PI);

       // float earth = 1.-texture(iChannel0, coords)-).x;
        vec2 deform = vec2(1., 2.);
        vec2 rep = deform*.003;
        vec2 id = floor((coords+rep*.5)/rep);

        vec2 mapcoord = mod(coords+rep*.5,rep)-rep*.5;

        float earth2 = 1.-texture(iChannel0, id*rep*.5).x;
        earth2 = sat(pow(earth2, 10.)*10.);
        vec3 h = normalize(rd+(lPos-p));
        float NdH =dot(n,h);
        vec3 rgb = vec3(0.239,0.216,0.565)*sat(sat(NdH)+.2);

        col = rgb;
        if (earth2 > 0.01)
        col = mix(rgb, vec3(0.843,0.643,0.918), 1.-sat(_cir(mapcoord/deform,sat(earth2)*.0005)*4000.));

        col += 2.*vec3(0.239,0.216,0.565)*(1.-sat(-dot(rd, n)*1.5))*sat(sat(NdH*1.)+.25);
        col += vec3(0.408,0.216,0.565)*(1.-sat(-dot(rd, n)*3.5));
    }
    else
        col += mix(vec3(0.239,0.216,0.565)*.1, vec3(0.408,0.216,0.565), 1.)*accLight;


    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;

    vec3 col = rdr(uv);
    vec2 off = vec2(1., -1.)/(iResolution.x*3.);
    if (true) // Not so cheap antialiasing
    {
        vec3 acc = col;
        acc += rdr(uv+off.xx);
        acc += rdr(uv+off.xy);
        acc += rdr(uv+off.yy);
        acc += rdr(uv+off.yx);
        col = acc/5.;

    }
    fragColor = vec4(col,1.0);
}

// Check out my tutorial to learn more: https://inspirnathan.com/posts/65-glow-shader-in-shadertoy

vec2 rotate(vec2 uv, float th) {
  return mat2(cos(th), sin(th), -sin(th), cos(th)) * uv;
}

float sdStar5(vec2 p, float r, float rf)
// Credit: https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
{
  const vec2 k1 = vec2(0.809016994375, -0.587785252292);
  const vec2 k2 = vec2(-k1.x,k1.y);
  p.x = abs(p.x);
  p -= 2.0*max(dot(k1,p),0.0)*k1;
  p -= 2.0*max(dot(k2,p),0.0)*k2;
  p.x = abs(p.x);
  p.y -= r;
  vec2 ba = rf*vec2(-k1.y,k1.x) - vec2(0,1);
  float h = clamp( dot(p,ba)/dot(ba,ba), 0.0, r );

  return length(p-ba*h) * sign(p.y*ba.x-p.x*ba.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // x: <0, 1>, y: <0, 1>
  uv -= 0.5; // x: <-0.5, 0.5>, y: <-0.5, 0.5>
  uv.x *= iResolution.x/iResolution.y; // x: <-0.5, 0.5> * aspect ratio, y: <-0.5, 0.5>

  float d = sdStar5(rotate(uv, iTime), 0.12, 0.45); // signed distance function

  vec3 col = vec3(step(0., -d));

  col += clamp(vec3(0.001/d), 0., 1.) * 12.; // add glow

  col *= vec3(1, 1, 0);

  fragColor = vec4(col,1.0);
}

float hash(float x) { return fract(x + 1.3215f * 1.8152f); }

float hash3(vec3 a) { return fract((hash(a.z * 42.8883f) + hash(a.y * 36.9125f) + hash(a.x * 65.4321f)) * 291.1257f); }

vec3 rehash3(float x) { return vec3(hash(((x + 0.5283f) * 59.3829f) * 274.3487f), hash(((x + 0.8192f) * 83.6621f) * 345.3871f), hash(((x + 0.2157f) * 36.6521f) * 458.3971f)); }

float sqr(float x) {return x*x;}
float fastdist(vec3 a, vec3 b) { return sqr(b.x - a.x) + sqr(b.y - a.y) + sqr(b.z - a.z); }

vec2 eval(float x, float y, float z) {
    vec4 p[27];
    for (int _x = -1; _x < 2; _x++) for (int _y = -1; _y < 2; _y++) for(int _z = -1; _z < 2; _z++) {
        vec3 _p = vec3(floor(x), floor(y), floor(z)) + vec3(_x, _y, _z);
        float h = hash3(_p);
        p[(_x + 1) + ((_y + 1) * 3) + ((_z + 1) * 3 * 3)] = vec4((rehash3(h) + _p).xyz, h);
    }
    float m = 9999.9999f, w = 0.0f;
    for (int i = 0; i < 27; i++) {
        float d = fastdist(vec3(x, y, z), p[i].xyz);
        if(d < m) { m = d; w = p[i].w; }
    }
    return vec2(m, w);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.y * 5.0f;

    vec2 n = eval(uv.x, uv.y, iTime);
    vec3 col = (1.0 - sqrt(n.x)) * rehash3(n.y);

    fragColor = vec4(col,1.0);
}

