float easeInOutCirc(float x) {
    return x < 0.5
      ? (1. - sqrt(1. - pow(2. * x, 2.))) / 2.
      : (sqrt(1. - pow(-2. * x + 2., 2.)) + 1.) / 2.;
}

float easeOutBack(float x){
    float c1 = 1.70158;
    float c3 = c1 + 1.;

    return 1. + c3 * pow(x - 1., 3.) + c1 * pow(x - 1., 2.);
}

float easeInOutBack(float x){
    float c1 = 5.;//prev 1.70158;
    float c2 = c1 * 1.525;

    return x < 0.5
      ? (pow(2. * x, 2.) * ((c2 + 1.) * 2. * x - c2)) / 2.
      : (pow(2. * x - 2., 2.) * ((c2 + 1.) * (x * 2. - 2.) + c2) + 2.) / 2.;
}

float easeInBack(float x) {
    float c1 = 1.70158;
    float c3 = c1 + 1.;

    return c3 * x * x * x - c1 * x * x;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}//from https://iquilezles.untergrund.net/www/articles/distfunctions/distfunctions.htm

float sminCubic( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}//from https://iquilezles.untergrund.net/www/articles/distfunctions/distfunctions.htm

float distortion(vec2 p, float mult, vec2 timeMult, float yMult){
    return sin(mult * p.x + sin(iTime * timeMult.x + p.y * yMult)) + cos(mult * p.y + iTime * timeMult.y);
}

float InvLerp( float a, float b, float v){
    return (v - a)/(b - a);
}

float circle_sdf(vec2 p, vec2 center, float rad){
    float dist = length(p - center) - rad;
    return dist;
}

float fluid_sdf(vec2 p){
    float d = distortion(p, 20., vec2(5., 10.), 10.) * 0.01;

    float circle1 = circle_sdf(p, vec2(xCenter,yCenter), 0.25) + d;


    float duration = 3.;
    float dropAnimTime = mod(iTime, duration)/duration;
    //dropAnimTime = easeInOutBack(dropAnimTime);

    float yPosChange;
    float sizeChange;
    float dMultiplier;

    //dropAnimTime = 1.2;

    if(dropAnimTime < 0.5){
        float animT = dropAnimTime/0.5;
        animT = easeInOutBack(animT);

        yPosChange = mix(yCenter * 0.4, yCenter, animT);
        sizeChange = abs(mix(0.1, 0.15, animT));
        dMultiplier = animT;
    }else{
        float animT = (dropAnimTime - 0.5)/0.5;
        animT = easeInOutBack(animT);

        yPosChange = mix(yCenter, yCenter * 0.4, animT);
        sizeChange = abs(mix(0.15, 0.1, animT));
        dMultiplier = 1.-animT;
    }

    float dropDistortion = distortion(p, 20., vec2(20., 2.), 2.);

    float circle2 = circle_sdf(p, vec2(xCenter,yPosChange), sizeChange) + d;

    return sminCubic(circle1, circle2, mix(0.2, 0.6, dMultiplier));
}

float sMinFluidSDF(vec2 p){
    return fluid_sdf(p);// sminCubic(fluid_sdf(p), -0.07, 0.002);
}

vec3 fluidNormal(vec2 p, float minVal){
    float epsilon = 0.05;

    vec2 right = vec2(epsilon, 0);
    vec2 up = vec2(0, epsilon);

    float center = sMinFluidSDF(p); 
    float north = sMinFluidSDF(p + up);
    float south = sMinFluidSDF(p - up);
    float east = sMinFluidSDF(p + right);
    float west = sMinFluidSDF(p - right);

    float xAxis = (east - center) - (west - center);
    float yAxis = (north - center) - (south - center);

    return normalize(vec3(xAxis, yAxis, sMinFluidSDF(p) * 1.0));
}

vec3 directionalLight(vec3 lightDir, vec3 lightColor, vec3 normal, float bands){
    float light = dot(normal, lightDir);
    light = InvLerp(-0.4, 1., light);
    light = bands == 0. ? light : round(light * bands)/bands;

    return lightColor * clamp(light, 0., 1.);
}

