/*
    Returns hash in range [0.0; 1.0].
*/
float sample2DHashUI32(uint x, uint y)
{
    // Pick some enthropy source values.
    // Try different values.
    const uint enthropy0 = 123u;
    const uint enthropy1 = 456u;
    const uint enthropy2 = 789u;

    // Use linear offset method to mix coordinates.
    uint value = y * enthropy2 + x;

    // Calculate hash.
	value += enthropy1;
	value *= 445593459u;
	value ^= enthropy0;

    // 1.0f / 4294967295.0f = 2.32830644e-10

    return float(value * value * value) * 2.32830644e-10;
}

float WorleyNoise2D(float u, float v)
{
    // Fractial part.
    float fractU = u - floor(u);
    float fractV = v - floor(v);

    // Integer part.
    u = floor(u);
    v = floor(v);

    float minDistance = 3.40282347e+37f; // FL_MAX = 3.40282347e+38f

    for (float y = -1.0f; y < 2.0f; y += 1.0f)
    {
        for (float x = -1.0f; x < 2.0f; x += 1.0f)
        {
            // Pseudorandom sample coordinates in corresponding cell.
            float xSample = x + sample2DHashUI32(uint(u + x), uint(v + y));
            float ySample = y + sample2DHashUI32(uint(u + x), uint(v + y));

            // Distance from pixel to pseudorandom sample.
            //float _distance = 
                //sqrt(
                    //(fractU - xSample) * (fractU - xSample) +
                    //(fractV - ySample) * (fractV - ySample));

            float _distance = 
                    (fractU - xSample) * (fractU - xSample) +
                    (fractV - ySample) * (fractV - ySample);

            // Mistance from pixel to pseudorandom sample.
            minDistance = min(minDistance, _distance);
        }
    }

    return minDistance;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv;

    if (fragCoord.x < iResolution.x * 0.5) { uv = fragCoord/iResolution.xy * 6.0f; }
    else { uv = fragCoord/iResolution.xy * 18.0f; }

    float _u = uv.x + iMouse.x * 0.1f + iTime * 0.1f;
    float _v = uv.y + iMouse.y * 0.1f + iTime * 0.2f;

    float gray = WorleyNoise2D(_u, _v) * 0.5f;
    gray += WorleyNoise2D(_u * 2.054f, _v * 2.210f) * 0.25f;
    gray += WorleyNoise2D(_u * 4.554f, _v * 4.710f) * 0.125f;

    // Output to screen
    fragColor = vec4(vec3(gray),1.0);
}

// 2D Random
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
            vec2(12.9898,78.233)))
                * 43758.5453123);
}

// 2D Noise based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners percentages
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

// https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9
float map(float value, float min1, float max1, float min2, float max2) {
    return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

