    vec2 pos = vec2(stc*vec2(
        map(sin(iTime/2.0), -1.0, 1.0, 8.0, 12.0), 
        map(cos(iTime/2.0), -1.0, 1.0, 4.0, 8.0)
    ) + vec2(0.0, -iTime));

    // Use the noise function
    float n = noise(pos);

    //ALLES KLAR WIR SPIELEN DEN SELBEN SONG NOCH MAL!!1
    // Scale the coordinate system to see
    // some noise in action
    vec2 pos2 = vec2(stc*vec2(
        map(sin(iTime/3.0), -1.0, 1.0, 12.0, 18.0), 
        map(cos(iTime/3.0), -1.0, 1.0, 4.0, 8.0)
    ) + vec2(0.0, -iTime/4.0));

    // Use the noise function
    n += noise(pos2) * map(sin(iTime/4.0), -1.0, 1.0, 0.1, 0.9);
    //n = mix(n, noise(pos2), map(sin(u_time/4.0), -1.0, 1.0, 0.0, 1.0));

