    //float squeeze = map(sin(iTime), -1.0, 1.0, 0.0001, 0.05);
    float squeeze = map(smoothstep(0.0, 1.0, st.y), 0.0, 1.0, 0.0001, 0.1);
    n = smoothstep(0.5-squeeze, 0.5+squeeze, n);

    float g = n*1.4;
    float b = n*1.1;

    fragColor = vec4(vec3(n, g, b), 1.0);
