vec3 GetNormalEulerOneSided(in vec3 p) { // get surface normal using euler approx. method
    vec2 e = vec2(EULER_APPROX_OFFSET, 0);
    int _;
    vec3 center = vec3(SceneSDF(_, p)),
          right = vec3(SceneSDF(_, p + e.xyy),
                       SceneSDF(_, p + e.yxy),
                       SceneSDF(_, p + e.yyx));

    vec3 n = normalize(right - center);
    return n;
}

#define GetNormal GetNormalEulerOneSided
//#define GetNormal GetNormalEulerTwoSided


/*
  p  -> position of point to shade
  ro -> ray origin (position of the camera)
*/
vec3 PhongIllumination(in vec3 p, in vec3 ro, in int hitObject) {
    vec3 lightPosOffset = vec3(sin(2. * iTime), 0, cos(2. * iTime)) * 3.;
    vec3 lightPos = light.pos + lightPosOffset;
    // PhongMaterial mat = (hitObject == 1) ? sphereMaterial : globalMaterial; // bugs are great!

    vec3 l = normalize(lightPos - p); // light vector
    vec3 n = GetNormal(p); // get normal of p
    vec3 r = reflect(l, n);
    vec3 v = normalize(p - ro);

    float dif  = clamp(dot(l, n), 0., 1.);
    float spec = clamp(dot(v, r), 0., 1.);

    // shadow stuff
    vec3 pOffset = n * SURF_DIST_MARCH * 1.2; // move the point above a little
    int _;
    float d = RayMarch(_, p + pOffset, l);
    if (d < length(lightPos - p)) { // If true then we've shaded a point on some object before, 
                                    // so shade the currnet point as shodow.
        dif *= .3; // no half-shadow because the light source is a point.    
        spec = 0.; // shadows don't have specular component, I think.
    }

    // Acutal Phong stuff
    vec3 ambientDiffuse = light.col * mats[hitObject].albedo;
    vec3 light1DiffuseComponent = dif * light.col;
    vec3 light1SpecularComponent = vec3(pow(spec, mats[hitObject].alpha));

    vec3 col = mats[hitObject].ka * ambientDiffuse + 
               mats[hitObject].kd * light1DiffuseComponent + 
               mats[hitObject].ks * light1SpecularComponent;

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5 ) / iResolution.y; // center around origin

    // simplest camera
    vec3 ro = rayOrigin;
    vec3 rd = normalize(vec3(uv.xy, 1));

    // RayMarching stuff
    int object;
    float d = RayMarch(object, ro, rd);
    vec3 p = ro + rd * d;

    vec3 col = PhongIllumination(p, ro, object);

    fragColor = vec4(col,1.0);
}

//Update 29-05-2021: 1 sqrt Heron's version from IQ added

#define BG   vec3(0.0745, 0.0862, 0.1058)*0.95;
#define COL0 vec3(236./255., 200./255., 146./255.)*0.7
#define COL1 vec3(30./255.,132./255.,127./255.)*1.3
#define COL3 vec3(1.)


//based on one by 4rknova: https://www.shadertoy.com/view/Ml23Dt
float circleUnfilled(float r, vec2 p){

    float eps = 1./min(iResolution.x, iResolution.y);
    float in_out_cut = 0.01;
    return smoothstep(-eps, +eps, abs(r - length(p))-in_out_cut );
    }


float DistToLine(vec2 p, vec2 start, vec2 ende){
    //area of a triangle is half the base X the height
    //we know this because if we take a square and cut it in
    //half on the diagonal we get half two triangles we can
    //then place side by side. This gives us a triangle
    //with a base = 2 X the length of one side of the square
    //this works for any triangle

    //here base is the distance between two points on the line in question
    float base = length(start - ende);

    //the other two sides then are
    float side2 = length(start-p);
    float side3 =  length(ende-p);

    //with all three sides known we calculate semi perimeter (s)
    //https://www.cuemath.com/measurement/area-of-equilateral-triangle/
    float s = (base+side2+side3)/2.;
    //Then area of triangle using Heron's formula
    float area = sqrt(s*(s-base)*(s-side2)*(s-side3));

    //cool
    //then with area we find the height of the triangle
    //and in our case that means the distance of our input point
    //to the base (the shortest distance to be exact)

    //IQ's Heron's Version with only 1 squareroot
    //https://iquilezles.org/www/articles/trianglearea/trianglearea.htm
    //https://www.iquilezles.org/blog/?p=1579
    vec2 pp = start - ende;
    vec2 si2 = start-p;
    vec2 si3 = ende-p;

    float A = pp.x*pp.x + pp.y*pp.y;
    float B = si2.x*si2.x + si2.y*si2.y;
    float C = si3.x*si3.x + si3.y*si3.y;
    float s2 = (2.*A*B + 2.*B*C + 2.*C*A - A*A - B*B - C*C)/16.;
    area = sqrt(s2);

    //a = (h*b)/2. -> (a*2.)/b = h
    float dist = (area*2.)/base;
    return dist;


}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)*2./iResolution.y;



    vec2 lineA = vec2(0.4, 0.5);
    vec2 lineB = vec2(-0.5, -0.5);

    //and let's animate them because that's cool
    lineA += vec2(sin(iTime/2.),cos(iTime/2.))/5.;
    lineB += vec2(sin(iTime/2.+9.),cos(iTime/2.+9.))/5.;
    //let's plot the points first so we know what we're talking about
    float pointA = circleUnfilled(0.03, uv-lineA);
    float pointB = circleUnfilled(0.03, uv-lineB);//smoothstep(0.05, 0.055, length(uv-lineB));

    vec3 col = BG;
    col = mix(col, COL0, 1.0-pointA);
    col = mix(col, COL0, 1.0-pointB);

    //now let's get our line
    float dists = DistToLine(uv, lineA, lineB);
    float line = smoothstep(0.008, 0.01, dists);
    col = mix(col, COL1, 1.0-line);

    //vignette
    uv = fragCoord/iResolution.xy;
    uv *=  1.0 - uv.yx;
    float vig = uv.x*uv.y * 15.0; // multiply with sth for intensity

    vig = pow(vig, 0.15); // change pow for modifying the extend of the  vignette

    fragColor = vec4(col*vig,1.0);
}

