surface sdSushi(vec3 p, vec3 sc, vec3 col) {
    surface result = SURF_NOHIT(1e5);

	float minsc = min(sc.x, min(sc.y, sc.z));
    p /= sc;

    // shari
    float shari = sdRoundBox(p, vec3(0.75,0.5,1.75), 0.125);

    result = SURF_SHARI(shari);

    //float neta = sdRoundBox(p-vec3(0,0.5625,0), vec3(1,0.25,2), 0.05);

    surface neta = SURF_NETA(sdRoundBox(p-vec3(0,0.5625,0), vec3(1,0.25,2), 0.05), col);

    result = opU(result, neta);

    result.dist *= minsc;

    return result;
}

mat3 camera(vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

mat3 lookAt(vec3 ro, vec3 ta, vec3 camUp)
{
    vec3 w = normalize(ta - ro);
	vec3 u = abs(dot(w, camUp)) > 1e-7 ? normalize(cross(camUp, w)) : w.yzx * vec3(1,1,-1);
	vec3 v = normalize(cross(w, u));
	return mat3(u, v, w);
}

// Quaternion multiplication.
// http://mathworld.wolfram.com/Quaternion.html
vec4 qmul(vec4 q1, vec4 q2)
{
	return vec4(
		q2.xyz * q1.w + q1.xyz * q2.w + cross(q1.xyz, q2.xyz),
		q1.w * q2.w - dot(q1.xyz, q2.xyz)
		);
}

// Rotate a vector with a rotation quaternion.
// http://mathworld.wolfram.com/Quaternion.html
vec3 rotateWithQuaternion(vec3 v, vec4 r)
{
	vec4 r_c = r * vec4(-1, -1, -1, 1);
	return qmul(r, qmul(vec4(v, 0), r_c)).xyz;
}

vec4 getAngleAxisRotation(vec3 v, vec3 axis, float angle) {
	axis = normalize(axis);
	float s, c;
	//sincos(angle, s, c);
	s = sin(angle);
	c = cos(angle);
	return vec4(axis.x*s, axis.y*s, axis.z*s, c);
}

vec3 rotateAngleAxis(vec3 v, vec3 axis, float angle) {
	vec4 q = getAngleAxisRotation(v, axis, angle);
	return rotateWithQuaternion(v, q);
}

vec4 fromToRotation(vec3 from, vec3 to) {
	vec3
		v1 = normalize(from),
		v2 = normalize(to),
		cr = cross(v1, v2);
	vec4 q = vec4(cr, 1. + dot(v1, v2));
	return normalize(q);
}

surface map(vec3 p)
{
	surface result = SURF_NOHIT(1e5);

    float ms = sin(iTime * 0.4 * M_PI2) * 0.5 + 0.5;

    // boze tower
    vec3 q = p;
    q = rotate(p, RAD90, vec3(0,1,0));
    q = opRep(q, vec3(1000,0.5,1000));

    q.xz = smoothRot(q.xz, 16., 0.0125, 0., 0.5);

    surface boze = sdBoze(q, vec3(2), ms);

    boze = opSU(boze, sdBoze(q + vec3(0,0.5,0), vec3(2), ms), 0.05);
    boze = opSU(boze, sdBoze(q - vec3(0,0.5,0), vec3(2), ms), 0.05);

    result = opU(result, boze);

    // boze wall
    q = p;
    q = opRep(q, vec3(1000,0.5,1000));

    q.xz = smoothRot(q.xz, 64., 0.0125, 0., 5.5);
    q = rotate(q, M_PI, vec3(0,1,0));

    boze = sdColorBoze(q, vec3(2), ms, vec3(0.02,0.02,0.2));

    boze = opSU(boze, sdColorBoze(q + vec3(0,0.5,0), vec3(2), ms, vec3(0.02,0.02,0.2)), 0.05);
    boze = opSU(boze, sdColorBoze(q - vec3(0,0.5,0), vec3(2), ms, vec3(0.02,0.02,0.2)), 0.05);

    result = opU(result, boze);    

    return result;
}
