#define fog_density(x) (1.0 - saturate(exp2(5.5 - (x) * 0.5)))

surface traceRay(in vec3 origin, in vec3 direction, float dist, out vec3 pos) {
    float t = 0.0;

    pos = origin;

    int count = 0;
    surface hit;
    float d;
    //surface volume = SURF_NOHIT(MAX_DIST); 

    for (int i = 0; i < MAX_MARCH; i++) {
        hit = map(pos);
        d = hit.dist;

        if (d <= EPS || d >= MAX_DIST) {
        	break;
        }

#ifdef ENABLE_GRID_BOUNDARY
        // grid overshoot check
        vec2 iBox = GetIntersectBox(pos, direction, floor(pos + 0.5), vec3(1.0,0.5,1.0));
        if(iBox.x < iBox.y && iBox.y > 0.0 && iBox.y < d) {
            d = iBox.y + EPS; 
        }
#endif        

        t += d * 1.0;

        pos = origin + direction * t;

        count++;        
    }

    hit.dist = t;
	hit.count = count;

    pos = origin + direction * t;
    vec3 sky = SkyColor(direction);

    if(hit.isHit)
    {
        vec3 nor = norm(pos);

        //vec3 lightPos;
		//vec3 lightColor;
        //fireworkLight(lightPos, lightColor);

#ifdef USE_BRDF_1
        // BRDF 1
        // Calc Ambient
        hit.albedo.rgb = calcAmb(pos, direction, nor, lightPos, lightColor, hit);
#else
        // BRDF 2
        //Shade 
        hit.albedo.rgb = shade(pos, nor, -direction, normalize(lightPos), lightColor, hit);
        // Calc Ambient
        hit.albedo.rgb += calcAmbient(pos, nor, -direction, hit.dist, vec3(0.5), hit);
#endif

#ifdef ENABLE_FOG
        // Exponential distance fog
        hit.albedo.rgb = mix(hit.albedo.rgb, sky, fog_density(hit.dist + dist));
#endif
    }

#ifdef USE_RAYCAST
    // raycast
    surface volume = rayCast(origin, direction, t);

    if(volume.isHit)
    {
        // Calc Ambient
        //volume.albedo.rgb = calcAmb(pos, direction, nor, lightPos, lightColor, volume);

        // Exponential distance fog
        volume.albedo.rgb = mix(volume.albedo.rgb, sky, fog_density(volume.dist + dist));

        hit.albedo.rgb = mix(hit.albedo.rgb, volume.albedo.rgb, volume.albedo.a);
		hit.albedo.a = max(hit.albedo.a, volume.albedo.a);
        hit.albedo.rgb = mix(sky, hit.albedo.rgb, hit.albedo.a);

        // Exponential distance fog
        volume.emission.rgb = mix(volume.emission.rgb, sky, fog_density(volume.dist + dist));
        hit.albedo.rgb += volume.emission;
        //hit.albedo.rgb += volume.albedo.rgb;
        hit.isHit = volume.isHit;
    }

    if(d <= EPS || volume.isHit){
#else
    if(d <= EPS){
#endif
        hit.isHit = true;
        return hit;
    	//return surface(hit.dist, hit.albedo, hit.emission, hit.roughness, hit.metalness, count, hit.isTransparent, hit.refractPower, true);
    }else{

        hit.isHit = false;
        return hit;

    	//return surface(hit.dist, hit.albedo, hit.emission, hit.roughness, hit.metalness, count, hit.isTransparent, hit.refractPower, false);
    }
}

vec3 render(vec3 p, vec3 ray, vec2 uv)
{
    vec3 pos;
    surface mat = traceRay(p, ray, 0., pos);

    //pos = p + mat.dist * ray;
    //return materialize(pos, ray, mat, uv);

    vec3 col = vec3(0,0,0);
    vec3 sky = SkyColor(ray);

    //if (!mat.isHit && mat.albedo.a <= EPS) {
    if (!mat.isHit) {
        col = sky;
    } else 
    {

        col = mat.albedo.rgb;

#ifdef ENABLE_REFLECTION
        float metalness = mat.metalness;
        float dist = mat.dist;

        if(mat.isHit){
            // reflection
            for(int i = 0; i < 1; i++)
            {
                vec3 nor = norm(pos);
                nor = normalize(nor + hash33(ray*3823.) * mat.roughness);
                bool isIncoming = dot(ray, nor) < 0.0;
                vec3 orientingNormal = isIncoming ? nor : -nor;
                //orientingNormal = normalize(orientingNormal + hash33(pos) * mat.roughness * 0.05);
                bool isTotalReflection = false;

                if(mat.isTransparent){
                    // refract
                  	//float nnt = isIncoming ? 1.0 / mat.refractPower : mat.refractPower;
                    //float nnt = 1.0 / mat.refractPower;
                    float nnt = isIncoming ? 1.0 / mat.refractPower : 1.0;
                    ray = refract(ray, orientingNormal, nnt);
                    pos = pos - orientingNormal * OFFSET;
                    isTotalReflection = (length(ray) <= 0.9);
                }

                if(isTotalReflection || !mat.isTransparent)
                {
                    // reflect
                    ray = reflect(ray, orientingNormal);
                    //ray = normalize(ray + hash33(ray) * mat.roughness * 0.05);
                    pos = pos + orientingNormal * OFFSET;
                }

                mat = traceRay(pos, ray, dist, pos);

                dist += mat.dist;

                pos = pos + mat.dist * ray;

                col += mat.albedo.rgb * metalness;
                //col += result;
                metalness *= mat.metalness;

				if(!mat.isHit || metalness < 0.0001)
                    break;
            }
        }
